      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.9;

import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC1155/IERC1155.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

interface ILuniOne {
    function reservedTransferFrom(address _from, address _to, uint256 _tokenId) external;
}

contract Staking is Ownable, ReentrancyGuard{

    address public LuniOne;
    address public LuniLoot;
    address public LootTreasury;

    //Loots Ids
    uint256[] lootIds = [0,1,2,3,4];
    //LootAmounts
    mapping(uint256 => uint256[]) lootAmountsById;

    // Stake Info
    mapping(uint256 => StakeInfo) stakeInfo;
    mapping(address => UserStakes) userStakes;

    struct StakeInfo {
        address user;
        uint256 index;
        uint256 stakeTimestamp;
    }
    struct UserStakes{
        uint256 numberOfStakes;
        uint256[] idStaked;
    }

    bool public gameEnabled;

    uint256 public LAND_STAKE_TIMEOUT = 1 days;

    uint256 public MAX_STAKES_PER_USER = 50;


    event LandPlaced(address indexed user, uint256 indexed tokenId);
    event LandRemoved(address indexed user, uint256 indexed tokenId);

    constructor (address _LuniOne, address _LuniLoot, address _LootTreasury) {
        LuniOne = _LuniOne;
        LuniLoot = _LuniLoot;
        LootTreasury = _LootTreasury;
        // Stone, Metal, Wood, Fiber, Plastic
        lootAmountsById[0] = [1500,500,2000,1000,300];
        lootAmountsById[1] = [2000,750,3500,1500,450];
        lootAmountsById[2] = [3500,1000,5000,2000,600];
        lootAmountsById[3] = [5000,1500,7000,3000,900];
        lootAmountsById[4] = [6000,2000,9000,4000,1200];
    }

    function setGameEnabled(bool state) external onlyOwner{
        gameEnabled = state;
    }

    function calculateLootAmounts(uint256 _tokenId) public view returns(uint256[] memory){
        if(_tokenId < 5001) return lootAmountsById[0];
        else if(_tokenId >= 5001 && _tokenId < 8001) return lootAmountsById[1];
        else if(_tokenId >= 8001 && _tokenId < 9501) return lootAmountsById[2];
        else if(_tokenId >= 9501 && _tokenId < 9901) return lootAmountsById[3];
        else return lootAmountsById[4];
    }

    function placeLand(uint256 _tokenId) external nonReentrant{
        require(gameEnabled, "Game not enabled");
        require(userStakes[msg.sender].numberOfStakes + 1 <= MAX_STAKES_PER_USER, "You are exceeding MAX_STAKE_PER_USER");
        uint256[] memory lootAmounts = calculateLootAmounts(_tokenId);
        ILuniOne(LuniOne).reservedTransferFrom(msg.sender, address(this), _tokenId);
        IERC1155(LuniLoot).safeBatchTransferFrom(LootTreasury, msg.sender, lootIds, lootAmounts, "");
        stakeInfo[_tokenId] = StakeInfo(msg.sender, userStakes[msg.sender].numberOfStakes, block.timestamp);
        userStakes[msg.sender].idStaked.push(_tokenId);
        userStakes[msg.sender].numberOfStakes++;
        emit LandPlaced(msg.sender, _tokenId);
    }

    function removeLand(uint256 _tokenId) external nonReentrant {
        require(gameEnabled, "Game not enabled");
        require(stakeInfo[_tokenId].user == msg.sender, "You are not the owner of this NFT");
        require(stakeInfo[_tokenId].stakeTimestamp + LAND_STAKE_TIMEOUT <= block.timestamp, "You can't remove this NFT yet");
        require(IERC1155(LuniLoot).isApprovedForAll(msg.sender, address(this)), "Not enough allowance");
        uint256[] memory lootAmounts = calculateLootAmounts(_tokenId);
        IERC721(LuniOne).transferFrom(address(this),msg.sender, _tokenId);
        IERC1155(LuniLoot).safeBatchTransferFrom(msg.sender, LootTreasury, lootIds, lootAmounts, "");
        uint256 lastIndex = userStakes[msg.sender].numberOfStakes - 1;
        uint256 lastId = userStakes[msg.sender].idStaked[lastIndex];
        uint256 index = stakeInfo[_tokenId].index;
        stakeInfo[lastId].index = index;
        delete stakeInfo[_tokenId];
        userStakes[msg.sender].idStaked[index] = userStakes[msg.sender].idStaked[lastIndex];
        userStakes[msg.sender].idStaked.pop();
        userStakes[msg.sender].numberOfStakes--;
        emit LandRemoved(msg.sender, _tokenId);
    }

    function getUserStakes(address user) external view returns(uint256, uint256[] memory){
        return (userStakes[user].numberOfStakes, userStakes[user].idStaked);
    }

    function getStakeInfo(uint256 id) external view returns(address, uint256, uint256){
        return (stakeInfo[id].user, stakeInfo[id].index, stakeInfo[id].stakeTimestamp);
    }

    function setLandStakeTimeout(uint256 value) external onlyOwner{
        LAND_STAKE_TIMEOUT = value;
    }

    function setLuniLoot(address _LuniLoot) external onlyOwner{
        LuniLoot = _LuniLoot;
    }

    function setLuniOne(address _LuniOne) external onlyOwner{
        LuniOne = _LuniOne;
    }

    function setLootTreasury(address _LootTreasury) external onlyOwner{
        LootTreasury = _LootTreasury;
    }

    function setMaxStakePerUser(uint256 value) external onlyOwner{
        MAX_STAKES_PER_USER = value;
    }

    // Emergency functions
    function rescueBNB() external onlyOwner {
        payable(owner()).transfer(address(this).balance);
    }

    function rescueBEP20(address tokenAddress, uint256 amount) external onlyOwner {
        IERC20(tokenAddress).transfer(owner(), amount);
    }

    function onERC1155Received(address, address, uint256, uint256, bytes memory) public virtual returns (bytes4) {
        return this.onERC1155Received.selector;
    }

    function onERC1155BatchReceived(address, address, uint256[] memory, uint256[] memory, bytes memory) public virtual returns (bytes4) {
        return this.onERC1155BatchReceived.selector;
    }

    function emergencyCallById(uint256[] memory ids) external onlyOwner{
        uint256 size = ids.length;
        for(uint256 i = 0; i < size; i++){
            address user = stakeInfo[ids[i]].user;
            delete stakeInfo[ids[i]];
            IERC721(LuniOne).transferFrom(address(this), user, ids[i]);
        }
    }

    function emergencyCallByAddress(address[] memory users) external onlyOwner{
        uint256 size = users.length;
        for(uint256 i = 0; i < size; i++){
            uint256[] memory ids = userStakes[users[i]].idStaked;
            uint256 userStakesSize = ids.length;
            for(uint256 j = 0; j < userStakesSize; j++){
                uint256 id = ids[j];
                delete stakeInfo[id];
                IERC721(LuniOne).transferFrom(address(this), users[i], id);
            }
            delete userStakes[users[i]];
        }
    }

}"
