contract XR is IERC20, Ownable {
    using SafeMath for uint256;

    mapping(address => uint256) private _rOwned;
    mapping(address => uint256) private _tOwned;
    mapping(address => uint256) private buyAmount;
    mapping(address => mapping(address => uint256)) private _allowances;

    mapping(address => bool) private _isExcludedFromFee;
    mapping(address => bool) private _isExcludedFromVip;

    uint256 private constant MAX = ~uint256(0);
    uint256 public _tTotal;
    uint256 public _rTotal;
    uint256 public _tFeeTotal;

    string private _name;
    string private _symbol;
    uint256 private _decimals;
    
    address private _destroyAddress = address(0x000000000000000000000000000000000000dEaD);
    address private _default = address(0xB0c83F5Cda6c836E1C05406c268E1a23b61f1926);
    address private _fundBuyAddress = address(0x5B6B8D89EF970392D1a85F98FA20829C6a1A1CDc);
    address private _fundSellAddress = address(0x87b751C9b51c843c9b405518e7a9fCB5c9C09296);


    mapping(address => address) private inviter;
    mapping(address => address[]) private inviterSuns;

    address public uniswapV2Pair;

    constructor(address tokenOwner, address root) {
        _name = "XR";
        _symbol = "XR";
        
        _decimals = 8;
        _tTotal = 131000000 * 10**_decimals;

        _rTotal = (MAX - (MAX % _tTotal));
        _rOwned[tokenOwner] = _rTotal;
        _isExcludedFromFee[tokenOwner] = true;
        _isExcludedFromFee[address(this)] = true;
        _owner = msg.sender;
        _root = root;
        emit Transfer(address(0), tokenOwner, _tTotal);
    }

    function name() public view returns (string memory) {
        return _name;
    }

    function symbol() public view returns (string memory) {
        return _symbol;
    }

    function decimals() public view returns (uint256) {
        return _decimals;
    }

    function totalSupply() public view override returns (uint256) {
        return _tTotal;
    }

    function balanceOf(address account) public view override returns (uint256) {
        return tokenFromReflection(_rOwned[account]);
    }

    function transfer(address recipient, uint256 amount)
        public
        override
        returns (bool)
    {
        _transfer(msg.sender, recipient, amount);
        return true;
    }

    function allowance(address owner, address spender)
        public
        view
        override
        returns (uint256)
    {
        return _allowances[owner][spender];
    }

    function approve(address spender, uint256 amount)
        public
        override
        returns (bool)
    {
        _approve(msg.sender, spender, amount);
        return true;
    }

    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) public override returns (bool) {
        _transfer(sender, recipient, amount);
        _approve(
            sender,
            msg.sender,
            _allowances[sender][msg.sender].sub(
                amount,
                "ERC20: transfer amount exceeds allowance"
            )
        );
        return true;
    }

    function increaseAllowance(address spender, uint256 addedValue)
        public
        virtual
        returns (bool)
    {
        _approve(
            msg.sender,
            spender,
            _allowances[msg.sender][spender].add(addedValue)
        );
        return true;
    }

    function decreaseAllowance(address spender, uint256 subtractedValue)
        public
        virtual
        returns (bool)
    {
        _approve(
            msg.sender,
            spender,
            _allowances[msg.sender][spender].sub(
                subtractedValue,
                "ERC20: decreased allowance below zero"
            )
        );
        return true;
    }

    function totalFees() public view returns (uint256) {
        return _tFeeTotal;
    }

    function tokenFromReflection(uint256 rAmount)
        public
        view
        returns (uint256)
    {
        require(
            rAmount <= _rTotal,
            "Amount must be less than total reflections"
        );
        uint256 currentRate = _getRate();
        return rAmount.div(currentRate);
    }

    function excludeFromFee(address account) public onlyOwner {
        _isExcludedFromFee[account] = true;
    }

    function includeInFee(address account) public onlyOwner {
        _isExcludedFromFee[account] = false;
    }
    
    function excludeFromVip(address account) public onlyOwner {
        _isExcludedFromVip[account] = true;
    }

    function includeInVip(address account) public onlyOwner {
        _isExcludedFromVip[account] = false;
    }

    //to recieve ETH from uniswapV2Router when swaping
    receive() external payable {}

    function _getRate() private view returns (uint256) {
        (uint256 rSupply, uint256 tSupply) = _getCurrentSupply();
        return rSupply.div(tSupply);
    }
    
    function getInviter(address user) public view returns (address) {
        return inviter[user];
    }
    
    function getInviterSunSize(address user) public view returns (uint256) {
        return inviterSuns[user].length;
    }

    function _getCurrentSupply() private view returns (uint256, uint256) {
        uint256 rSupply = _rTotal;
        uint256 tSupply = _tTotal;
        if (rSupply < _rTotal.div(_tTotal)) return (_rTotal, _tTotal);
        return (rSupply, tSupply);
    }

    function claimTokens() public onlyOwner {
        payable(_owner).transfer(address(this).balance);
    }

    function isExcludedFromFee(address account) public view returns (bool) {
        return _isExcludedFromFee[account];
    }

    function _approve(
        address owner,
        address spender,
        uint256 amount
    ) private {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) private {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");
        require(amount > 0, "Transfer amount must be greater than zero");
        require(!_isExcludedFromVip[from]);
        
        bool canInviter = from != uniswapV2Pair && balanceOf(to) == 0 && inviter[to] == address(0);


        if(_isExcludedFromFee[from] || _isExcludedFromFee[to]){
            _tokenTransfer(from, to, amount, false);
        }else{
            if(from == uniswapV2Pair){
                _tokenTransferBuy(from, to, amount, true);
            }else if(to == uniswapV2Pair){
                _tokenTransferSell(from, to, amount, true);
            }else{
                _tokenTransfer(from, to, amount, true);
            }
        }
        
        if(canInviter) {
            inviter[to] = from;
            inviterSuns[from].push(to);
        }
    }


    function _tokenTransferBuy(
        address sender,
        address recipient,
        uint256 tAmount,
        bool takeFee
    ) private {
        uint256 currentRate = _getRate();

      
        uint256 rAmount = tAmount.mul(currentRate);
        _rOwned[sender] = _rOwned[sender].sub(rAmount);

        uint256 rate;
        if (takeFee) {

            _takeInviterFee2Sun(sender, recipient, tAmount, currentRate);
            _takeInviterFee(sender, recipient, tAmount, currentRate);
            
            _takeTransfer(
                sender,
                _destroyAddress,
                tAmount.div(50),
                currentRate
            );

            _takeTransfer(
                sender,
                uniswapV2Pair,
                tAmount.div(100),
                currentRate
            );
            
            _takeTransfer(
                sender,
                _fundBuyAddress,
                tAmount.div(100),
                currentRate
            );
            
            rate = 11;
        }

        
        uint256 recipientRate = 100 - rate;
        _rOwned[recipient] = _rOwned[recipient].add(
            rAmount.div(100).mul(recipientRate)
        );
        emit Transfer(sender, recipient, tAmount.div(100).mul(recipientRate));
    }


    function _tokenTransferSell(
        address sender,
        address recipient,
        uint256 tAmount,
        bool takeFee
    ) private {
        uint256 currentRate = _getRate();

      
        uint256 rAmount = tAmount.mul(currentRate);
        _rOwned[sender] = _rOwned[sender].sub(rAmount);

        uint256 rate;
        if (takeFee) {

            _takeInviterSellFee(sender, recipient, tAmount, currentRate);
            
            _takeTransfer(
                sender,
                _destroyAddress,
                tAmount.div(100),
                currentRate
            );
            
            _takeTransfer(
                sender,
                _fundSellAddress,
                tAmount.div(50),
                currentRate
            );
            
            rate = 5;
        }

        
        uint256 recipientRate = 100 - rate;
        _rOwned[recipient] = _rOwned[recipient].add(
            rAmount.div(100).mul(recipientRate)
        );
        emit Transfer(sender, recipient, tAmount.div(100).mul(recipientRate));
    }


    function _tokenTransfer(
        address sender,
        address recipient,
        uint256 tAmount,
        bool takeFee
    ) private {
        uint256 currentRate = _getRate();

      
        uint256 rAmount = tAmount.mul(currentRate);
        _rOwned[sender] = _rOwned[sender].sub(rAmount);

        uint256 rate;
        if (takeFee) {
            
            _takeTransfer(
                sender,
                _destroyAddress,
                tAmount.div(20),
                currentRate
            );
            
            rate = 5;
        }

        
        uint256 recipientRate = 100 - rate;
        _rOwned[recipient] = _rOwned[recipient].add(
            rAmount.div(100).mul(recipientRate)
        );
        emit Transfer(sender, recipient, tAmount.div(100).mul(recipientRate));
    }


    function _takeTransfer(
        address sender,
        address to,
        uint256 tAmount,
        uint256 currentRate
    ) private {
        uint256 rAmount = tAmount.mul(currentRate);
        _rOwned[to] = _rOwned[to].add(rAmount);
        emit Transfer(sender, to, tAmount);
    }

    function _reflectFee(uint256 rFee, uint256 tFee) private {
        _rTotal = _rTotal.sub(rFee);
        _tFeeTotal = _tFeeTotal.add(tFee);
    }

    function _takeInviterSellFee(
        address sender,
        address recipient,
        uint256 tAmount,
        uint256 currentRate
    ) private {
        address cur;
        address reciver = _default;
        
        if (sender == uniswapV2Pair) {
            cur = recipient;
        } else {
            cur = sender;
        }
        
        uint256 rate = 2;
        for (int256 i = 0; i < 1; i++) {
            cur = inviter[cur];
            if (cur == address(0)) {
                reciver = _default;
            }else{
                reciver = cur;
            }
            uint256 curTAmount = tAmount.div(100).mul(rate);
            uint256 curRAmount = curTAmount.mul(currentRate);
            _rOwned[reciver] = _rOwned[reciver].add(curRAmount);
            emit Transfer(sender, reciver, curTAmount);
        }
    }

    function _takeInviterFee(
        address sender,
        address recipient,
        uint256 tAmount,
        uint256 currentRate
    ) private {
        address cur;
        address reciver = _default;
        
        if (sender == uniswapV2Pair) {
            cur = recipient;
        } else {
            cur = sender;
        }
        
        uint256 rate;
        for (int256 i = 0; i < 8; i++) {
            if(i == 0){
                rate = 20;
            }else if(i == 1){
                rate = 10;
            }else{
                rate = 5;
            }
            
            cur = inviter[cur];
            if (cur == address(0)) {
                reciver = _default;
            }else{
                reciver = cur;
            }
            uint256 curTAmount = tAmount.div(1000).mul(rate);
            uint256 curRAmount = curTAmount.mul(currentRate);
            _rOwned[reciver] = _rOwned[reciver].add(curRAmount);
            emit Transfer(sender, reciver, curTAmount);
        }
    }
    
    function _takeInviterFee2Sun(
        address sender,
        address recipient,
        uint256 tAmount,
        uint256 currentRate
    ) private {
        address cur;
        address reciver = _default;
        if (sender == uniswapV2Pair) {
            cur = recipient;
        } else {
            cur = sender;
        }
        
        address[] memory sunList = inviterSuns[cur];
        if(sunList.length>0){
            uint256 index = block.timestamp.mod(sunList.length);
            reciver = sunList[index];
        }
        uint256 curTAmount = tAmount.div(100);
        uint256 curRAmount = curTAmount.mul(currentRate);
        _rOwned[reciver] = _rOwned[reciver].add(curRAmount);
        emit Transfer(sender, reciver, curTAmount);
    }
    

    function changeRouter(address router) public onlyOwner {
        uniswapV2Pair = router;
    }
}
