    "P2POrder.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity >=0.7.0 <0.9.0;

import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/utils/Address.sol";
import "@openzeppelin/contracts/utils/Context.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

/// @custom:security-contact nima2142.p30@gmail.com
contract P2POrders is Context, Pausable, Ownable {
    using SafeMath  for uint;
    using Address for address;

    uint fee = 99900;
    uint base = 100000;
    uint base2 = 10000000000;

    struct Order{
        address from;
        uint amount;
        uint deadline;
    }

    struct fullOrder{
        address from;
        address to;
        uint price;
        uint id;
    }
    mapping(address => mapping(address => mapping(uint => Order[]))) public orders;
    mapping(address => fullOrder[]) public userOrders;

    event OrderCreated(address from,address to,uint price,uint id, address addr, uint amount);
    event OrderUpdated(address from,address to,uint price,uint id,uint newAmount);
    event OrderCancelled(address from,address to,uint price,uint id, uint fromAmount);
    event PriceChanged(address _from,address _to, uint _price);

    function makeOrder(address _from, address _to,uint _amount,uint _price,uint _deadline) public returns (uint) {
        bool sell = true;
        uint _aa = _amount;
        if(_from > _to){
            sell = false;
            _aa = _amount.div(_price).mul(base);
        }
        IERC20(_from).transferFrom(_msgSender(),address(this),_aa);
        uint time = block.timestamp;
        uint id = orders[_from][_to][_price].length;

        Order memory order = Order(_msgSender(),_amount,_deadline > 0 ? time.add(_deadline) : 0);
        orders[_from][_to][_price].push(order);
        userOrders[_msgSender()].push(fullOrder(_from,_to,_price,id));

        emit OrderCreated(_from, _to, _price, id, _msgSender(), _amount);

        for (uint i=0;i< orders[_to][_from][_price].length;i++){
            Order memory fromOrder = orders[_to][_from][_price][i];
            if(fromOrder.amount != 0 && (time <= fromOrder.deadline || fromOrder.deadline == 0)){
                emit PriceChanged(sell ? _from : _to,sell ? _to : _from, _price);
                uint _a = fromOrder.amount > _amount ? _amount : fromOrder.amount;
                uint _p = _a.mul(_price).mul(fee).div(base2);
                if(sell){
                    IERC20(_from).transfer(fromOrder.from,_a.mul(fee).div(base));
                    IERC20(_to).transfer(_msgSender(),_p);
                } else {
                    IERC20(_from).transfer(fromOrder.from,_p);
                    IERC20(_to).transfer(_msgSender(),_a.mul(fee).div(base));
                }
                orders[_to][_from][_price][i].amount = fromOrder.amount.sub(_a);
                emit OrderUpdated(_to, _from, _price, i, orders[_to][_from][_price][i].amount);
                _amount = _amount.sub(_a);
                emit OrderUpdated(_from, _to, _price, id, _amount);
                if(_amount == 0){
                    break;
                }
            }
        }
        return _amount;

    }

    function cancelOrder(address _from, address _to,uint _price,uint _id) public {
        Order memory order = orders[_from][_to][_price][_id];
        require(_msgSender() == order.from, "not approve");
        uint _aa = order.amount;
        if(_from > _to){
            _aa = _aa.div(_price).mul(base);
        }
        emit OrderCancelled(_from,_to,_price,_id,order.amount);
        orders[_from][_to][_price][_id].amount = 0;
        IERC20(_from).transfer(order.from,_aa.mul(fee).div(base));
    }

    function setFee(uint _fee) public onlyOwner{
        fee = _fee;
    }
}"
