	using SafeMath for uint256;

	struct SwapData {
		string pairId;
		uint256 code;
		bytes signature;
	}

	address public defaultReserveAddress;

	address public signer;

	mapping(string => address) public reserveAddresses;

	mapping(uint256 => bool) private usedCodes;

	event SwapNative(address applier, string pairId, uint256 amount);

	event SwapERC20(address applier, string pairId, address tokenAddress, uint256 amount);

	event SwapERC20Burn(address applier, string pairId, address tokenAddress, uint256 amount);

	event SwapERC721(address applier, string pairId, address tokenAddress, uint256 tokenId);

	event SwapERC721Burn(address applier, string pairId, address tokenAddress, uint256 tokenId);

	event DefaultReserveAddress(address oldReserveAddress, address newReserveAddress);

	event SwapReserveAddress(string pairId, address oldReserveAddress, address newReserveAddress);

	event RemoveReserveAddress(string pairId, address reserveAddress);

	event SignerChanged(address newSigner);

	constructor(address _newDefaultReserveAddress, address _signer) {
		changeDefaultAddress(_newDefaultReserveAddress);
		signer = _signer;
	}

	function setNewSigner(address _newSigner) public onlyOwner {
		signer = _newSigner;
		emit SignerChanged(_newSigner);
	}

	function _getReserveAddress(string calldata _pairId) internal virtual returns (address) {
		if (reserveAddresses[_pairId] != address(0)) {
			return reserveAddresses[_pairId];
		}

		return defaultReserveAddress;
	}

	function pause() public onlyOwner {
		super._pause();
	}

	function unpause() public onlyOwner {
		super._unpause();
	}

	function executeSwap(
		SwapData calldata _swapData,
		uint256[] calldata _erc20Amounts,
		address[] calldata _erc20Addresses,
		uint256[] calldata _erc721TokensIds,
		address[] calldata _erc721Addresses
