		{
			// scope to avoid stack too deep errors
			string memory message = "ExecuteSwap";
			message = string.concat(
				message,
				_swapData.pairId,
				Strings.toString(_swapData.code),
				Strings.toString(msg.value)
			);
			bytes32 hash = ECDSA.toEthSignedMessageHash(abi.encodePacked(message));
			signerRecovered = ECDSA.recover(hash, _swapData.signature);
		}
		require(signerRecovered == signer, "bad signature");

		if (msg.value > 0) {
			TransferHelper.safeTransferETH(
				_getReserveAddress(_swapData.pairId),
				msg.value
			);
			
			emit SwapNative(_msgSender(), _swapData.pairId, msg.value);
		}

		if (_erc20Addresses.length > 0) {
			require(_erc20Amounts.length == _erc20Addresses.length, "erc20 amounts and addresses length mismatch");

			for (uint i = 0; i < _erc20Addresses.length; i++) {
				if (_erc20Amounts[i] > 0) {
					// ERC20 must be approved
					TransferHelper.safeTransferFrom(
						_erc20Addresses[i],
						_msgSender(),
						_getReserveAddress(_swapData.pairId),
						_erc20Amounts[i]
					);

					emit SwapERC20(_msgSender(), _swapData.pairId, _erc20Addresses[i], _erc20Amounts[i]);
				}
			}
		}

		if (_erc721Addresses.length > 0) {
			require(_erc721TokensIds.length == _erc721Addresses.length, "erc721 amounts and addresses length mismatch");

			for (uint i = 0; i < _erc721Addresses.length; i++) {
				// ERC721 must be approved for all
				INokuCustomERC721Lite(_erc721Addresses[i]).transferFrom(
					_msgSender(),
					_getReserveAddress(_swapData.pairId),
					_erc721TokensIds[i]
				);

				emit SwapERC721(_msgSender(), _swapData.pairId, _erc721Addresses[i], _erc721TokensIds[i]);
			}
		}
	}

	function executeSwapBurn(
		SwapData calldata _swapData,
		uint256[] calldata _erc20Amounts,
		address[] calldata _erc20Addresses,
		uint256[] calldata _erc721TokensIds,
		address[] calldata _erc721Addresses
	) public payable whenNotPaused nonReentrant {
		require(usedCodes[_swapData.code] == false, "code already used");
		usedCodes[_swapData.code] = true;

		address signerRecovered;
		{
			// scope to avoid stack too deep errors
			string memory message = "ExecuteSwapBurn";
			message = string.concat(
				message,
				_swapData.pairId,
				Strings.toString(_swapData.code),
				Strings.toString(msg.value)
			);
			bytes32 hash = ECDSA.toEthSignedMessageHash(abi.encodePacked(message));
			signerRecovered = ECDSA.recover(hash, _swapData.signature);
		}
		require(signerRecovered == signer, "bad signature");

		if (msg.value > 0) {
			TransferHelper.safeTransferETH(
				_getReserveAddress(_swapData.pairId),
				msg.value
			);

			emit SwapNative(_msgSender(), _swapData.pairId, msg.value);
		}

		if (_erc20Addresses.length > 0) {
			require(_erc20Amounts.length == _erc20Addresses.length, "erc20 amounts and addresses length mismatch");

			for (uint i = 0; i < _erc20Addresses.length; i++) {
				if (_erc20Amounts[i] > 0) {
					// ERC20 must be approved
					ERC20Burnable(_erc20Addresses[i]).burnFrom(_msgSender(), _erc20Amounts[i]);

					emit SwapERC20Burn(_msgSender(), _swapData.pairId, _erc20Addresses[i], _erc20Amounts[i]);
				}
			}
		}

		if (_erc721Addresses.length > 0) {
			require(_erc721TokensIds.length == _erc721Addresses.length, "erc721 amounts and addresses length mismatch");

			for (uint i = 0; i < _erc721Addresses.length; i++) {
				// ERC721 must be approved for all
				INokuCustomERC721Lite(_erc721Addresses[i]).burn(_erc721TokensIds[i]);

				emit SwapERC721Burn(_msgSender(), _swapData.pairId, _erc721Addresses[i], _erc721TokensIds[i]);
			}
		}
	}

	function changeDefaultAddress(address _newDefaultAddress) public onlyOwner {
		require(_newDefaultAddress != address(0), "new default address is the zero address");
		require(_newDefaultAddress != address(this), "reserve address is this address");

		address _oldAddress = defaultReserveAddress;
		defaultReserveAddress = _newDefaultAddress;

		emit DefaultReserveAddress(_oldAddress, _newDefaultAddress);
	}

