		require(_reserveAddress != address(0), "reserve address is the zero address");
		require(_reserveAddress != address(this), "reserve address is this address");

		address _oldReserveAddress = reserveAddresses[_pairId];
		reserveAddresses[_pairId] = _reserveAddress;

		emit SwapReserveAddress(_pairId, _oldReserveAddress, _reserveAddress);
	}

	function removeReserveAddress(string calldata _pairId) public onlyOwner {
		address _oldReserveAddress = reserveAddresses[_pairId];
		delete reserveAddresses[_pairId];

		emit RemoveReserveAddress(_pairId, _oldReserveAddress);
	}
