pragma solidity ^0.8.7;

contract Vesting is AccessControl, ReentrancyGuard {
    using Counters for Counters.Counter;
    IERC20 private erc20;

    bytes32 public BACK_ROLE = keccak256("BACK_ROLE");

    uint256 public constant period = 6; // duration of token delivery
    uint256 private constant daysInSeconds = 86400;

    mapping(address => mapping(uint256 => Inv)) public processForInvestor;
    mapping(address => Counters.Counter) public indexForUser;

    struct Inv {
        uint256 part; // the share of tokens that the user will receive
        uint256 start; // time in seconds of the beginning of the vesting
        uint256 lockForUser; // the amount of tokens intended for this user
        uint8 typeVesting; // == 0 if 3 Months Lock UP / 6 Months release each month 16.66666%
        // == 1 if 6 Months Lock UP / 6 Months release each month 16.66666%
        // == 2 if 12 Months Lock UP / 6 Months release each month 16.66666%
    }

    event claimedTokenForUser(
        address investor,
        uint256 amount,
        uint256 date,
        uint256 index
    );
    event startVestingForUser(
        address investor,
        uint256 lockForUser,
        uint8 typeVesting,
        uint256 start,
        uint256 index
    );

    constructor() {
        _setupRole(BACK_ROLE, 0x160444CB772E871138e33c5259e20F7AF0b44B33);
        erc20 = IERC20(0xfD0fD32A20532ad690731c2685d77c351015ebBa);
    }

    function getUserInfo(address _investor, uint256 _index)
        external
        view
        returns (Inv memory)
    {
        require(
            _index < indexForUser[_investor].current(),
            "Vesting: uncorrect address"
        );
        return processForInvestor[_investor][_index];
    }

    /**
     * logs user information
     * @param  _typeVesting user who will receive tokens
     * @param _lockForUser the amount of tokens intended for this user
     * @param _typeVesting the type of process
     */
    function lockUpFor(
        address _investor,
        uint256 _lockForUser,
        uint8 _typeVesting
    ) public nonReentrant onlyOwner {
        address sender = _msgSender();

        require(
            _lockForUser > 0 && _investor != address(0),
            "Vesting: uncorrect lock sum"
        );
        require(_typeVesting < 3, "Vesting: uncorrect lock sum");

        uint256 newIndex = indexForUser[_investor].current();
        processForInvestor[_investor][newIndex] = Inv(
            _lockForUser / period,
            block.timestamp,
            _lockForUser,
            _typeVesting
        );
        indexForUser[_investor].increment();

        require(
            erc20.transferFrom(sender, address(this), _lockForUser),
            "Vesting: tokens didn`t transfer"
        );

        emit startVestingForUser(
            _investor,
            _lockForUser,
            _typeVesting,
            block.timestamp,
            newIndex
        );
    }

    /**
     * this is a function for manual vesting
     * checks the elapsed time and sends tokens if conditions are met
     * @param _investor user who will receive tokens
     */
    function claimForUser(address _investor, uint256 _index)
        public
        nonReentrant
    {
        address sender = _msgSender();

        if (sender != _investor) {
            require(
                hasRole(BACK_ROLE, sender),
                "Vesting: caller is not back and not investor"
            );
        }
        require(_investor != address(0), "Vesting: uncorrect address");
        require(
            _index < indexForUser[_investor].current(),
            "Vesting: uncorrect index"
        );
        require(
            processForInvestor[_investor][_index].lockForUser > 0,
            "Vesting: address hasn`t tokens"
        );

        Inv memory userSign = processForInvestor[_investor][_index];

        uint256 months = (block.timestamp - userSign.start) / (30 * daysInSeconds);

        if (userSign.typeVesting == 0 && months >= 3) {
            if (months > 9) {
                transferTokens(_investor, _index);
            } else if (
                userSign.lockForUser / userSign.part >= period - (months - 3)
            ) {
                transferTokens(_investor, _index);
            } else {
                require(false, "Vesting: none got tokens");
            }
        } else if (userSign.typeVesting == 1 && months >= 6) {
            if (months > 12) {
                transferTokens(_investor, _index);
            } else if (
                userSign.lockForUser / userSign.part >= period - (months - 6)
            ) {
                transferTokens(_investor, _index);
            } else {
                require(false, "Vesting: none got tokens");
            }
        } else if (userSign.typeVesting == 2 && months >= 12) {
            if (months > 18) {
                transferTokens(_investor, _index);
            } else if (
                userSign.lockForUser / userSign.part >= period - (months - 12)
            ) {
                transferTokens(_investor, _index);
            } else {
                require(false, "Vesting: none got tokens");
            }
        } else {
            require(false, "Vesting: none got tokens");
        }
    }

    function transferTokens(address _investor, uint256 index) internal {
        Inv memory userSign = processForInvestor[_investor][index];

        if (userSign.lockForUser / userSign.part == 1) {
            processForInvestor[_investor][index].lockForUser = 0;
            require(
                erc20.transfer(_investor, userSign.lockForUser),
                "Vesting: tokens didn`t transfered"
            );
        } else {
            processForInvestor[_investor][index].lockForUser =
                userSign.lockForUser -
                userSign.part;
            require(
                erc20.transfer(_investor, userSign.part),
                "Vesting: tokens didn`t transfered"
            );
        }

        emit claimedTokenForUser(
            _investor,
            processForInvestor[_investor][index].part,
            block.timestamp,
            index
        );
    }
}
