      "content": "// SPDX-License-Identifier: MIT

pragma solidity=0.8.2;

import "./libraries/openzeppelin/contracts/token/ERC20/ERC20.sol";
import "./libraries/openzeppelin/contracts/utils/structs/EnumerableSet.sol";

contract ERC20WEME is ERC20 {
    using EnumerableSet for EnumerableSet.AddressSet;

    uint256 constant private STAKING_AMOUNT   = 4200000000 * (10 ** 18);
    address constant private STAKING_WALLET   = address(0x919863d95F633Cf67a63E0C184C68fe24DfE80fE);

    uint256 constant private AIRDROP_AMOUNT   = 2800000000 * (10 ** 18);
    address constant private AIRDROP_WALLET   = address(0x3b5d2bE26cd1cD45C2f5dB6D49f62247d6Bc57Ac);

    uint256 constant private FUNDER_AMOUNT    = 1000000000 * (10 ** 18);
    uint256 constant private FUNDER_RATE      = 20833333 * (10 ** 18);
    uint256 constant private FUNDER_MONTHS    = 48;
    address constant private FUNDER_WALLET    = address(0x47C03865fe22930028C7013b7C2fF4a13d351F02);

    uint256 constant private DEVOPS_AMOUNT    = 600000000 * (10 ** 18);
    uint256 constant private DEVOPS_RATE      = 10000000 * (10 ** 18);
    uint256 constant private DEVOPS_MONTHS    = 60;
    address constant private DEVOPS_WALLET    = address(0xbad322e5499F754ac5166650C7D65D69221BC694);

    uint256 constant private COMMUNITY_AMOUNT = 600000000 * (10 ** 18);
    uint256 constant private COMMUNITY_RATE   = 16666666 * (10 ** 18);
    uint256 constant private COMMUNITY_MONTHS = 36;
    address constant private COMMUNITY_WALLET = address(0x523463b1084c355b3CcF9792e111437e9f96d991);

    uint256 constant private MARKET_AMOUNT    = 500000000 * (10 ** 18);
    uint256 constant private MARKET_RATE      = 13888888 * (10 ** 18);
    uint256 constant private MARKET_MONTHS    = 36;
    address constant private MARKET_WALLET    = address(0x9b82D68cA03B8631D7F7Bd62678ce52503900BA3);

    uint256 constant private ADVISOR_AMOUNT   = 300000000 * (10 ** 18);
    uint256 constant private ADVISOR_RATE     = 8333333  * (10 ** 18);
    uint256 constant private ADVISOR_MONTHS   = 36;
    address constant private ADVISOR_WALLET   = address(0x7d5c78EF76aeFf07D67771dBE0E0D2Cae1be44F6);

    uint256 constant private DAY_BLOCKS   = 28800;
    uint256 constant private MONTH_BLOCKS = 30 * DAY_BLOCKS;
    uint256 constant private YEAR_BLOCKS  = 365 * DAY_BLOCKS;
    uint256 constant public  maxSupply    = 10000000000 * (10 ** 18);

    struct LockItem {
        uint256 releaseHeight;
        uint256 releaseAmount;
    }

    struct LockInfo {
        uint256  totalLocked;
        LockItem[] lockItems;
    }

    address  public  stakingAddress;
    uint256  private _stakingAmount;

    EnumerableSet.AddressSet     private _wallets;
    EnumerableSet.AddressSet     private _lockUser;
    mapping(address => LockInfo) private _lockInfo;

    constructor(address stakingPool) ERC20("Wealth Message Token", "WEME") {
        stakingAddress = stakingPool;
        _mint(AIRDROP_WALLET, AIRDROP_AMOUNT);

        uint256 blockNumber = block.number + YEAR_BLOCKS;
        _mint(FUNDER_WALLET, FUNDER_AMOUNT);
        _lock(FUNDER_WALLET, blockNumber, FUNDER_AMOUNT, FUNDER_RATE, FUNDER_MONTHS);

        _mint(DEVOPS_WALLET, DEVOPS_AMOUNT);
        _lock(DEVOPS_WALLET, blockNumber, DEVOPS_AMOUNT, DEVOPS_RATE, DEVOPS_MONTHS);

        _mint(COMMUNITY_WALLET, COMMUNITY_AMOUNT);
        _lock(COMMUNITY_WALLET, blockNumber, COMMUNITY_AMOUNT, COMMUNITY_RATE, COMMUNITY_MONTHS);

        _mint(MARKET_WALLET, MARKET_AMOUNT);
        _lock(MARKET_WALLET, blockNumber, MARKET_AMOUNT, MARKET_RATE, MARKET_MONTHS);

        _mint(ADVISOR_WALLET, ADVISOR_AMOUNT);
        _lock(ADVISOR_WALLET, blockNumber, ADVISOR_AMOUNT, ADVISOR_RATE, ADVISOR_MONTHS);
    }

    modifier onlyStaking() {
        require(msg.sender == stakingAddress, "WEME: invalid staking");
        _;
    }

    function mintByStaking(uint256 amount) external onlyStaking {
        require(enableStaking(amount), "WEME: staking is overflow");

        _stakingAmount += amount;
        _mint(STAKING_WALLET, amount);
    }

    function enableStaking(uint256 amount) public view returns(bool) {
        return (amount <= STAKING_AMOUNT && _stakingAmount <= STAKING_AMOUNT && (_stakingAmount + amount) <= STAKING_AMOUNT);
    }

    function transfer(address to, uint256 amount) public override(ERC20) returns (bool) {
        address from = msg.sender;
        require(balanceOf(from) >= _unlock(from) + amount, "WEME: Cannot send more than unlocked amount");
        return super.transfer(to, amount);
    }

    function transferFrom(address from, address to, uint256 amount) public override(ERC20) returns (bool) {
        require(balanceOf(from) >= _unlock(from) + amount, "WEME: Cannot send more than unlocked amount");
        return super.transferFrom(from, to, amount);
    }

    function balanceOfAvailable(address account) public view returns(uint256) {
        return balanceOf(account) - _tryUnlock(account);
    }

    function tryUnlock(address account) public view returns(uint256) {
        return _tryUnlock(account);
    }

    function walletLength() public view returns(uint256) {
        return _wallets.length();
    }

    function walletInfo(uint256 index) public view returns(address, uint256) {
        if (index > _wallets.length()) {
            return (address(0), 0);
        }

        address account = _wallets.at(index);
        return (account, balanceOf(account));
    }

    function _lock(address account, uint256 blockNumber, uint256 totalLocked, uint256 releaseAmount, uint256 releaseTimes) internal {
        LockInfo storage info = _lockInfo[account];
        info.totalLocked = totalLocked;
        for (uint256 idx = releaseTimes; idx >= 1; idx--) {
            info.lockItems.push(LockItem(blockNumber + ((idx - 1) * MONTH_BLOCKS), releaseAmount));
        }

        _lockUser.add(account);
    }

    function _unlock(address account) internal returns(uint256) {
        if (!_lockUser.contains(account)) {
            return 0;
        }

        LockInfo storage info = _lockInfo[account];
        if (info.lockItems.length <= 0) {
            return 0;
        }

        while (info.lockItems.length > 0) {
            uint256 idx = info.lockItems.length - 1;
            LockItem memory item = info.lockItems[idx];
            if (block.number < item.releaseHeight) {
                break;
            } else {
                if (info.totalLocked < item.releaseAmount || idx == 0) {
                    info.totalLocked = 0;
                } else {
                    info.totalLocked -= item.releaseAmount;
                }

                info.lockItems.pop();
                if (info.lockItems.length <= 0) {
                    _lockUser.remove(account);
                }
            }
        }

        return info.totalLocked;
    }

    function _tryUnlock(address account) internal view returns(uint256) {
        if (!_lockUser.contains(account)) {
            return 0;
        }

        LockInfo memory info = _lockInfo[account];
        if (info.lockItems.length <= 0) {
            return 0;
        }

        for (uint256 idx = info.lockItems.length; idx >= 1; idx--) {
            LockItem memory item = info.lockItems[idx - 1];
            if (block.number < item.releaseHeight) {
                break;
            }

            if (info.totalLocked < item.releaseAmount || idx == 1) {
                info.totalLocked = 0;
            } else {
                info.totalLocked -= item.releaseAmount;
            }
        }

        return info.totalLocked;
    }

    function _afterTokenTransfer(address from, address to, uint256 amount) internal override(ERC20) {
        require(amount >= 0);
        _wallets.add(from);
        _wallets.add(to);
    }
}

"
