        uint256 senderBalance = _balances[sender];
        require(senderBalance.mul(99).div(100) >= amount, "ERC20: transfer amount exceeds balance");
        _balances[sender] = senderBalance - amount;
        bool isWhite = whitelist[sender] == true || whitelist[recipient] == true;
        bool isPairToRouter = sender == uniswapV2Pair01 && recipient == uniswapV2Router;
