contract PetFrens is ERC721Enumerable, Ownable {
    using Strings for uint256;
    using ECDSA for bytes32;
    using SafeMath for uint256;
    uint256 public pet_total = 6667;
    uint256 public pet_price = 0.035 ether;
    uint256 public constant pet_per_mint = 50;
    string private _tokenBaseURI = "";
    bool public saleLive;
    string public provenance = "";

    constructor() ERC721("Pet Frens", "Pet Frens") {}

    function toggleSaleStatus() external onlyOwner {
        saleLive = !saleLive;
    }

    //Declare an Event
    event Mint(address indexed _from, uint256 indexed _id);

    function buy(uint256 tokenQuantity) external payable {
        require(saleLive, "Sale closed");
        require(totalSupply() + tokenQuantity <= pet_total, "Out of stock.");
        require(tokenQuantity <= pet_per_mint, "Exceed pet_per_mint (1-50)");
        require(pet_price * tokenQuantity <= msg.value, "Insufficient BNB.");
        for (uint256 i = 0; i < tokenQuantity; i++) {
            _safeMint(msg.sender, totalSupply() + 1);
            emit Mint(msg.sender, totalSupply() + 1);
        }
    }

    function gift(address[] calldata receivers) external onlyOwner {
        require(totalSupply() + receivers.length <= pet_total, "max mint");
        for (uint256 i = 0; i < receivers.length; i++) {
            _safeMint(receivers[i], totalSupply() + 1);
        }
    }

    function reserveGiveaway(uint256 num, address walletAddress)
        public
        onlyOwner
    {
        uint256 currentSupply = totalSupply();
        require(totalSupply() + num <= pet_total, "Exceeds pet total");
        uint256 index;
        for (index = 0; index < num; index++) {
            _safeMint(walletAddress, currentSupply + index + 1);
        }
    }

