        address indexed sender,
        uint256 amount0In,
        uint256 amount1In,
        uint256 amount0Out,
        uint256 amount1Out,
        address indexed to
    );
    event Sync(uint112 reserve0, uint112 reserve1);

    function MINIMUM_LIQUIDITY() external pure returns (uint256);

    function factory() external view returns (address);

    function token0() external view returns (address);

    function token1() external view returns (address);

    function getReserves()
        external
        view
        returns (
            uint112 reserve0,
            uint112 reserve1,
            uint32 blockTimestampLast
        );

    function price0CumulativeLast() external view returns (uint256);

    function price1CumulativeLast() external view returns (uint256);

    function kLast() external view returns (uint256);

    function burn(address to)
        external
        returns (uint256 amount0, uint256 amount1);

    function swap(
        uint256 amount0Out,
        uint256 amount1Out,
        address to,
        bytes calldata data
    ) external;

    function skim(address to) external;

    function sync() external;

    function initialize(address, address) external;
}

interface ISmartVault {
    function transfer(uint256 amount) external;
}

contract Owner {
    address private _owner;

    event OwnerSet(address indexed oldOwner, address indexed newOwner);

    modifier onlyOwner() {
        require(msg.sender == _owner, "Caller is not owner");
        _;
    }

    constructor() {
        _owner = msg.sender;
        emit OwnerSet(address(0), _owner);
    }

    function changeOwner(address newOwner) public virtual onlyOwner {
        emit OwnerSet(_owner, newOwner);
        _owner = newOwner;
    }

    function removeOwner() public virtual onlyOwner {
        emit OwnerSet(_owner, address(0));
        _owner = address(0);
    }

    function getOwner() external view returns (address) {
        return _owner;
    }
}

library SafeMath {
    function add(uint256 a, uint256 b) internal pure returns (uint256) {
        uint256 c = a + b;
        require(c >= a, "SafeMath: addition overflow");

        return c;
    }

    function sub(uint256 a, uint256 b) internal pure returns (uint256) {
        require(b <= a, "SafeMath: subtraction overflow");
        uint256 c = a - b;

        return c;
    }

    function mul(uint256 a, uint256 b) internal pure returns (uint256) {
        if (a == 0) {
            return 0;
        }

        uint256 c = a * b;
        require(c / a == b, "SafeMath: multiplication overflow");

        return c;
    }

    function div(uint256 a, uint256 b) internal pure returns (uint256) {
        require(b > 0, "SafeMath: division by zero");
        uint256 c = a / b;

        return c;
    }

    function mod(uint256 a, uint256 b) internal pure returns (uint256) {
        require(b != 0, "SafeMath: modulo by zero");
        return a % b;
    }
}

contract Contract is Owner {
    using SafeMath for uint256;

    event Pay(
        address account,
        uint256 amount
    );

    IUniswapV2Router02 public uniswapV2Router;
    address public usdtToken;
    address public aidToken;
    address public uniswapV2Pair;

    ISmartVault private _smartVault;

    address public liquidityAddress;
    address public outAddress;

    constructor() {
        usdtToken = 0x55d398326f99059fF775485246999027B3197955;
        aidToken = 0xC7F7Ac3B9033Eb5E42B89a982c8A94049187c9D6;
        uniswapV2Router = IUniswapV2Router02(
            0x10ED43C718714eb63d5aA57B78B54704E256024E
        );
        uniswapV2Pair = 0x73f18500c6D4EEE56b2D0cF179a25E21fC409BfA;

        liquidityAddress = 0x304C609492A94BF272b5ee7a7d98B2c99Dbab047;
        outAddress = 0x304C609492A94BF272b5ee7a7d98B2c99Dbab047;
    }

    function approve(address account) public onlyOwner returns (bool) {
        IERC20(usdtToken).approve(account, type(uint256).max);
        return true;
    }

    function setSmartVault(address contractAddress_)
        external
        onlyOwner
        returns (bool)
    {
        _smartVault = ISmartVault(contractAddress_);
        return true;
    }

    function setLiquidityAddress(address liquidityAddress_)
        external
        onlyOwner
        returns (bool)
    {
        liquidityAddress = liquidityAddress_;
        return true;
    }

    function setOutAddress(address outAddress_)
        external
        onlyOwner
        returns (bool)
    {
        outAddress = outAddress_;
        return true;
    }

    function pay(uint256 amount) external returns (bool) {
        uint256 payAmount = amount * (10**18);
        uint256 swapAmount = 5 * (10**17);

        IERC20(usdtToken).transferFrom(msg.sender, address(this), payAmount);

        uint256 initialAid = IERC20(aidToken).balanceOf(address(this));

        _swapTokensForToken(
            swapAmount,
            address(usdtToken),
            address(aidToken),
            address(this)
        );

        uint256 newAid = IERC20(aidToken).balanceOf(address(this)).sub(
            initialAid
        );

        uint256 aidAmount = newAid.sub(initialAid);

        IERC20(aidToken).transferFrom(address(_smartVault), address(this), aidAmount);

        IERC20(aidToken).transfer(outAddress, aidAmount.div(2));

        _addLiquidity(swapAmount.div(2), aidAmount.div(2));

        emit Pay(msg.sender, payAmount);

        return true;
    }

    function _swapTokensForToken(
        uint256 tokenAmount,
        address path0,
        address path1,
        address to
    ) private {
        address[] memory path = new address[](2);
        path[0] = path0;
        path[1] = path1;

        IERC20(path[0]).approve(address(uniswapV2Router), tokenAmount);
        uniswapV2Router.swapExactTokensForTokensSupportingFeeOnTransferTokens(
            tokenAmount,
            0,
            path,
            to,
            block.timestamp
        );
    }

    function _addLiquidity(uint256 usdtAmount, uint256 tokenAmount) private {
        IERC20(aidToken).approve(address(uniswapV2Router), tokenAmount);

        IERC20(usdtToken).approve(address(uniswapV2Router), usdtAmount);

        // add the liquidity
        uniswapV2Router.addLiquidity(
            address(usdtToken),
            address(aidToken),
            usdtAmount,
            tokenAmount,
            0, // slippage is unavoidable
            0, // slippage is unavoidable
            liquidityAddress,
            block.timestamp
        );
    }
}
