/*
* Extra amount from reflections coming from staked amount will be burned on the basis of it's minimum threshold.
* whenever contract receives the ethers it will split the ethers values 70% for the marketing wallet can be withdraw anytime.
*  30 % of the amount will add to the next reward batch.
*   
*/

contract StakingBlazex is Ownable, ReentrancyGuard {
    IERC20 public token; // Staking token
    using SafeMath for uint256;
    using Strings for uint256;

    uint256 public batchInterval = 48 hours;
    // uint256 public batchInterval = 2 minutes;
    
    uint256 public totalStaked = 0;
    uint256 public minimumBurnThreshold = 10 ether;
    uint256 public totalBurned = 0;

    uint256 public nextRewardBatch;
    uint256 public marketingToWithdraw;
    uint256 public rewardPercent = 30;

    uint256 public minimumStakeTokens;

    struct Stake {
        uint256 amount;
        uint256 nextBatchTime;
        uint256 rewardsToWithdraw;
    }
    
    struct BatchDetails {
        uint256 rewardInBatch;
        uint256 totalStaked;
    }

    mapping(address => Stake) public stakes;
    mapping(uint256 => BatchDetails) public batches;

    modifier updateNextBatch() {
        if(block.timestamp >= nextRewardBatch) {
            uint256 multiple = (block.timestamp - nextRewardBatch) / batchInterval;
            if(multiple > 0){
                BatchDetails memory batch = batches[nextRewardBatch];
                emit RewardedToBatch(nextRewardBatch, batch.rewardInBatch, batch.totalStaked);
                nextRewardBatch += batchInterval*multiple;
            }
            if(batches[nextRewardBatch].totalStaked != totalStaked){
                batches[nextRewardBatch].totalStaked = totalStaked;
            }
        }
        _;
    }

    modifier burnExtra() {
        uint256 extra = 0;
        if(token.balanceOf(address(this)) > totalStaked){
            extra = token.balanceOf(address(this)) - totalStaked;
        }
        if(extra >= minimumBurnThreshold){
            token.transfer(address(0xdead), extra);
            totalBurned += extra;
        }
        _;
    }

    event Staked(address stakerAddress, uint256 amount, uint256 nextRewardBatch);
    event Unstaked(address stakerAddress, uint256 amount, uint256 lastRewardBatch);
    event Claimed(address stakersAddress, uint256 rewardAmount, uint256 stakedAmount, uint256 nextRewardBatch);
    event RewardUpdated(address stakerAddress, uint256 rewardAmount, uint256 fromRewardBatch, uint256 toRewardBatch);
    event RewardedToBatch(uint256 batchTimestamp, uint256 amountBnb, uint256 totalStaked);

    constructor(address _tokenAddress) {
        token = IERC20(_tokenAddress);
        nextRewardBatch = block.timestamp;
        // setting minimum tokens amount to stake
        minimumStakeTokens = 500000e9; // 500k tokens
    }

    function setMinimumBurnThreshold(uint256 _amount) external onlyOwner {
        minimumBurnThreshold = _amount;
    }

    function setMinimumAmountTokens(uint256 _amount) external onlyOwner {
        minimumStakeTokens = _amount;
    }
    
    function stake(uint256 _amount) external nonReentrant updateNextBatch burnExtra {
        require(_amount > 0, "Amount should be greater then 0");
        _updateRewards(msg.sender, 5);
        
        Stake storage userStake = stakes[msg.sender];
        require(userStake.amount + _amount >= minimumStakeTokens, "Minimum Amount Required");
        require(token.transferFrom(msg.sender, address(this), _amount), "TransferFrom failed");

        if (userStake.amount == 0) {
            userStake.amount = _amount;
            totalStaked += _amount;
            userStake.nextBatchTime = nextRewardBatch;
        } else {
            require(userStake.nextBatchTime > lastRewardBatch(), "Must update since the lastRewardBatch");
            userStake.amount += _amount;
            totalStaked += _amount;
            userStake.nextBatchTime = nextRewardBatch;
        }
        
        emit Staked(msg.sender, _amount, nextRewardBatch);
    }

    function lastRewardBatch() public view returns(uint256) {
        return nextRewardBatch - batchInterval;
    }
    
    function unstake() external burnExtra nonReentrant updateNextBatch {
        require(stakes[msg.sender].amount > 0, "No staked amount");
        
        Stake storage userStakes = stakes[msg.sender];
        _updateRewards(msg.sender, 5);
        uint256 batchDiff = (lastRewardBatch() - userStakes.nextBatchTime);

        require(batchDiff == 0,  string(abi.encodePacked("Please claim all pending rewards before unstaking. Batch difference:", batchDiff.toString())));

        uint256 stakedAmount = userStakes.amount;
        userStakes.amount = 0;
        userStakes.nextBatchTime = 0;
        totalStaked = totalStaked.sub(stakedAmount);
        token.transfer(msg.sender, stakedAmount);
        emit Unstaked(msg.sender, stakedAmount, lastRewardBatch());
    }

    function getClaimableRewards(address _address) external view returns(uint256 claimableRewards) {
        Stake memory userStaked = stakes[_address];
        claimableRewards = 0;
        while(userStaked.nextBatchTime < nextRewardBatch && userStaked.amount > 0 && userStaked.nextBatchTime > 0){
            BatchDetails memory batch = batches[userStaked.nextBatchTime];
            if(userStaked.nextBatchTime >= lastRewardBatch()){
                break;
            }
            if(userStaked.amount > 0 && batch.totalStaked > 0 && batch.rewardInBatch > 0){
                claimableRewards += userStaked.amount.mul(batch.rewardInBatch).div(batch.totalStaked);
            }
            userStaked.nextBatchTime += batchInterval;
        }
        claimableRewards += userStaked.rewardsToWithdraw;
    }
    
    function claimRewards() public nonReentrant burnExtra updateNextBatch {
        Stake storage userStakes = stakes[msg.sender];
        _updateRewards(msg.sender, 5);
        require(userStakes.rewardsToWithdraw > 0, "No rewards to claim");
        uint256 rewardsToWithdraw = userStakes.rewardsToWithdraw;
        userStakes.rewardsToWithdraw = 0;
        payable(msg.sender).transfer(rewardsToWithdraw);
        emit Claimed(msg.sender, rewardsToWithdraw, userStakes.amount, nextRewardBatch);
    }
    
    function updateRewards(address _staker, uint loop) nonReentrant burnExtra updateNextBatch public returns(bool){
        return _updateRewards(_staker, loop);
    }

    function _updateRewards(address _staker, uint loop) private returns(bool) {
        Stake storage userStakes = stakes[_staker];
        uint256 nextBatch = userStakes.nextBatchTime;
        if(nextBatch < nextRewardBatch && nextBatch > 0) {
            uint256 startBatch = nextBatch;
            uint256 amountStaked = userStakes.amount;
            BatchDetails memory batch = batches[nextBatch];
            uint256 rewards = 0;
            uint i = 0;
            while(i < loop){
                if(nextBatch >= lastRewardBatch()){
                    break;
                }
                if(amountStaked > 0 && batch.totalStaked > 0 && batch.rewardInBatch > 0){
                    rewards += amountStaked.mul(batch.rewardInBatch).div(batch.totalStaked);
                    i++;
                }
                nextBatch += batchInterval;
                batch = batches[nextBatch];
            }
            userStakes.nextBatchTime = nextBatch;
            userStakes.rewardsToWithdraw += rewards;
            emit RewardUpdated(_staker, userStakes.rewardsToWithdraw, startBatch, nextBatch);
            return true;
        }
        else {
            return false;
        }
    }

    receive() external payable updateNextBatch() {
        uint256 rewardAmount = uint256(msg.value).mul(rewardPercent).div(100);
        marketingToWithdraw += uint256(msg.value) - rewardAmount;
        batches[nextRewardBatch].rewardInBatch += rewardAmount;
    }

    function changeRewardPercentage(uint256 _rewardPercent) external onlyOwner {
        rewardPercent = _rewardPercent;
    }

    function withdrawMarketing(address _marketingWallet) public nonReentrant updateNextBatch onlyOwner {
        payable(_marketingWallet).transfer(marketingToWithdraw);
        marketingToWithdraw = 0;
    }

    function emergencyWithdraw() external onlyOwner {
        payable(owner()).transfer(address(this).balance);
    }
    
}
