    "contracts/CorgiFarmManager.sol": {
      "content": "pragma solidity ^0.8.0 ;

import '@openzeppelin/contracts/utils/math/SafeMath.sol';
import '@openzeppelin/contracts/token/ERC20/IERC20.sol';
import '@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol';
import '@openzeppelin/contracts/access/Ownable.sol';

interface IMigratorCorgiFarmManager {
    // Perform LP token migration from legacy Corgi to Corgi.
    // Take the current LP token address and return the new LP token address.
    // Migrator should have full access to the caller's LP token.
    // Return the new LP token address.
    //
    // XXX Migrator must have allowance access to Corgi LP tokens.
    // Corgi must mint EXACTLY the same amount of Corgi LP tokens or
    // else something bad will happen. Traditional Corgi does not
    // do that so be careful!
    function migrate(IERC20 token) external returns (IERC20);
}

// Have fun reading it. Hopefully it's bug-free. God bless.
contract CorgiFarmManager is Ownable {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;

    // Info of each user.
    struct UserInfo {
        uint256 amount;     // How many LP tokens the user has provided.
        uint256 rewardDebt; // Reward debt. See explanation below.
        //
        // We do some fancy math here. Basically, any point in time, the amount of Corgis
        // entitled to a user but is pending to be distributed is:
        //
        //   pending reward = (user.amount * pool.accCorgiPerShare) - user.rewardDebt
        //
        // Whenever a user deposits or withdraws LP tokens to a pool. Here's what happens:
        //   1. The pool's `accCorgiPerShare` (and `lastRewardBlock`) gets updated.
        //   2. User receives the pending reward sent to his/her address.
        //   3. User's `amount` gets updated.
        //   4. User's `rewardDebt` gets updated.
    }

    // Info of each pool.
    struct PoolInfo {
        IERC20 lpToken;           // Address of LP token contract.
        uint256 allocPoint;       // How many allocation points assigned to this pool. Corgis to distribute per block.
        uint256 lastRewardBlock;  // Last block number that Corgis distribution occurs.
        uint256 accCorgiPerShare; // Accumulated Corgis per share, times 1e12. See below.
    }

    // The Corgi TOKEN!
    IERC20 public corToken;
    IERC20 public rewardToken;
    // Corgi tokens created per block.
    uint256 public CorgiPerBlock;
    // Bonus muliplier for early Corgi makers.
    uint256 public BONUS_MULTIPLIER = 1;
    // The migrator contract. It has a lot of power. Can only be set through governance (owner).
    IMigratorCorgiFarmManager public migrator;

    // wallet distribute reward for Farm & Stake
    address public distributeRewardWallet = address(0);

    // wallet hold rewarded for all user in Farm & Stake
    address public holdRewardedWallet = address(0);

    // Info of each pool.
    PoolInfo[] public poolInfo;
    // Info of each user that stakes LP tokens.
    mapping (uint256 => mapping (address => UserInfo)) public userInfo;
    // Total allocation points. Must be the sum of all allocation points in all pools.
    uint256 public totalAllocPoint = 0;
    // The block number when Corgi mining starts.
    uint256 public startBlock;

    event Deposit(address indexed user, uint256 indexed pid, uint256 amount);
    event Withdraw(address indexed user, uint256 indexed pid, uint256 amount);
    event EmergencyWithdraw(address indexed user, uint256 indexed pid, uint256 amount);
    event SafeCorgiTransfer(address sender, address receiver, uint256 amount);
    event UpdatePoolEvent(uint256 accCorgiPerShare, uint256 corgiReward, uint256 addAccCorgiPerShare );
    event DepositEvent(uint256 accCorgiPerShare, uint256 rewardDebt);

    constructor(
        IERC20 _Corgi,
        uint256 _CorgiPerBlock,
        uint256 _startBlock,
        address _distributeRewardWallet,
        address _holdRewardedWallet
    ) public {
        corToken = _Corgi;
        CorgiPerBlock = _CorgiPerBlock;
        startBlock = _startBlock;
        distributeRewardWallet = _distributeRewardWallet;
        holdRewardedWallet = _holdRewardedWallet;
        
        // staking corgi pool
        poolInfo.push(PoolInfo({
            lpToken: _Corgi,
            allocPoint: 1000,
            lastRewardBlock: startBlock,
            accCorgiPerShare: 0
        }));
        totalAllocPoint = 1000;

    }

    function setCorgiToken(address _corgi) public onlyOwner{
        corToken = IERC20(_corgi);
    }

    // setup wallet distribute reward  
    function setdistributeRewardWallet(address _wallet) public onlyOwner{
        distributeRewardWallet = _wallet;
    }
    
    // setup hold reward wallet 
    function setHoldRewardedWallet(address _wallet) public onlyOwner{
        holdRewardedWallet = _wallet;
    }

    function updateMultiplier(uint256 multiplierNumber) public onlyOwner {
        BONUS_MULTIPLIER = multiplierNumber;
    }

    //Update corgi reward per block
    function updateCorgiPerBlock(uint256 _CorgiPerBlock) public onlyOwner {
        CorgiPerBlock = _CorgiPerBlock;
    }

    //Update totalAllocPoint
    function updateTotalAllocPoint(uint256 _totalAllocPoint) public onlyOwner {
        totalAllocPoint = _totalAllocPoint;
    }

    function poolLength() external view returns (uint256) {
        return poolInfo.length;
    }

    // Add a new lp to the pool. Can only be called by the owner.
    // XXX DO NOT add the same LP token more than once. Rewards will be messed up if you do.
    function add(uint256 _allocPoint, IERC20 _lpToken, bool _withUpdate) public onlyOwner {
        if (_withUpdate) {
            massUpdatePools();
        }
        uint256 lastRewardBlock = block.number > startBlock ? block.number : startBlock;
        totalAllocPoint = totalAllocPoint.add(_allocPoint);
        poolInfo.push(PoolInfo({
            lpToken: _lpToken,
            allocPoint: _allocPoint,
            lastRewardBlock: lastRewardBlock,
            accCorgiPerShare: 0
        }));
    }

    // Update the given pool's Corgi allocation point. Can only be called by the owner.
    function updatePoolInfo(uint256 _pid, uint256 _allocPoint, bool _withUpdate) public onlyOwner {
        if (_withUpdate) {
            massUpdatePools();
        }
        uint256 prevAllocPoint = poolInfo[_pid].allocPoint;
        poolInfo[_pid].allocPoint = _allocPoint;
        if (prevAllocPoint != _allocPoint) {
            totalAllocPoint = totalAllocPoint.sub(prevAllocPoint).add(_allocPoint);
        }
    }


    // Set the migrator contract. Can only be called by the owner.
    function setMigrator(IMigratorCorgiFarmManager _migrator) public onlyOwner {
        migrator = _migrator;
    }

    // Migrate lp token to another lp contract. Can be called by anyone. We trust that migrator contract is good.
    function migrate(uint256 _pid) public {
        require(address(migrator) != address(0), "migrate: no migrator");
        PoolInfo storage pool = poolInfo[_pid];
        IERC20 lpToken = pool.lpToken;
        uint256 bal = lpToken.balanceOf(address(this));
        lpToken.safeApprove(address(migrator), bal);
        IERC20 newLpToken = migrator.migrate(lpToken);
        require(bal == newLpToken.balanceOf(address(this)), "migrate: bad");
        pool.lpToken = newLpToken;
    }

    // Return reward multiplier over the given _from to _to block.
    function getMultiplier(uint256 _from, uint256 _to) public view returns (uint256) {
        return _to.sub(_from).mul(BONUS_MULTIPLIER);
    }

    // View function to see pending Corgi on frontend.
    function pendingCorgi(uint256 _pid, address _user) external view returns (uint256) {
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][_user];
        uint256 accCorgiPerShare = pool.accCorgiPerShare;
        uint256 lpSupply = pool.lpToken.balanceOf(address(this));
        if (block.number > pool.lastRewardBlock && lpSupply != 0) {
            uint256 multiplier = getMultiplier(pool.lastRewardBlock, block.number);
            uint256 CorgiReward = multiplier.mul(CorgiPerBlock).mul(pool.allocPoint).div(totalAllocPoint);
            accCorgiPerShare = accCorgiPerShare.add(CorgiReward.mul(1e12).div(lpSupply));
        }
        return user.amount.mul(accCorgiPerShare).div(1e12).sub(user.rewardDebt);
    }

    // Update reward variables for all pools. Be careful of gas spending!
    function massUpdatePools() public {
        uint256 length = poolInfo.length;
        for (uint256 pid = 0; pid < length; ++pid) {
            updatePool(pid);
        }
    }


    // Update reward variables of the given pool to be up-to-date.
    function updatePool(uint256 _pid) public {
        PoolInfo storage pool = poolInfo[_pid];
        if (block.number <= pool.lastRewardBlock) {
            return;
        }
        uint256 lpSupply = pool.lpToken.balanceOf(address(this));
        if (lpSupply == 0) {
            pool.lastRewardBlock = block.number;
            return;
        }
        uint256 multiplier = getMultiplier(pool.lastRewardBlock, block.number);
        uint256 CorgiReward = multiplier.mul(CorgiPerBlock).mul(pool.allocPoint).div(totalAllocPoint);

        require(corToken.balanceOf(distributeRewardWallet) > CorgiReward , "not enough reward");

        corToken.safeTransferFrom(distributeRewardWallet, holdRewardedWallet, CorgiReward);
        pool.accCorgiPerShare = pool.accCorgiPerShare.add(CorgiReward.mul(1e12).div(lpSupply));
        pool.lastRewardBlock = block.number;
    }

    // stake LP-token or COR to CorgiFarmManager .
    function deposit(uint256 _pid, uint256 _amount) public {
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][msg.sender];
        updatePool(_pid);
        if (user.amount > 0) {
            uint256 pending = user.amount.mul(pool.accCorgiPerShare).div(1e12).sub(user.rewardDebt);
            if(pending > 0) {
                safeCorgiTransfer(msg.sender, pending);
            }
        }
        if (_amount > 0) {
            pool.lpToken.safeTransferFrom(address(msg.sender), address(this), _amount);
            user.amount = user.amount.add(_amount);
        }
        user.rewardDebt = user.amount.mul(pool.accCorgiPerShare).div(1e12);
        emit Deposit(msg.sender, _pid, _amount);
    }

    // unstake LP-token or COR from CorgiFarmManager.
    function withdraw(uint256 _pid, uint256 _amount) public {
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][msg.sender];
        require(user.amount >= _amount, "withdraw: not good");

        updatePool(_pid);
        uint256 pending = user.amount.mul(pool.accCorgiPerShare).div(1e12).sub(user.rewardDebt);
        if(pending > 0) {
            safeCorgiTransfer(msg.sender, pending);
        }
        if(_amount > 0) {
            user.amount = user.amount.sub(_amount);
            pool.lpToken.safeTransfer(address(msg.sender), _amount);
        }
        user.rewardDebt = user.amount.mul(pool.accCorgiPerShare).div(1e12);
        emit Withdraw(msg.sender, _pid, _amount);
    }


    // Withdraw without caring about rewards. EMERGENCY ONLY.
    function emergencyWithdraw(uint256 _pid) public {
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][msg.sender];
        pool.lpToken.safeTransfer(address(msg.sender), user.amount);
        emit EmergencyWithdraw(msg.sender, _pid, user.amount);
        user.amount = 0;
        user.rewardDebt = 0;
    }

    // transfer Reward from holdRewardWallet to staker
    function safeCorgiTransfer(address _to, uint256 _amount) internal {
        require(corToken.balanceOf(holdRewardedWallet) > _amount, "not enough reward in holdRewardWallet");
        corToken.safeTransferFrom(holdRewardedWallet, _to, _amount);
        emit SafeCorgiTransfer(msg.sender, _to, _amount);
    }

}"
