    "contracts/PMPM.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {IERC721} from "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";
import "./interface/ISwapFactory.sol";
import "./interface/ISwapRouter.sol";
import "./interface/ISwapPair.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "./token/TokenDistributor.sol";
interface INFTHolders{
    function totalSupply() external returns(uint256);
    function getHolders() view external returns(address[] memory);

}
contract AbsToken is UUPSUpgradeable, IERC20, OwnableUpgradeable {
    mapping(address => uint256) private _balances;
    mapping(address => mapping(address => uint256)) private _allowances;
    string private _name; 
    string private _symbol; 
    uint8 private _decimals; 

    uint256 public startTradeBlock;
    uint256 public startTradeTime;
	mapping(address => bool) public _buyWhiteList;
	mapping(address => bool) public _blackList;
    mapping(address => bool) public _feeWhiteList;
	mapping(address => bool) public _swapPairList;
	mapping(address => bool) public _swapRouters;
	address public _mainPair;
	ISwapRouter public _swapRouter;

    uint256 public numTokensSellToFund;


    uint256 private constant MAX = ~uint256(0);
    uint256 private _tTotal; 
    address DEAD;
    address usdtAddress;

    address fundAddress;
    address nftAddress;
    IERC20 public USDT;

    bool private inSwap;

    //TODO　
	uint256 public _rewardGas;

     // lp
    address[] public LPholders;
	mapping(address => uint256) public LPholderIndex;
	mapping(address => bool) public LPexcludeHolder;

    // token
    address[] public holders;
	mapping(address => uint256) public holderIndex;
	mapping(address => bool) public excludeHolder;

    // tokenRewards
    uint256 public currentIndex;
	uint256 public RewardCondition;
    uint256 public holderCondition;
	uint256 public progressRewardBlock;
	uint256 public progressRewardBlockDebt;
    address public passLpAddress;
    bool public rewardTypeIsLp;

    // LPRewards
    uint256 public LPcurrentIndex;

    address private _lastMaybeAddLPAddress;

    uint256 buyDestroyFee;

    // uint256 sellAddLPFee;
    uint256 sellLpRewardsFee;
    uint256 sellRewardsFee;
    uint256 sellNFTRewardFee;
    uint256 sellTotalFee;


    uint256 holdMaxPercentBase;
    uint256 blackListBuyMax;

    address private lastAirdropAddress;

    // !!! Add new variable MUST append it only, do not insert, update type & name, or change order !!!
    // https://docs.openzeppelin.com/upgrades-plugins/1.x/writing-upgradeable#potentially-unsafe-operations
    TokenDistributor public tokenDistributor;

    // For upgradable, add one new variable above, minus 1 at here
    uint256[49] private __gap;

	modifier lockTheSwap() {
		inSwap = true;
		_;
		inSwap = false;
	}

    function AbsToken__init(string memory Name,
        string memory Symbol,
        uint8 Decimals,
        uint256 Supply,
        address UsdtAddress,
        address ReceiveAddress,
        address FundAddress,
        address NftAddress,
        address swapRouter
    ) public initializer {
        __Ownable_init();
        __UUPSUpgradeable_init();
        _name = Name;
        _symbol = Symbol;
        _decimals = Decimals;
        _tTotal = Supply * 10 ** _decimals;
        _balances[ReceiveAddress] = _tTotal;
        emit Transfer(address(0), ReceiveAddress, _tTotal);

        passLpAddress = 0x000000000000000000000000000000000000dEaD;
        usdtAddress = UsdtAddress;
        fundAddress = FundAddress;
        nftAddress = NftAddress;

        _swapRouter = ISwapRouter(swapRouter);

		_allowances[address(this)][address(_swapRouter)] = MAX;
		_swapRouters[address(_swapRouter)] = true;

        address usdtPair;
		usdtPair = ISwapFactory(_swapRouter.factory()).createPair(address(this), usdtAddress);
        _swapPairList[usdtPair] = true;
		_mainPair = usdtPair;

        // _buyWhiteList[address(this)] = true;

        _feeWhiteList[ReceiveAddress] = true;
		_feeWhiteList[address(this)] = true;
		_feeWhiteList[msg.sender] = true;
		_feeWhiteList[address(0)] = true;
		_feeWhiteList[DEAD] = true;

        excludeHolder[DEAD] = true;
        excludeHolder[passLpAddress] = true;

        holdMaxPercentBase = 100;
        blackListBuyMax = 10 ** 18;

        LPexcludeHolder[DEAD] = true;
        LPexcludeHolder[passLpAddress] = true;

        USDT = IERC20(usdtAddress);
        _allowances[address(this)][address(_swapRouter)] = MAX;


        numTokensSellToFund = 100 * 10 ** 18;

        buyDestroyFee = 20;

        // sellAddLPFee = 10;
        sellLpRewardsFee = 20;
        sellRewardsFee = 20;
        sellNFTRewardFee = 20;
        sellTotalFee = 60;

        DEAD = 0x000000000000000000000000000000000000dEaD;
        _rewardGas = 500000;

        RewardCondition = 100 ether;
        holderCondition = 100 ether;
	    progressRewardBlock;
	    progressRewardBlockDebt = 1;

    }

    modifier onlyWhiteList() {
		address msgSender = msg.sender;
		require(msgSender == fundAddress || msgSender == owner(), "nw");
		_;
	}

    function symbol() external view returns (string memory) {
        return _symbol;
    }

    function name() external view returns (string memory) {
        return _name;
    }

    function decimals() external view returns (uint8) {
        return _decimals;
    }

    function totalSupply() external view returns (uint256) {
        return _tTotal;
    }

    function balanceOf(address account) public view returns (uint256) {
        return _balances[account];
    }

    function transfer(
        address recipient,
        uint256 amount
    ) public override returns (bool) {
        _transfer(msg.sender, recipient, amount);
        return true;
    }

    function allowance(
        address owner,
        address spender
    ) public view override returns (uint256) {
        return _allowances[owner][spender];
    }

    function approve(
        address spender,
        uint256 amount
    ) public override returns (bool) {
        _approve(msg.sender, spender, amount);
        return true;
    }

    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) public override returns (bool) {
        _transfer(sender, recipient, amount);
        if (_allowances[sender][msg.sender] != MAX) {
            _allowances[sender][msg.sender] =
                _allowances[sender][msg.sender] -
                amount;
        }
        return true;
    }


    function addLpHolder(address adr) private {
		if (0 == LPholderIndex[adr]) {
			if (0 == LPholders.length || LPholders[0] != adr) {
				uint256 size;
				assembly {
					size := extcodesize(adr)
				}
				if (size > 0) {
					return;
				}
				LPholderIndex[adr] = LPholders.length;
				LPholders.push(adr);
			}
		}
	}



    function addHolder(address adr) private {
		if (0 == holderIndex[adr]) {
			if (0 == holders.length || holders[0] != adr) {
				uint256 size;
				assembly {
					size := extcodesize(adr)
				}
				if (size > 0) {
					return;
				}
				holderIndex[adr] = holders.length;
				holders.push(adr);
			}
		}
	}



	function processReward(uint256 gas) private {
		uint256 blockNum = block.number;

		if (progressRewardBlock + progressRewardBlockDebt > blockNum) {
			return;
		}

		if (USDT.balanceOf(address(this)) < RewardCondition) {
			return;
		}
		uint256 thisTokenTotal = _tTotal;
        uint256 tokenBalance;


		address shareHolder;
		uint256 amount;

		uint256 shareholderCount = holders.length;
		uint256 gasUsed = 0;
		uint256 iterations = 0;
		uint256 gasLeft = gasleft();

		while (gasUsed < gas && iterations < shareholderCount) {
			if (currentIndex >= shareholderCount) {
				currentIndex = 0;
			}
			shareHolder = holders[currentIndex];
			tokenBalance = USDT.balanceOf(shareHolder);
			if (!excludeHolder[shareHolder] && tokenBalance > holderCondition) {
				amount = (RewardCondition * tokenBalance) / thisTokenTotal;
				if (amount > 0) {
					USDT.transfer(shareHolder, amount);
				}
			}
			gasUsed = gasUsed + (gasLeft - gasleft());
			gasLeft = gasleft();
			currentIndex++;
			iterations++;
		}
		progressRewardBlock = blockNum;
        rewardTypeIsLp = true;
	}



	function processLpReward(uint256 gas) private {
		uint256 blockNum = block.number;

		if (progressRewardBlock + progressRewardBlockDebt > blockNum) {
			return;
		}

		if (USDT.balanceOf(address(this)) < RewardCondition) {
			return;
		}

		IERC20 lpToken = IERC20(_mainPair);
		uint lpTokenTotal = lpToken.totalSupply() - lpToken.balanceOf(passLpAddress) ;

		address shareHolder;
        uint256 lpBalance;
		uint256 amount;

		uint256 shareholderCount = LPholders.length;
		uint256 gasUsed = 0;
		uint256 iterations = 0;
		uint256 gasLeft = gasleft();

		while (gasUsed < gas && iterations < shareholderCount) {
			if (LPcurrentIndex >= shareholderCount) {
				LPcurrentIndex = 0;
			}
			shareHolder = LPholders[LPcurrentIndex];
			lpBalance = lpToken.balanceOf(shareHolder);
			if (!LPexcludeHolder[shareHolder]) {
				amount = (RewardCondition * lpBalance) / lpTokenTotal;
				if (amount > 0) {
					USDT.transfer(shareHolder, amount);
				}
			}
			gasUsed = gasUsed + (gasLeft - gasleft());
			gasLeft = gasleft();
			LPcurrentIndex++;
			iterations++;
		}
		progressRewardBlock = blockNum;
        rewardTypeIsLp = false;
	}




    function _approve(address owner, address spender, uint256 amount) private {
        require(owner != address(0), "approve from the zero address");
        require(spender != address(0), "approve to the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    function _transfer(address from, address to, uint256 amount) private {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");
        require(amount > 0, "Transfer amount must be greater than zero");
        uint256 balance = _balances[from];
        require(balance>=amount,"Insufficient balance");

        address lastMaybeAddLPAddress = _lastMaybeAddLPAddress;
		if (address(0) != lastMaybeAddLPAddress) {
			_lastMaybeAddLPAddress = address(0);

			if (IERC20(_mainPair).balanceOf(lastMaybeAddLPAddress) > 0) {
				addLpHolder(lastMaybeAddLPAddress);
			}
		}

        bool takeFee;

        if (_swapPairList[from] || _swapPairList[to]) {
            // buy
            if (_swapPairList[from]) {
                if(block.timestamp<(startTradeTime+48 hours) && !_buyWhiteList[to] ) {
                    require(amount<1000000 ether,"amount must less than 100");
                    uint256 balanceTo = balanceOf(to);
                    uint256 balanceLp = balanceOf(_mainPair);
                    require( (balanceTo+amount)*holdMaxPercentBase < balanceLp,"cannot hold more now");
                }
                if(_blackList[to]) {
                    require(amount<blackListBuyMax,"amount must less than 1");
                }
            }
			if (!_feeWhiteList[from] && !_feeWhiteList[to]) {
				takeFee = true;

				require(0 < startTradeBlock);
				if (block.number < startTradeBlock + 300) {
					_funTransfer(from, to, amount, 99);
					return;
				}
				uint256 maxSellAmount = (balance * 9999) / 10000;
				if (amount > maxSellAmount) {
					amount = maxSellAmount;
				}

			}
		}

        _tokenTransfer(from, to, amount,takeFee);

        if (_balances[to] >= holderCondition){
            addHolder(to);
        }
        //
        if (from != address(tokenDistributor) && from != address(this) ) {
			if (_mainPair == to) {
				_lastMaybeAddLPAddress = from;
			}
            uint256 rewardAmountUsdt = calRewardUsdtAmount();
			if (takeFee && rewardAmountUsdt >= RewardCondition) {
                internalSwap();
                if (rewardTypeIsLp){
                    processLpReward(_rewardGas);
                }else{
				    processReward(_rewardGas);
                }
			}
		}
    }

    function calRewardUsdtAmount() view internal returns(uint256) {
        require(address(tokenDistributor) != address(0x0), "token distributor is zero");
        uint256 rewardAmountToken = balanceOf(address(tokenDistributor));
        if(rewardAmountToken==0) {
            return 0;
        }
        uint256 reserveIn = balanceOf(_mainPair);
        uint256 reserveOut = USDT.balanceOf(_mainPair);
        return getAmountOut(rewardAmountToken, reserveIn, reserveOut);
    }

    function getAmountOut(uint amountIn, uint reserveIn, uint reserveOut) internal pure returns (uint) {
        require(amountIn > 0, 'PMPM: INSUFFICIENT_INPUT_AMOUNT');
        require(reserveIn > 0 && reserveOut > 0, 'PMPM: INSUFFICIENT_LIQUIDITY');
        uint amountInWithFee = amountIn * 998;
        uint numerator = amountInWithFee * reserveOut;
        uint denominator = reserveIn*1000+amountInWithFee;
        return numerator / denominator;
    }

    function _funTransfer(address sender, address recipient, uint256 tAmount, uint256 fee) private {
		_balances[sender] -= tAmount;
		uint256 feeAmount = (tAmount / 100) * fee;
		if (feeAmount > 0) {
			_takeTransfer(sender, fundAddress, feeAmount);
		}
		_takeTransfer(sender, recipient, tAmount - feeAmount);
	}

     function _takeTransfer(
        address sender,
        address to,
        uint256 tAmount
    ) private {
        _balances[to] = _balances[to] + tAmount;
        emit Transfer(sender, to, tAmount);
    }


    function _tokenTransfer(
        address sender,
        address recipient,
        uint256 tAmount,
        bool takeFee
    ) private {

        _balances[sender] -= tAmount;

        uint256 feeAmount;

        if (takeFee){
            uint256 feeForAirdrop;
            uint256 _buyDestroyFee = (tAmount * buyDestroyFee) / 1000;
            uint256 _sellLpRewardsFee = (tAmount * sellLpRewardsFee) / 1000;
            uint256 _sellRewardsFee = (tAmount * sellRewardsFee) / 1000;
            uint256 _sellNFTRewardFee = (tAmount * sellNFTRewardFee) / 1000;

            // buy
            if (_swapPairList[sender]) {
                _takeTransfer(sender, DEAD, _buyDestroyFee);
                feeAmount += _buyDestroyFee;
            }
            // sell
            else if (_swapPairList[recipient]) {
                feeAmount += _sellLpRewardsFee + _sellRewardsFee + _sellNFTRewardFee;
                _takeTransfer(sender, address(this), feeAmount);

                // airdrop
                feeForAirdrop = feeAmount / 100000;
                if (feeForAirdrop > 0) {
                    uint256 seed = (uint160(lastAirdropAddress) |
                        block.number) ^ uint160(recipient);
                    feeAmount += feeForAirdrop;
                    uint256 airdropAmount = feeForAirdrop / 3;
                    address airdropAddress;
                    for (uint256 i; i < 3; ) {
                        airdropAddress = address(uint160(seed | tAmount));
                        _takeTransfer(sender, airdropAddress, airdropAmount);
                        unchecked {
                            ++i;
                            seed = seed >> 1;
                        }
                    }
                    lastAirdropAddress = airdropAddress;
                }
            }

            uint256 contractTokenBalance = balanceOf(address(this));
			bool need_sell = contractTokenBalance >= numTokensSellToFund;
			if (need_sell && !inSwap && _swapPairList[recipient]) {
                uint256 nftReward = contractTokenBalance * sellNFTRewardFee / sellTotalFee;
                if(sellNFTRewardFee > 0) {nftShareReward(nftReward);}
                address tokenDistributorAddr = address(tokenDistributor);
                if(contractTokenBalance>nftReward && tokenDistributorAddr!=address(0x0)) {
                    _takeTransfer(address(this), tokenDistributorAddr, contractTokenBalance-nftReward);
                }
			}
        }
        _takeTransfer(sender,recipient, tAmount - feeAmount);
    }

    function internalSwap() internal lockTheSwap {

        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = usdtAddress;
        address distributer = address(tokenDistributor);
        if (_allowances[distributer][address(_swapRouter)] != type(uint256).max) {
            _allowances[distributer][address(_swapRouter)] = type(uint256).max;
        }
        if (_allowances[distributer][address(this)] != type(uint256).max) {
            _allowances[distributer][address(this)] = type(uint256).max;
        }
        uint256 rewardAmountToken = balanceOf(address(tokenDistributor));
        // swapToOwner(ISwapRouter _swapRouter, address[] memory path, uint256 amount) 
        tokenDistributor.swapToOwner(_swapRouter, path, rewardAmountToken);
    }
    function nftShareReward(uint256 contractTokenBalance) internal {
        address[] memory nftHolders =  INFTHolders(nftAddress).getHolders();
        IERC721 nft = IERC721(nftAddress);
        uint256 nftTotalSupply = INFTHolders(nftAddress).totalSupply();
        for (uint256 i; i < nftHolders.length; i++) {
            uint256 balance = nft.balanceOf(nftHolders[i]);
            uint256 reward = balance * contractTokenBalance /nftTotalSupply;
            _takeTransfer(address(this),nftHolders[i], reward);
        }
    }
