{{
  "language": "Solidity",
  "sources": {
    "contracts/IFancyPublicSale.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "./ReentrancyGuard.sol";
import "./TransferHelper.sol";
import "./Ownable.sol";
import "hardhat/console.sol";

interface OracleWrapper {
    function latestAnswer() external view returns (uint128);
}

interface Token {
    function decimals() external view returns (uint8);

    function symbol() external view returns (string memory);

    function totalSupply() external view returns (uint256);

    function balanceOf(address who) external view returns (uint256);

    function transfer(address to, uint256 value) external returns (bool);

    function allowance(address owner, address spender)
        external
        view
        returns (uint256);

    function transferFrom(
        address from,
        address to,
        uint256 value
    ) external returns (bool);
}

contract IFancyPublicSale is Ownable, ReentrancyGuard {
    uint256 public totalTokenSold;
    uint256 public totalTokenSoldUSD;
    uint256 public tokensRemaining;
    uint256 public max_tokens;
    uint128 public decimalsValue;
    uint8 public totalPhases;
    uint8 public defaultPhase;
    address public tokenAddress;
    address public lotteryWalletAddress;
    bool public transferredToLottery;

    address public BNBOracleAddress =
        0x0567F2323251f0Aab15c8dFb1967E4e8A7D42aeE;
    address public BUSDOracleAddress =
        0xcBb98864Ef56E9042e7d2efef76141f15731B82f;
    address public BUSDAddress = 0xe9e7CEA3DedcA5984780Bafc599bD69ADd087D56;

    address public receiverAddress = 0xF307148332cC90c4fEe6f148c043286BC3eA928F;

    /* ============= STRUCT SECTION ============= */

    // Stores instances of Phases
    struct PhaseInfo {
        uint256 tokenSold;
        uint256 tokenLimit;
        uint256 minPurchaseAmount; //in 10**8
        uint256 maxPurchaseAmount; //in 10**8
        uint32 startTimestamp;
        uint32 expirationTimestamp;
        uint32 price; //10**5
        bool isComplete;
    }

    struct LockTokenData {
        uint256 expirationTimestamp;
        uint256 lockShare;
    }

    mapping(uint256 => LockTokenData) public lockTokenInfo;
    mapping(uint8 => PhaseInfo) public phaseInfo;
    mapping(address => uint256) public userLockedTokens;
    mapping(address => uint256) public userClaimedTokens;
    mapping(address => uint256) public userUSDValue;
    mapping(address => uint256) public userClaimCounter;

    /* ============= EVENT SECTION ============= */

    // Emits when tokens are bought
    event TokensBought(
        uint256 buyAmount,
        uint256 noOfTokens,
        uint256 userTransferTokens,
        uint256 timestamp,
        uint32 price,
        uint8 phase,
        uint8 tokenType,
        address userAddress
    );

    event TokensClaimed(
        uint256 noOfTokens,
        uint256 timestamp,
        address userAddress
    );

    /* ============= CONSTRUCTOR SECTION ============= */
    constructor(address _tokenAddress, address _lotteryWalletAddress) {
        tokenAddress = _tokenAddress;
        lotteryWalletAddress = _lotteryWalletAddress;
        decimalsValue = uint128(10**Token(tokenAddress).decimals());
        max_tokens = 38_500_000_000 * (10**Token(tokenAddress).decimals());

        uint32 currenTimeStamp = uint32(block.timestamp);

        defaultPhase = 1;
        totalPhases = 7;

        phaseInfo[1] = PhaseInfo({
            tokenLimit: 2_500_000_000 * decimalsValue,
            tokenSold: 0,
            startTimestamp: currenTimeStamp,
            expirationTimestamp: 1654041599,
            minPurchaseAmount: (1000 * 10**8), // 1000$
            maxPurchaseAmount: (200_000 * 10**8), // 200_000$
            price: 20, // 1$ = 10^5
            isComplete: false
        });

        phaseInfo[2] = PhaseInfo({
            tokenLimit: 6_000_000_000 * decimalsValue,
            tokenSold: 0,
            startTimestamp: phaseInfo[1].expirationTimestamp + 1,
            expirationTimestamp: 1656633599,
            minPurchaseAmount: (50 * 10**8), // 50$
            maxPurchaseAmount: (25_000 * 10**8), // 25_000$
            price: 50,
            isComplete: false
        });

        phaseInfo[3] = PhaseInfo({
            tokenLimit: 6_000_000_000 * decimalsValue,
            tokenSold: 0,
            startTimestamp: 1659312000,
            expirationTimestamp: 1661990399,
            minPurchaseAmount: (50 * 10**8), // 50$
            maxPurchaseAmount: (25_000 * 10**8), // 25_000$
            price: 80,
            isComplete: false
        });

        phaseInfo[4] = PhaseInfo({
            tokenLimit: 6_000_000_000 * decimalsValue,
            tokenSold: 0,
            startTimestamp: 1664582400,
            expirationTimestamp: 1667260799,
            minPurchaseAmount: (50 * 10**8), // 50$
            maxPurchaseAmount: (25_000 * 10**8), // 25_000$
            price: 120,
            isComplete: false
        });

        phaseInfo[5] = PhaseInfo({
            tokenLimit: 6_000_000_000 * decimalsValue,
            tokenSold: 0,
            startTimestamp: 1669852800,
            expirationTimestamp: 1672531199,
            minPurchaseAmount: (50 * 10**8), // 50$
            maxPurchaseAmount: (25_000 * 10**8), // 25_000$
            price: 160,
            isComplete: false
        });

        phaseInfo[6] = PhaseInfo({
            tokenLimit: 6_000_000_000 * decimalsValue,
            tokenSold: 0,
            startTimestamp: 1675209600,
            expirationTimestamp: 1677628799,
            minPurchaseAmount: (50 * 10**8), // 15 minutes
            maxPurchaseAmount: (25_000 * 10**8), // 25_000$
            price: 200,
            isComplete: false
        });

        phaseInfo[7] = PhaseInfo({
            tokenLimit: 6_000_000_000 * decimalsValue,
            tokenSold: 0,
            startTimestamp: 1680307200,
            expirationTimestamp: 1682899199,
            minPurchaseAmount: (50 * 10**8), // 50$
            maxPurchaseAmount: (25_000 * 10**8), // 25_000$
            price: 250,
            isComplete: false
        });

        // locked Tokens Data
        lockTokenInfo[1] = LockTokenData({
            expirationTimestamp: 1688169600,
            lockShare: 2000 // 20%
        });

        lockTokenInfo[2] = LockTokenData({
            expirationTimestamp: 1690848000,
            lockShare: 2000 // 20%
        });

        lockTokenInfo[3] = LockTokenData({
            expirationTimestamp: 1693526400,
            lockShare: 2000 //20%
        });

        lockTokenInfo[4] = LockTokenData({
            expirationTimestamp: 1696118400,
            lockShare: 2000 //20%
        });
    }

    /* ============= BUY TOKENS SECTION ============= */

    function buyTokens(uint8 _type, uint256 _busdAmount)
        public
        payable
        nonReentrant
    {
        //_type=1 for BNB and type =2 for BUSD
        require(
            block.timestamp < phaseInfo[totalPhases].expirationTimestamp,
            "Buying Phases are over"
        );
        uint256 buyAmount;

        if (_type == 1) {
            buyAmount = msg.value;
        } else {
            buyAmount = _busdAmount;

            // Balance Check
            require(
                (Token(BUSDAddress).balanceOf(msg.sender)) >= buyAmount,
                "check your balance."
            );

            // Allowance Check
            require(
                Token(BUSDAddress).allowance(msg.sender, address(this)) >=
                    buyAmount,
                "Approve BUSD."
            );
        }

        // Zero value not possible
        require(buyAmount > 0, "Zero value is not possible");

        // Calculates token amount
        (
            uint256 _tokenAmount,
            uint8 _phaseValue,
            uint256 _amountGivenInUsd
        ) = calculateTokens(_type, buyAmount);

        uint256 _userTransferTokens = _calculateUserTransferTokens(
            _tokenAmount,
            2000
        );
        _tokenAmount = _userTransferTokens * 5;
        // updating user token mappings
        userLockedTokens[msg.sender] += _tokenAmount;
        userClaimedTokens[msg.sender] += _userTransferTokens;
        userUSDValue[msg.sender] += _amountGivenInUsd;

        // setPhaseInfo(_tokenAmount, _phaseValue);
        setPhaseInfo(_tokenAmount, defaultPhase);
        totalTokenSoldUSD += _amountGivenInUsd;
        totalTokenSold += _tokenAmount;
        defaultPhase = _phaseValue;

        // Transfers the tokens bought to the user
        TransferHelper.safeTransfer(
            tokenAddress,
            msg.sender,
            _userTransferTokens
        );

        // Sending the amount to the receiver address
        if (_type == 1) {
            TransferHelper.safeTransferETH(receiverAddress, msg.value);
        } else {
            TransferHelper.safeTransferFrom(
                BUSDAddress,
                msg.sender,
                receiverAddress,
                buyAmount
            );
        }
        // Emits event
        emit TokensBought(
            buyAmount,
            _tokenAmount,
            _userTransferTokens,
            block.timestamp,
            phaseInfo[defaultPhase].price,
            defaultPhase,
            _type,
            msg.sender
        );
    }

    function _calculateUserTransferTokens(uint256 _amount, uint256 _share)
        internal
        pure
        returns (uint256)
    {
        return (_share * _amount) / 10**4;
    }

    // method to claim tokens after all phases are over
    function claimLockedTokens() external {
        uint256 _claimPhase = userClaimCounter[msg.sender] + 1;
        require(_claimPhase <= 4, "Already claimed all tokens");
        require(
            (userLockedTokens[msg.sender] - userClaimedTokens[msg.sender]) > 0,
            "Already claimed all tokens"
        );
        require(
            block.timestamp > lockTokenInfo[_claimPhase].expirationTimestamp,
            "Cannot claim yet"
        );

        uint256 lockShare;
        uint256 _currentClaimPhase = _getCurrentClaimPhase();

        for (
            uint256 i = userClaimCounter[msg.sender] + 1;
            i <= _currentClaimPhase;
            i++
        ) {
            lockShare += lockTokenInfo[i].lockShare;
        }

        uint256 _claimableTokens = _calculateUserTransferTokens(
            userLockedTokens[msg.sender],
            lockShare
        );

        userClaimedTokens[msg.sender] += _claimableTokens;
        userClaimCounter[msg.sender] +=
            _currentClaimPhase -
            userClaimCounter[msg.sender];

        // Transfers the tokens  to the user
        TransferHelper.safeTransfer(tokenAddress, msg.sender, _claimableTokens);

        //  emitting events
        emit TokensClaimed(_claimableTokens, block.timestamp, msg.sender);
    }

    function _getCurrentClaimPhase() internal view returns (uint8) {
        if (block.timestamp < phaseInfo[totalPhases].expirationTimestamp) {
            return 0;
        }
        uint8 i;
        for (i = 1; i <= 4; i++) {
            if (block.timestamp < lockTokenInfo[i].expirationTimestamp) {
                return i - 1;
            }
        }

        return i - 1;
    }

    /* ============= TOKEN CALCULATION SECTION ============= */
    // Calculates Tokens
    function calculateTokens(uint8 _type, uint256 _amount)
        public
        view
        returns (
            uint256,
            uint8,
            uint256
        )
    {
        (uint256 _amountToUSD, uint256 _typeDecimal) = cryptoValues(_type);
        uint256 _amountGivenInUsd = ((_amount * _amountToUSD) / _typeDecimal);
        (uint256 _tokenAmount, uint8 _phaseValue) = calculateTokensInternal(
            _amountGivenInUsd,
            defaultPhase,
            0
        );
        return (_tokenAmount, _phaseValue, _amountGivenInUsd);
    }

    // Internal Function to calculate tokens
    function calculateTokensInternal(
        uint256 _amount,
        uint8 _phaseNo,
        uint256 _previousTokens
    ) internal view returns (uint256, uint8) {
        // Phases cannot exceed totalPhases
        require(
            _phaseNo <= totalPhases,
            "Not enough tokens in the contract or Phase expired"
        );

        PhaseInfo memory pInfo = phaseInfo[_phaseNo];

        if (block.timestamp < pInfo.expirationTimestamp) {
            require(
                uint32(block.timestamp) > pInfo.startTimestamp,
                "Phase has not started yet"
            );
            require(
                uint32(block.timestamp) < pInfo.expirationTimestamp,
                "Phase has ended"
            );
            require(
                _amount >= pInfo.minPurchaseAmount,
                "Min. purchase not reached. Please increase to min. value."
            );
            require(
                _amount <= pInfo.maxPurchaseAmount,
                "Limit exceed, reduce amount to max. value only."
            );

            // If phase is still going on
            uint256 _tokensAmount = tokensUserWillGet(_amount, pInfo.price);
            uint256 _tokensLeftToSell = (pInfo.tokenLimit + _previousTokens) -
                pInfo.tokenSold;

            require(
                _tokensLeftToSell >= _tokensAmount,
                "Insufficient tokens available in phase"
            );
            return (_tokensAmount, _phaseNo);
        } else {
            // if its a new phase
            uint256 _remainingTokens = pInfo.tokenLimit - pInfo.tokenSold;
            return
                calculateTokensInternal(
                    _amount,
                    _phaseNo + 1,
                    _remainingTokens + _previousTokens
                );
        }
    }

    // Returns the value of tokens in the phase in dollors
    function tokenValueInPhase(uint32 _price, uint256 _tokenAmount)
        internal
        view
        returns (uint256)
    {
        return ((_tokenAmount * uint256(_price) * (10**8)) /
            (10**5 * decimalsValue));
    }

    // Calculate tokens user will get for an amount
    // Tokens user will get according to the price
    function tokensUserWillGet(uint256 _amount, uint32 _price)
        internal
        view
        returns (uint256)
    {
        return ((_amount * decimalsValue * 10**5) /
            ((10**8) * uint256(_price)));
    }

    // Returns the crypto values used
    function cryptoValues(uint8 _type)
        internal
        view
        returns (uint256, uint256)
    {
        uint128 _amountToUsd;
        uint128 _decimalValue;

        if (_type == 1) {
            _amountToUsd = OracleWrapper(BNBOracleAddress).latestAnswer();
            _decimalValue = 10**18;
        } else if (_type == 2) {
            _amountToUsd = OracleWrapper(BUSDOracleAddress).latestAnswer();
            _decimalValue = uint128(10**Token(BUSDAddress).decimals());
        }
        return (_amountToUsd, _decimalValue);
    }

    /* ============= SETS PHASE INFO SECTION ============= */

    // Updates phase struct instances according to the new tokens bought
    function setPhaseInfo(uint256 _totalTokens, uint8 _phase) internal {
        require(
            _phase <= (totalPhases),
            "Not enough tokens in the contract or Phase expired"
        );
        PhaseInfo storage pInfo = phaseInfo[_phase];

        if (block.timestamp < pInfo.expirationTimestamp) {
            // Case 1: Tokens left in the current phase are more than the tokens bought
            if ((pInfo.tokenLimit - pInfo.tokenSold) > _totalTokens) {
                pInfo.tokenSold += _totalTokens;
            }
            // Case 2: Tokens left in the current phase are equal to the tokens bought
            else if ((pInfo.tokenLimit - pInfo.tokenSold) == _totalTokens) {
                pInfo.tokenSold = pInfo.tokenLimit;
                pInfo.isComplete = true;
            }
            // Case 3: Tokens left in the current phase are less than the tokens bought (Recursion)
            else {
                uint256 _leftTokens = _totalTokens -
                    (pInfo.tokenLimit - pInfo.tokenSold);
                pInfo.tokenSold = pInfo.tokenLimit;
                pInfo.isComplete = true;
                setPhaseInfo(_leftTokens, _phase + 1);
            }
        } else {
            uint256 _remainingTokens = pInfo.tokenLimit - pInfo.tokenSold;
            pInfo.tokenLimit = pInfo.tokenSold;
            pInfo.isComplete = true;

            // Limit of next phase is increased
            phaseInfo[_phase + 1].tokenLimit += _remainingTokens;
            setPhaseInfo(_totalTokens, _phase + 1);
        }
    }

    /* ============= TRANSFER LEFTOVER TOKENS TO receiver SECTION ============= */

    // Transfers left over tokens to the receiver
    function transferToLotteryWalletAfterICO() external onlyOwner {
        uint256 _contractBalance = max_tokens - totalTokenSold;

        // Phases should have ended
        require(
            (phaseInfo[totalPhases].expirationTimestamp < block.timestamp),
            "ICO is running."
        );

        // Balance should not already be claimed
        require(_contractBalance != 0, "No leftover tokens");
        require(!transferredToLottery, "Already Claimed.");

        transferredToLottery = true;

        // Transfers the left over tokens to the receiver
        TransferHelper.safeTransfer(
            tokenAddress,
            lotteryWalletAddress,
            _contractBalance
        );
    }

    /* ============= OTHER FUNCTION SECTION ============= */
    // Updates receiver address
    function updateReceiverAddress(address _receiverAddress)
        external
        onlyOwner
    {
        receiverAddress = _receiverAddress;
    }

    // Updates BUSD Address
    function updateBUSDAddress(address _BUSDAddress) external onlyOwner {
        BUSDAddress = _BUSDAddress;
    }

    // Updates BNB Oracle Address
    function updateBNBOracleAddress(address _BNBOracleAddress)
        external
        onlyOwner
    {
        BNBOracleAddress = _BNBOracleAddress;
    }

    // Updates BUSD Oracle Address
    function updateBUSDOracleAddress(address _BUSDOracleAddress)
        external
        onlyOwner
    {
        BUSDOracleAddress = _BUSDOracleAddress;
    }

    // Updates lottery wallet  address
    function updateLotteryWalletAddress(address _lotteryWalletAddress)
        external
        onlyOwner
    {
        lotteryWalletAddress = _lotteryWalletAddress;
    }
}
"
    },
    "contracts/ReentrancyGuard.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.7;

/**
 * @dev Contract module that helps prevent reentrant calls to a function.
 *
 * Inheriting from `ReentrancyGuard` will make the {nonReentrant} modifier
 * available, which can be applied to functions to make sure there are no nested
 * (reentrant) calls to them.
 *
 * Note that because there is a single `nonReentrant` guard, functions marked as
 * `nonReentrant` may not call one another. This can be worked around by making
 * those functions `private`, and then adding `external` `nonReentrant` entry
 * points to them.
 *
 * TIP: If you would like to learn more about reentrancy and alternative ways
 * to protect against it, check out our blog post
 * https://blog.openzeppelin.com/reentrancy-after-istanbul/[Reentrancy After Istanbul].
 */
abstract contract ReentrancyGuard {
    // Booleans are more expensive than uint256 or any type that takes up a full
    // word because each write operation emits an extra SLOAD to first read the
    // slot's contents, replace the bits taken up by the boolean, and then write
    // back. This is the compiler's defense against contract upgrades and
    // pointer aliasing, and it cannot be disabled.

    // The values being non-zero value makes deployment a bit more expensive,
    // but in exchange the refund on every call to nonReentrant will be lower in
    // amount. Since refunds are capped to a percentage of the total
    // transaction's gas, it is best to keep them low in cases like this one, to
    // increase the likelihood of the full refund coming into effect.
    uint256 private constant _NOT_ENTERED = 1;
    uint256 private constant _ENTERED = 2;

    uint256 private _status;

    constructor() {
        _status = _NOT_ENTERED;
    }

    /**
     * @dev Prevents a contract from calling itself, directly or indirectly.
     * Calling a `nonReentrant` function from another `nonReentrant`
     * function is not supported. It is possible to prevent this from happening
     * by making the `nonReentrant` function external, and make it call a
     * `private` function that does the actual work.
     */
    modifier nonReentrant() {
        // On the first call to nonReentrant, _notEntered will be true
        require(_status != _ENTERED, "ReentrancyGuard: reentrant call");

        // Any calls to nonReentrant after this point will fail
        _status = _ENTERED;

        _;

        // By storing the original value once again, a refund is triggered (see
        // https://eips.ethereum.org/EIPS/eip-2200)
        _status = _NOT_ENTERED;
    }
}
"
    },
    "contracts/TransferHelper.sol": {
      "content": "// SPDX-License-Identifier: GPL-3.0-or-later

pragma solidity >=0.6.0;

// helper methods for interacting with ERC20 tokens and sending ETH that do not consistently return true/false
library TransferHelper {
    function safeApprove(
        address token,
        address to,
        uint256 value
    ) internal {
        // bytes4(keccak256(bytes('approve(address,uint256)')));
        (bool success, bytes memory data) = token.call(
            abi.encodeWithSelector(0x095ea7b3, to, value)
        );
        require(
            success && (data.length == 0 || abi.decode(data, (bool))),
            "TransferHelper::safeApprove: approve failed"
        );
    }

    function safeTransfer(
        address token,
        address to,
        uint256 value
    ) internal {
        // bytes4(keccak256(bytes('transfer(address,uint256)')));
        (bool success, bytes memory data) = token.call(
            abi.encodeWithSelector(0xa9059cbb, to, value)
        );
        require(
            success && (data.length == 0 || abi.decode(data, (bool))),
            "TransferHelper::safeTransfer: transfer failed"
        );
    }

    function safeTransferFrom(
        address token,
        address from,
        address to,
        uint256 value
    ) internal {
        // bytes4(keccak256(bytes('transferFrom(address,address,uint256)')));
        (bool success, bytes memory data) = token.call(
            abi.encodeWithSelector(0x23b872dd, from, to, value)
        );
        require(
            success && (data.length == 0 || abi.decode(data, (bool))),
            "TransferHelper::transferFrom: transferFrom failed"
        );
    }

    function safeTransferETH(address to, uint256 value) internal {
        (bool success, ) = to.call{value: value}(new bytes(0));
        require(
            success,
            "TransferHelper::safeTransferETH: ETH transfer failed"
        );
    }
}
"
    },
    "contracts/Ownable.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.7;

contract Ownable {
    address public owner;

    event OwnershipTransferred(
        address indexed previousOwner,
        address indexed newOwner
    );

    /**
     * @dev The Ownable constructor sets the original `owner` of the contract to the sender
     * account.
     */
    constructor() {
        _setOwner(msg.sender);
    }

    /**
     * @dev Throws if called by any account other than the owner.
     */
    modifier onlyOwner() {
        require(msg.sender == owner, "Ownable: caller is not the owner");
        _;
    }

    /**
     * @dev Allows the current owner to transfer control of the contract to a newOwner.
     * @param newOwner The address to transfer ownership to.
     */
    function transferOwnership(address newOwner) public onlyOwner {
        require(newOwner != address(0));
        emit OwnershipTransferred(owner, newOwner);
        owner = newOwner;
    }

    function _setOwner(address newOwner) internal {
        owner = newOwner;
    }
}
"
