    "contracts/libraries/UniswapHelpers.sol": {
      "content": "import "@uniswap/v3-periphery/contracts/interfaces/ISwapRouter.sol";
import "@uniswap/v2-core/contracts/interfaces/IUniswapV2Pair.sol";
import "@uniswap/v2-core/contracts/interfaces/IUniswapV2Factory.sol";

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "./UniswapV2Helpers.sol";

library UniswapHelpers {
    using UniswapV2Helpers for IUniswapV2Router02;

    IUniswapV2Router02 constant uniswapV2Router =
        IUniswapV2Router02(0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D);

    // The ETH address according to 1inch API, this address is used as the address of the native token on all chains
    IERC20 constant ETH = IERC20(0xEeeeeEeeeEeEeeEeEeEeeEEEeeeeEeeeeeeeEEeE);

    /// @dev In handler meaning, we are providing an exact amount of input to receive a variable amount of output. This function also handles the routing to the appropriate uniswap V2 function
    function _exactInUniswapHandler(
        IERC20 _fromToken,
        IERC20 _toToken,
        uint256 _amountIn,
        uint256 _amountOutMinimum
    ) internal returns (uint256, uint256) {
        if (_fromToken == ETH) {
            revert("Swapping from ETH not supported");
            // ! Deprecated currently
            // (
            //     uint256 swappedAmountIn,
            //     uint256 swappedAmountOut
            // ) = uniswapV2Router._swapExactETHForTokens(
            //         _fromToken,
            //         _amountIn,
            //         _amountOutMinimum,
            //         address(this)
            //     );

            // return (swappedAmountIn, swappedAmountOut);
        } else if (_toToken == ETH) {
            (
                uint256 swappedAmountIn,
                uint256 swappedAmountOut
            ) = uniswapV2Router._swapExactTokensForETH(
                    _fromToken,
                    _amountIn,
                    _amountOutMinimum,
                    address(this)
                );

            return (swappedAmountIn, swappedAmountOut);
        } else {
            (
                uint256 swappedAmountIn,
                uint256 swappedAmountOut
            ) = uniswapV2Router._swapExactTokensForTokens(
                    _fromToken,
                    _toToken,
                    _amountIn,
                    _amountOutMinimum,
                    address(this)
                );
            return (swappedAmountIn, swappedAmountOut);
        }
    }
}
"
    },
    "contracts/interfaces/IBaseSwapProxy.sol": {
      "content": "import "./IVault.sol";
import "./IERC20Extension.sol";

interface IBaseSwapProxy {
    /// @dev Event used when an admin updates the feePercentage
    event SetFee(address indexed from, uint256 fee);
    /// @dev Event used when an admin updates the vault contract
    event VaultSet(IVault vault, address indexed setter);

    /// @notice This function calculates the exchange rate between the _fromToken and _toToken
    /// @dev This function tries to lookup the rate through chainlink first and if the request fails it then looks for a rate through Uniswap V2. If no rate can be found the function reverts.
    /// @param _fromToken The token the user is swapping with
    /// @param _toToken The token the user wwants
    /// @return The exchange rate
    function getExchangeRate(
        IERC20Extension _fromToken,
        IERC20Extension _toToken
    ) external view returns (uint256);

    /// @notice This function tries to calculate the exchange rate between the two tokens using chainlink
    /// @dev This function returns 0 if no rate can be found
    /// @param _fromToken The token the user is swapping with
    /// @param _toToken The token the user wwants
    /// @return exchangeRate The exchange rate or 0 if no rate is found
    function getChainlinkRate(
        IERC20Extension _fromToken,
        IERC20Extension _toToken
    ) external view returns (uint256 exchangeRate);

    /// @notice This function tries to find a rate using Uniswap V2. It gets the spot ratio between _fromToken and _toToken. This is typically a pretty unsafe operation and susceptible to MEV and sandwich attacks. This is somewhat mitigated because all swap transactions will be submitted through flashbots, which is private RPC.
    /// @param _fromToken The token the user is swapping with
    /// @param _toToken The token the user wwants
    /// @return exchangeRate The exchange rate or 0 if no rate is found
    function getUniswapV2Rate(
        IERC20Extension _fromToken,
        IERC20Extension _toToken
    ) external view returns (uint256);

    /// @notice This function calculates the percentage fee amount in ETH for the _fromToken the user is swapping from. It is deducts the _gasRefund value to ensure the user is charged correctly
    /// @param _fromToken The token the user is swapping from
    /// @param _amount The amount they are swapping with
    /// @param _gasRefund The gas refund required to cover the proxied swap
    /// @return feeTotalInETH The fee total priced in ETH
    /// @return feeTotalInFromToken The fee total priced in _fromToken
    function calculatePercentageFeeInETH(
        IERC20Extension _fromToken,
        uint256 _amount,
        uint256 _gasRefund
    )
        external
        view
        returns (uint256 feeTotalInETH, uint256 feeTotalInFromToken);
}
"
    },
    "contracts/interfaces/IVault.sol": {
      "content": "interface IVault {
    /// @dev Event used when the contract receives ETH
    event Received(address indexed from, uint256 value);
    /// @dev Event used when the contract is paid ETH, this will occur when fees have been deducted and transferred to the Vault contract for holding
    event PaidFees(address indexed from, uint256 value);
    /// @dev Event used whenever an admin claims fees from the contract
    event FeesClaimed(address indexed from, uint256 value);

    /// @dev To allow the contract to receive ETH
    receive() external payable;

    /// @notice Allows an admin to claim the ETH balance of the contract
    function claimFees() external;

    /// @notice This function is called whenever fees have been deducted from a user and transffered into the Vault for holding
    /// @param _sender The user who has been deducted a fee
    /// @param _amount The fee amount
    function paidFees(address _sender, uint256 _amount) external payable;

    /// @notice Allows an admin to set the balance limit for wallets that have the HOT_WALLET role
    /// @param _walletBalanceLimit The updated balance limit
    function setWalletBalanceLimit(uint256 _walletBalanceLimit) external;

    /// @notice Allows an admin to top up registered HOT_WALLETS, these wallets will be used for sponosoring transactions and this method allows updating all of them with a single contract call
    function topUpHotWallets() external payable;

    /// @notice This allows us to easily see the total amount of ETH required to top-up all hot wallets. Used before calling the topUpHotWallets function
    /// @return totalETH The total amount of ETH required
    function ethRequiredForHotWalletTopup()
        external
        view
        returns (uint256 totalETH);
}
"
