                addliquidity = true;
            }
        }
        super._transfer(sender, recipient, amount);
    }

    function checkWhales(uint256 amount) private view {
        if (
            !hasRole(RoleAdmins, _msgSender()) &&
            !hasRole(RoleExFee, _msgSender()) &&
            antiWhaleEnabled
        ) {
            (, uint256 total) = getPairliquidity();
            uint256 max = total.mul(antiWhaleAmountRate).div(10000);
            require(amount <= max, "TKBToken: antiWhaleEnabled max");
        }
    }

    function getPairliquidity()
        public
        view
        returns (uint256 wbnb, uint256 token)
    {
        if (pairAddress != address(0)) {
            IPancakePair pair = IPancakePair(pairAddress);
            (uint112 reserve0, uint112 reserve1, ) = pair.getReserves();
            wbnb = pair.token0() == swapRouter.WETH() ? reserve0 : reserve1;
            token = pair.token0() == address(this) ? reserve0 : reserve1;
        }
    }

    function grantRoleExFees(address[] calldata _accounts)
        external
        onlyRole(RoleAdmins)
    {
        for (uint256 index = 0; index < _accounts.length; index++) {
            address account = _accounts[index];
            if (!hasRole(RoleExFee, account)) {
                grantRole(RoleExFee, account);
            }
        }
    }

    function setFeeRate(uint256 _rate) external onlyRole(RoleAdmins) {
        transferFeeRate = _rate;
    }

    function setFeeAddress(address _addr) external onlyRole(RoleAdmins) {
        require(_addr != address(0), "0x is not accepted here");
        feeAddress = _addr;
    }

    function setwhalepunish(uint256 _whal) external onlyRole(RoleAdmins) {
        require(_whal > 0, "0x is not accepted here");
        whalepunish = _whal;
    }

    function addTransferFeeAddress(address _addr) public onlyRole(RoleAdmins) {
        _transactionFee[_addr] = true;
    }

    function enableAntiWhale(uint256 _rate) external onlyRole(RoleAdmins) {
        require(_rate > 0, "not accept 0 value");
        antiWhaleAmountRate = _rate;
        antiWhaleEnabled = true;
    }

    function disAntiWhale() external onlyRole(RoleAdmins) {
        require(antiWhaleEnabled);
        antiWhaleEnabled = true;
    }

    function manyTransfer(address[] calldata _addrs, uint256 amount)
        external
        onlyRole(RoleAdmins)
    {
        for (uint256 index = 0; index < _addrs.length; index++) {
            address recipient = _addrs[index];
            super._transfer(_msgSender(), recipient, amount);
        }
    }

    function farm(address recipient, uint256 amount)
        external
        onlyRole(RoleFarms)
    {
        require(farmReward != capFarm, "Over cap farm");
        require(recipient != address(0), "0x is not accepted here");
        require(amount > 0, "not accept 0 value");

        farmReward = farmReward.add(amount);
        if (farmReward <= capFarm) _mint(recipient, amount);
        else {
            uint256 availableReward = farmReward.sub(capFarm);
            _mint(recipient, availableReward);
            farmReward = capFarm;
        }
    }

    event Whalepunish(address indexed addr, uint256 amount);
}
