    "contracts/DripPotluck.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/interfaces/IERC20.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/security/Pausable.sol";

contract DripPotluck is Ownable, Pausable, ReentrancyGuard {
	using SafeERC20 for IERC20;

	struct Room {
		address[] players;
		uint256 entryFee;
		uint256 numberOfPlayers;
		address winner;
		bool gameStarted;
		uint256 roomBalance;
	}

	Room[] public rooms;
	event Winner(address indexed winner, uint256 indexed amount);
	uint256 public constant PRECISION = 1e18; // 18 decimal places
	uint256 public constant PERCENTAGE_BASE = 100; // 100% as base
	uint256 public constant GAME_FEE = 15; // 15% game fee
	address public protocolWallet;
	uint256 public totalFees;
	uint256 public totalGames;
	uint256 private seed;
	mapping(address => bool) public blacklist;
	uint256 public maxGasPrice;
	uint256 public minGasPrice;

	constructor(address _protocolWallet) {
		require(_protocolWallet != address(0), "_protocolWallet cannot be the zero address");
		seed = uint256(blockhash(block.number - 1));
		maxGasPrice = 3 gwei;
		minGasPrice = 1 gwei;
		// Initialize rooms with different entry fees and capacities
		// 0.01 games
		createDefaultRoom(0.01 ether, 2);
		createDefaultRoom(0.01 ether, 3);
		createDefaultRoom(0.01 ether, 5);
		createDefaultRoom(0.01 ether, 10);
		createDefaultRoom(0.01 ether, 50);
		createDefaultRoom(0.01 ether, 100);
		createDefaultRoom(0.01 ether, 1000);
		// 0.02 games
		createDefaultRoom(0.02 ether, 2);
		createDefaultRoom(0.02 ether, 3);
		createDefaultRoom(0.02 ether, 5);
		createDefaultRoom(0.02 ether, 10);
		createDefaultRoom(0.02 ether, 50);
		createDefaultRoom(0.02 ether, 100);
		// 0.05 games
		createDefaultRoom(0.05 ether, 2);
		createDefaultRoom(0.05 ether, 3);
		createDefaultRoom(0.05 ether, 5);
		createDefaultRoom(0.05 ether, 10);
		createDefaultRoom(0.05 ether, 50);
		// 0.10 games
		createDefaultRoom(0.1 ether, 2);
		createDefaultRoom(0.1 ether, 3);
		createDefaultRoom(0.1 ether, 5);
		createDefaultRoom(0.1 ether, 10);
		createDefaultRoom(0.1 ether, 50);
		createDefaultRoom(0.1 ether, 100);
		createDefaultRoom(0.1 ether, 1000);
		// 0.2 games
		createDefaultRoom(0.2 ether, 2);
		createDefaultRoom(0.2 ether, 3);
		createDefaultRoom(0.2 ether, 5);
		createDefaultRoom(0.2 ether, 10);
		createDefaultRoom(0.2 ether, 50);
		// 0.5 games
		createDefaultRoom(0.5 ether, 2);
		createDefaultRoom(0.5 ether, 3);
		createDefaultRoom(0.5 ether, 5);
		createDefaultRoom(0.5 ether, 10);
		createDefaultRoom(0.5 ether, 50);
		// 1.0 games
		createDefaultRoom(1 ether, 2);
		createDefaultRoom(1 ether, 3);
		createDefaultRoom(1 ether, 5);
		createDefaultRoom(1 ether, 10);

		protocolWallet = _protocolWallet;
	}

	// **** EXTERNAL FUNCTIONS ****

	// If the numberOfEntries is too high, user may get TransactionExecutionError: Transaction ran out of gas
	// If this happens, user can register multiple times with lesser amount of numberOfEntries
	function register(uint256 roomId, uint256 numberOfEntries) external payable whenNotPaused nonReentrant {
		require(tx.origin == msg.sender, "Only EOA are allowed (tx.origin)");
		require(!Address.isContract(msg.sender), "Only EOA are allowed (isContract)");
		require(!blacklist[msg.sender], "Sender is blacklisted");
		require(tx.gasprice >= minGasPrice && tx.gasprice <= maxGasPrice, "Gas price is not within the allowed range");
		require(roomId < rooms.length, "Invalid room ID");
		require(numberOfEntries > 0, "Number of entries must be greater than 0");
		Room storage room = rooms[roomId];
		require(!room.gameStarted, "Game has already started");
		uint256 requiredAmount = room.entryFee * numberOfEntries;
		require(msg.value == requiredAmount, "Incorrect entry fee");
		require(room.players.length + numberOfEntries <= room.numberOfPlayers, "Room is full");

		for (uint256 i = 0; i < numberOfEntries; i++) {
			room.players.push(msg.sender);
		}
		room.roomBalance += requiredAmount;

		if (room.numberOfPlayers == room.players.length) {
			startGame(roomId);
		}
	}

	function exitRoom(uint256 roomId) external nonReentrant {
		require(roomId < rooms.length, "Invalid room ID");
		require(amountRegistered(roomId) > 0, "No amount to refund");
		Room storage room = rooms[roomId];
		require(!room.gameStarted, "Game has already started");

		uint256 refundValue = 0;
		// Solidity How to Loop Through Array and Remove Items Without Breaking the For Loop
		for (int256 i = int256(room.players.length) - 1; i >= 0; i--) {
			if (room.players[uint256(i)] == msg.sender) {
				refundValue += room.entryFee;
				// Move the last element to the current position
				room.players[uint256(i)] = room.players[room.players.length - 1];
				// Remove the last element
				room.players.pop();
			}
		}
		// Update room balance
		room.roomBalance -= refundValue;
		payable(msg.sender).transfer(refundValue);
	}

	function createRoom(uint256 _entryFee, uint256 _numberOfPlayers) external onlyOwner {
		require(_entryFee > 0, "Entry Fee is required");
		require(_numberOfPlayers > 1, "Number of Players must be greater than 1");
		rooms.push(
			Room({
				players: new address[](0),
				entryFee: _entryFee,
				numberOfPlayers: _numberOfPlayers,
				winner: address(0),
				gameStarted: false,
				roomBalance: 0
			})
		);
	}

	function removeRoom(uint256 roomId) external onlyOwner {
		require(roomId < rooms.length, "Invalid room ID");
		Room storage room = rooms[roomId];
		require(!room.gameStarted, "Game has already started");
		require(room.roomBalance == 0, "Room balance is not Zero");
		rooms[roomId] = rooms[rooms.length - 1];
		rooms.pop();
	}

	function forceRoomGame(uint256 roomId) external onlyOwner {
		require(roomId < rooms.length, "Invalid room ID");
		Room storage room = rooms[roomId];
		require(!room.gameStarted, "Game has already started");
		require(room.roomBalance > 0, "Room balance is Zero");
		if (room.players.length > 1) {
			startGame(roomId);
		} else {
			(bool sent1, ) = payable(room.players[0]).call{ value: room.roomBalance }("");
			require(sent1, "Force game, failed to send refund");
			delete room.players;
			room.winner = address(0);
			room.gameStarted = false;
			room.roomBalance = 0;
		}
	}

	function sweepTokens(address tokenAddress) external onlyOwner {
		IERC20 token = IERC20(tokenAddress);
		uint256 balance = token.balanceOf(address(this));
		require(balance > 0, "No tokens to sweep");
		token.safeTransfer(owner(), balance);
	}

	function updateProtocolWalletWallet(address _protocolWallet) external onlyOwner {
		require(_protocolWallet != address(0), "_protocolWallet cannot be the zero address");
		protocolWallet = _protocolWallet;
	}

	function pauseContract() external onlyOwner {
		_pause();
	}

	function unpauseContract() external onlyOwner {
		_unpause();
	}

	function addToBlacklist(address _address) public onlyOwner {
		blacklist[_address] = true;
	}

	function removeFromBlacklist(address _address) public onlyOwner {
		blacklist[_address] = false;
	}

	function setMaxGasPrice(uint256 _maxGasPrice) external onlyOwner {
		maxGasPrice = _maxGasPrice;
	}

	function setMinGasPrice(uint256 _minGasPrice) external onlyOwner {
		minGasPrice = _minGasPrice;
	}

	// **** INTERNAL FUNCTIONS ****

	function distributeFunds(uint256 roomId) internal {
		Room storage room = rooms[roomId];
		uint256 roomBalance = room.roomBalance;
		address roomWinner = room.winner;

		uint256 fee = ((roomBalance * GAME_FEE * PRECISION) / PERCENTAGE_BASE) / PRECISION;
		uint256 amountToDistribute = roomBalance - fee;

		// Reset the room state after funds have been distributed
		delete room.players;
		room.winner = address(0);
		room.gameStarted = false;
		room.roomBalance = 0;

		totalFees += fee;
		totalGames += 1;

		(bool sent1, ) = payable(roomWinner).call{ value: amountToDistribute }("");
		(bool sent2, ) = payable(protocolWallet).call{ value: fee }("");
		require(sent1, "Failed to send Ether to winner");
		require(sent2, "Failed to send Ether to dev wallet");
		emit Winner(roomWinner, amountToDistribute);
	}

	function amountRegistered(uint256 roomId) internal view returns (uint256) {
		require(roomId < rooms.length, "Invalid room ID");
		Room memory room = rooms[roomId];
		uint256 amount = 0;
		for (uint256 i = 0; i < room.players.length; i++) {
			if (room.players[i] == msg.sender) {
				amount += room.entryFee;
			}
		}
		return amount;
	}

	function startGame(uint256 roomId) internal {
		Room storage room = rooms[roomId];
		room.gameStarted = true;
		uint256 randomSeed = uint256(
			keccak256(abi.encodePacked(blockhash(block.number - 1), block.prevrandao, seed, msg.sender))
		) % room.players.length;
		room.winner = room.players[randomSeed];
		seed = uint256(keccak256(abi.encodePacked(seed, randomSeed, blockhash(block.number - 1))));
		distributeFunds(roomId);
	}

	function createDefaultRoom(uint256 _entryFee, uint256 _numberOfPlayers) internal {
		require(_entryFee > 0, "Entry Fee is required");
		require(_numberOfPlayers > 1, "Number of Players must be greater than 1");
		rooms.push(
			Room({
				players: new address[](0),
				entryFee: _entryFee,
				numberOfPlayers: _numberOfPlayers,
				winner: address(0),
				gameStarted: false,
				roomBalance: 0
			})
		);
	}

	// **** VIEW FUNCTIONS ****

	function getContractBalance() external view returns (uint256) {
		return address(this).balance;
	}

	function getTotalRooms() external view returns (uint256) {
		return rooms.length;
	}

	function getRoomInfo(
		uint256 roomId
	)
		external
		view
		returns (
			address[] memory players,
			uint256 entryFee,
			uint256 numberOfPlayers,
			bool gameStarted,
			address winner,
			uint256 roomBalance
		)
	{
		require(roomId < rooms.length, "Invalid room ID");
		Room memory room = rooms[roomId];
		players = room.players;
		entryFee = room.entryFee;
		numberOfPlayers = room.numberOfPlayers;
		gameStarted = room.gameStarted;
		winner = room.winner;
		roomBalance = room.roomBalance;
	}

	// working with dynamic array, prone to out of gas exception
	// owners must be mindful of the number of rooms they create
	function getRooms() external view returns (Room[] memory) {
		return rooms;
	}

	function amountRegistered(uint256 roomId, address _address) external view returns (uint256) {
		require(roomId < rooms.length, "Invalid room ID");
		Room memory room = rooms[roomId];
		uint256 amount = 0;
		for (uint256 i = 0; i < room.players.length; i++) {
			if (room.players[i] == _address) {
				amount += room.entryFee;
			}
		}
		return amount;
	}
}
