    "/contracts/doge_factory/DividendPayingToken.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.6.12;

import "@openzeppelin/contracts-upgradeable/token/ERC20/ERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts/math/SafeMath.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

import "./libs/SafeMathUint.sol";
import "./interface/DividendPayingTokenInterface.sol";
import "./interface/DividendPayingTokenOptionalInterface.sol";

contract DividendPayingToken is ERC20Upgradeable, OwnableUpgradeable, DividendPayingTokenInterface, DividendPayingTokenOptionalInterface {
  // using SafeMath for uint256;
  using SafeMathUint for uint256;
  using SafeMathInt for int256;

  address public REWARD;


  // With `magnitude`, we can properly distribute dividends even if the amount of received ether is small.
  // For more discussion about choosing the value of `magnitude`,
  //  see https://github.com/ethereum/EIPs/issues/1726#issuecomment-472352728
  uint256 constant internal magnitude = 2**128;

  uint256 internal magnifiedDividendPerShare;

  // About dividendCorrection:
  // If the token balance of a `_user` is never changed, the dividend of `_user` can be computed with:
  //   `dividendOf(_user) = dividendPerShare * balanceOf(_user)`.
  // When `balanceOf(_user)` is changed (via minting/burning/transferring tokens),
  //   `dividendOf(_user)` should not be changed,
  //   but the computed value of `dividendPerShare * balanceOf(_user)` is changed.
  // To keep the `dividendOf(_user)` unchanged, we add a correction term:
  //   `dividendOf(_user) = dividendPerShare * balanceOf(_user) + dividendCorrectionOf(_user)`,
  //   where `dividendCorrectionOf(_user)` is updated whenever `balanceOf(_user)` is changed:
  //   `dividendCorrectionOf(_user) = dividendPerShare * (old balanceOf(_user)) - (new balanceOf(_user))`.
  // So now `dividendOf(_user)` returns the same value before and after `balanceOf(_user)` is changed.
  mapping(address => int256) internal magnifiedDividendCorrections;
  mapping(address => uint256) internal withdrawnDividends;

  uint256 public totalDividendsDistributed;

//  function __ERC20_init(string memory name_, string memory symbol_) internal initializer
  function __DividendPayingToken_init(string memory _name, string memory _symbol) internal initializer {
      __ERC20_init(_name, _symbol);
      __Ownable_init();
  }


  function distributeUSDCDividends(uint256 amount) public onlyOwner{
    require(totalSupply() > 0);

    if (amount > 0) {
      magnifiedDividendPerShare = magnifiedDividendPerShare.add(
        (amount).mul(magnitude) / totalSupply()
      );
      emit DividendsDistributed(msg.sender, amount);

      totalDividendsDistributed = totalDividendsDistributed.add(amount);
    }
  }

  /// @notice Withdraws the ether distributed to the sender.
  /// @dev It emits a `DividendWithdrawn` event if the amount of withdrawn ether is greater than 0.
  function withdrawDividend() public virtual override {
    _withdrawDividendOfUser(msg.sender);
  }

  /// @notice Withdraws the ether distributed to the sender.
  /// @dev It emits a `DividendWithdrawn` event if the amount of withdrawn ether is greater than 0.
 function _withdrawDividendOfUser(address payable user) internal returns (uint256) {
    uint256 _withdrawableDividend = withdrawableDividendOf(user);
    if (_withdrawableDividend > 0) {
      withdrawnDividends[user] = withdrawnDividends[user].add(_withdrawableDividend);
      emit DividendWithdrawn(user, _withdrawableDividend);
      bool success = IERC20(REWARD).transfer(user, _withdrawableDividend);

      if(!success) {
        withdrawnDividends[user] = withdrawnDividends[user].sub(_withdrawableDividend);
        return 0;
      }

      return _withdrawableDividend;
    }

    return 0;
  }


  /// @notice View the amount of dividend in wei that an address can withdraw.
  /// @param _owner The address of a token holder.
  /// @return The amount of dividend in wei that `_owner` can withdraw.
  function dividendOf(address _owner) public view override returns(uint256) {
    return withdrawableDividendOf(_owner);
  }

  /// @notice View the amount of dividend in wei that an address can withdraw.
  /// @param _owner The address of a token holder.
  /// @return The amount of dividend in wei that `_owner` can withdraw.
  function withdrawableDividendOf(address _owner) public view override returns(uint256) {
    return accumulativeDividendOf(_owner).sub(withdrawnDividends[_owner]);
  }

  /// @notice View the amount of dividend in wei that an address has withdrawn.
  /// @param _owner The address of a token holder.
  /// @return The amount of dividend in wei that `_owner` has withdrawn.
  function withdrawnDividendOf(address _owner) public view override returns(uint256) {
    return withdrawnDividends[_owner];
  }


  /// @notice View the amount of dividend in wei that an address has earned in total.
  /// @dev accumulativeDividendOf(_owner) = withdrawableDividendOf(_owner) + withdrawnDividendOf(_owner)
  /// = (magnifiedDividendPerShare * balanceOf(_owner) + magnifiedDividendCorrections[_owner]) / magnitude
  /// @param _owner The address of a token holder.
  /// @return The amount of dividend in wei that `_owner` has earned in total.
  function accumulativeDividendOf(address _owner) public view override returns(uint256) {
    return magnifiedDividendPerShare.mul(balanceOf(_owner)).toInt256Safe()
      .add(magnifiedDividendCorrections[_owner]).toUint256Safe() / magnitude;
  }

  /// @dev Internal function that transfer tokens from one address to another.
  /// Update magnifiedDividendCorrections to keep dividends unchanged.
  /// @param from The address to transfer from.
  /// @param to The address to transfer to.
  /// @param value The amount to be transferred.
  function _transfer(address from, address to, uint256 value) internal virtual override {
    require(false);

    int256 _magCorrection = magnifiedDividendPerShare.mul(value).toInt256Safe();
    magnifiedDividendCorrections[from] = magnifiedDividendCorrections[from].add(_magCorrection);
    magnifiedDividendCorrections[to] = magnifiedDividendCorrections[to].sub(_magCorrection);
  }

  /// @dev Internal function that mints tokens to an account.
  /// Update magnifiedDividendCorrections to keep dividends unchanged.
  /// @param account The account that will receive the created tokens.
  /// @param value The amount that will be created.
  function _mint(address account, uint256 value) internal override {
    super._mint(account, value);

    magnifiedDividendCorrections[account] = magnifiedDividendCorrections[account]
      .sub( (magnifiedDividendPerShare.mul(value)).toInt256Safe() );
  }

  /// @dev Internal function that burns an amount of the token of a given account.
  /// Update magnifiedDividendCorrections to keep dividends unchanged.
  /// @param account The account whose tokens will be burnt.
  /// @param value The amount that will be burnt.
  function _burn(address account, uint256 value) internal override {
    super._burn(account, value);

    magnifiedDividendCorrections[account] = magnifiedDividendCorrections[account]
      .add( (magnifiedDividendPerShare.mul(value)).toInt256Safe() );
  }

  function _setBalance(address account, uint256 newBalance) internal {
    uint256 currentBalance = balanceOf(account);

    if(newBalance > currentBalance) {
      uint256 mintAmount = newBalance.sub(currentBalance);
      _mint(account, mintAmount);
    } else if(newBalance < currentBalance) {
      uint256 burnAmount = currentBalance.sub(newBalance);
      _burn(account, burnAmount);
    }
  }
}
"
    },
    "/contracts/doge_factory/DividendTracker.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.6.12;

// import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "./DividendPayingToken.sol";
import "./libs/IterableMapping.sol";

contract DividendTracker is DividendPayingToken {
    // using SafeMath for uint256;
    using SafeMathInt for int256;
    using IterableMapping for IterableMapping.Map;

    IterableMapping.Map private tokenHoldersMap;
    uint256 public lastProcessedIndex;

    mapping (address => bool) public excludedFromDividends;

    mapping (address => uint256) public lastClaimTimes;

    uint256 public claimWait;
    uint256 public minimumTokenBalanceForDividends;

    event ExcludeFromDividends(address indexed account);
    event ClaimWaitUpdated(uint256 indexed newValue, uint256 indexed oldValue);

    event Claim(address indexed account, uint256 amount, bool indexed automatic);

    function initialize(address rewardToken, uint256 _baseTotalSupply) public initializer {
        __DividendPayingToken_init("_Dividend_Tracker", "_Dividend_Tracker");
        REWARD = rewardToken;
        claimWait = 300;
        // 0.01%
        minimumTokenBalanceForDividends = _baseTotalSupply / 10000;

        // transferOwnership(rewardToken);
    }

    function _transfer(address, address, uint256) internal override {
        require(false, "Dividend_Tracker: No transfers allowed");
    }

    function withdrawDividend() public override {
        require(false, "Dividend_Tracker: withdrawDividend disabled. Use the 'claim' function on the main FUCKBABY contract.");
    }

    function excludeFromDividends(address account) external onlyOwner {
    	require(!excludedFromDividends[account]);
    	excludedFromDividends[account] = true;

    	_setBalance(account, 0);
    	tokenHoldersMap.remove(account);

    	emit ExcludeFromDividends(account);
    }

    function updateClaimWait(uint256 newClaimWait) external onlyOwner {
        require(newClaimWait >= 3600 && newClaimWait <= 86400, "Dividend_Tracker: claimWait must be updated to between 1 and 24 hours");
        require(newClaimWait != claimWait, "Dividend_Tracker: Cannot update claimWait to same value");
        emit ClaimWaitUpdated(newClaimWait, claimWait);
        claimWait = newClaimWait;
    }

    function getLastProcessedIndex() external view returns(uint256) {
    	return lastProcessedIndex;
    }

    function getNumberOfTokenHolders() external view returns(uint256) {
        return tokenHoldersMap.keys.length;
    }



    function getAccount(address _account)
        public view returns (
            address account,
            int256 index,
            int256 iterationsUntilProcessed,
            uint256 withdrawableDividends,
            uint256 totalDividends,
            uint256 lastClaimTime,
            uint256 nextClaimTime,
            uint256 secondsUntilAutoClaimAvailable) {
        account = _account;

        index = tokenHoldersMap.getIndexOfKey(account);

        iterationsUntilProcessed = -1;

        if(index >= 0) {
            if(uint256(index) > lastProcessedIndex) {
                iterationsUntilProcessed = index.sub(int256(lastProcessedIndex));
            }
            else {
                uint256 processesUntilEndOfArray = tokenHoldersMap.keys.length > lastProcessedIndex ?
                                                        tokenHoldersMap.keys.length.sub(lastProcessedIndex) :
                                                        0;


                iterationsUntilProcessed = index.add(int256(processesUntilEndOfArray));
            }
        }


        withdrawableDividends = withdrawableDividendOf(account);
        totalDividends = accumulativeDividendOf(account);

        lastClaimTime = lastClaimTimes[account];

        nextClaimTime = lastClaimTime > 0 ?
                                    lastClaimTime.add(claimWait) :
                                    0;

        secondsUntilAutoClaimAvailable = nextClaimTime > block.timestamp ?
                                                    nextClaimTime.sub(block.timestamp) :
                                                    0;
    }

    function getAccountAtIndex(uint256 index)
        public view returns (
            address,
            int256,
            int256,
            uint256,
            uint256,
            uint256,
            uint256,
            uint256) {
    	if(index >= tokenHoldersMap.size()) {
            return (0x0000000000000000000000000000000000000000, -1, -1, 0, 0, 0, 0, 0);
        }

        address account = tokenHoldersMap.getKeyAtIndex(index);

        return getAccount(account);
    }

    function canAutoClaim(uint256 lastClaimTime) private view returns (bool) {
    	if(lastClaimTime > block.timestamp)  {
    		return false;
    	}

    	return block.timestamp.sub(lastClaimTime) >= claimWait;
    }

    function setBalance(address payable account, uint256 newBalance) external onlyOwner {
    	if(excludedFromDividends[account]) {
    		return;
    	}

    	if(newBalance >= minimumTokenBalanceForDividends) {
            _setBalance(account, newBalance);
    		tokenHoldersMap.set(account, newBalance);
    	}
    	else {
            _setBalance(account, 0);
    		tokenHoldersMap.remove(account);
    	}

    	processAccount(account, true);
    }

    function process(uint256 gas) public returns (uint256, uint256, uint256) {
    	uint256 numberOfTokenHolders = tokenHoldersMap.keys.length;

    	if(numberOfTokenHolders == 0) {
    		return (0, 0, lastProcessedIndex);
    	}

    	uint256 _lastProcessedIndex = lastProcessedIndex;

    	uint256 gasUsed = 0;

    	uint256 gasLeft = gasleft();

    	uint256 iterations = 0;
    	uint256 claims = 0;

    	while(gasUsed < gas && iterations < numberOfTokenHolders) {
    		_lastProcessedIndex++;

    		if(_lastProcessedIndex >= tokenHoldersMap.keys.length) {
    			_lastProcessedIndex = 0;
    		}

    		address account = tokenHoldersMap.keys[_lastProcessedIndex];

    		if(canAutoClaim(lastClaimTimes[account])) {
    			if(processAccount(payable(account), true)) {
    				claims++;
    			}
    		}

    		iterations++;

    		uint256 newGasLeft = gasleft();

    		if(gasLeft > newGasLeft) {
    			gasUsed = gasUsed.add(gasLeft.sub(newGasLeft));
    		}

    		gasLeft = newGasLeft;
    	}

    	lastProcessedIndex = _lastProcessedIndex;

    	return (iterations, claims, lastProcessedIndex);
    } 

    function processAccount(address payable account, bool automatic) public onlyOwner returns (bool) {
        uint256 amount = _withdrawDividendOfUser(account);

    	if(amount > 0) {
    		lastClaimTimes[account] = block.timestamp;
            emit Claim(account, amount, automatic);
    		return true;
    	}

    	return false;
    }
}"
    },
    "/contracts/doge_factory/FeeProcessor.sol": {
      "content": "//SPDX-License-Identifier: Unlicense
pragma solidity ^0.6.12;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/SafeERC20.sol";
import "@openzeppelin/contracts/math/SafeMath.sol";
import "@openzeppelin/contracts-upgradeable/proxy/Initializable.sol";

import "./interface/IUniswapV2Router02.sol";

// due token in [token0,token1] cannot as `to` in swap,
// swap via FeeHolder, then transfer back
contract FeeProcessor is Initializable{
    using SafeMath for uint256;
    using SafeERC20 for IERC20;

    bool public constant isFeeProcessor = true;

    address public jfRouter;

    address public constant DEAD = 0x000000000000000000000000000000000000dEaD;

    /** OK MainNet */
    // address public constant WOKT = 0x8F8526dbfd6E38E3D8307702cA8469Bae6C56C15;
    // address public constant USDT = 0x382bB369d343125BfB2117af9c149795C6C65C50;
    // address public constant JF   = 0x5fAc926Bf1e638944BB16fb5B787B5bA4BC85b0A;

    /** bsc testnet */
    // address public constant WOKT = 0xae13d989daC2f0dEbFf460aC112a837C89BAa7cd;
    // address public constant USDT = 0x783145bb5499Ff4d10a1a7fBd5aB53fCdcE58641;
    // address public constant JF   = 0x9A16b4f59Dc4072EA477ddd6bc7E45686d2a9804;

    /** BSC MainNet */
    address public constant WOKT = 0xbb4CdB9CBd36B01bD1cBaEBF2De08d9173bc095c;
    address public constant USDT = 0x55d398326f99059fF775485246999027B3197955;
    address public constant JF   = 0x5fAc926Bf1e638944BB16fb5B787B5bA4BC85b0A;

    address public SOURCE_TOKEN;
    address public PAIR_TOKEN;

    address public marketWallet;
    address public dividendWallet;

    uint256 public marketFee;
    uint256 public dividendFee;
    uint256 public contributeFee;
    uint256 public liquidityFee;

    uint256 public totalFee;

    function initialize(
        address _router, 
        address _sourceToken, 
        address _pairedToken, 
        address _dividendWallet,
        address _marketWallet,
        uint256 _marketFee,
        uint256 _liquidityFee,
        uint256 _dividendFee,
        uint256 _contributeFee
        )
        public
        initializer
    {
        jfRouter = _router;
        SOURCE_TOKEN = _sourceToken;
        PAIR_TOKEN = _pairedToken;
        marketWallet = _marketWallet;
        dividendWallet = _dividendWallet;

        marketFee  = _marketFee;
        dividendFee = _dividendFee;
        contributeFee = _contributeFee;
        liquidityFee = _liquidityFee;

        totalFee = _marketFee.add(_dividendFee).add(_contributeFee).add(_liquidityFee);
    }

    modifier onlySourceToken {
        require(msg.sender == SOURCE_TOKEN, "FeeProcessor: only SOURCE_TOKEN");
        _;
    }

    function feeProcess() external onlySourceToken {
        // safe check
        uint256 sourceAmount = IERC20(SOURCE_TOKEN).balanceOf(address(this));

        IERC20(SOURCE_TOKEN).approve(jfRouter, sourceAmount);
        // left SOURCE amount to add Liquidity
        uint256 leftForLiquidity = sourceAmount
                .mul(liquidityFee)
                .div(totalFee);
        //swap to JF
        feeReward2Jf(sourceAmount.sub(leftForLiquidity));
        // fee to market & dividend & contribute
        shareJfReward();
        // addLiquidity
        feeReward2LP();
    }

    function shareJfReward() private {
        uint256 jfBal = IERC20(JF).balanceOf(address(this));
        uint256 jfPoint = totalFee.sub(liquidityFee);
        // marketWallet
        IERC20(JF).transfer(marketWallet, jfBal.mul(marketFee).div(jfPoint));
        // Dividend
        IERC20(JF).transfer(dividendWallet, jfBal.mul(dividendFee).div(jfPoint));
         // contribute
        IERC20(JF).transfer(DEAD, jfBal.mul(contributeFee).div(jfPoint));
    }

    function toJfPath() private view returns (address[] memory path) {
        if(PAIR_TOKEN == JF) {
            address[] memory path = new address[](2);
            path[0] = SOURCE_TOKEN;
            path[1] = JF;
            return path;
        } else if (PAIR_TOKEN == USDT) {
            address[] memory path = new address[](3);
            path[0] = SOURCE_TOKEN;
            path[1] = USDT;
            path[2] = JF;
            return path;
        } else if (PAIR_TOKEN == WOKT) {
            address[] memory path = new address[](4);
            path[0] = SOURCE_TOKEN;
            path[1] = WOKT;
            path[2] = USDT;
            path[3] = JF;
            return path;
        } else {
            revert("PairToken not support");
        }
    }

    function toPairToken() private  {

        uint256 sourceAmount = IERC20(SOURCE_TOKEN).balanceOf(address(this)).div(2);

        address[] memory path = new address[](2);
        path[0] = SOURCE_TOKEN;
        path[1] = PAIR_TOKEN;
        IUniswapV2Router02(jfRouter).swapExactTokensForTokens(
                sourceAmount,
                0,
                path,
                address(this), // to cannot be address(token)
                block.timestamp
        );
    }

    //@dev Mint LP to dEaD,  sell to USDT
    // Mint SOURCE-USDT LP
    function feeReward2LP() private {
        // sell half to PairedToken
        toPairToken();

        uint256 pairBal = IERC20(PAIR_TOKEN).balanceOf(address(this));
        IERC20(PAIR_TOKEN).approve(jfRouter, pairBal);

        IUniswapV2Router02(jfRouter).addLiquidity(
                PAIR_TOKEN, SOURCE_TOKEN, 
                pairBal,
                IERC20(SOURCE_TOKEN).balanceOf(address(this)),
                0, 0,
                DEAD,
                block.timestamp
        );
    }

    //@dev publish Reward to users who hold  
    function feeReward2Jf(uint256 totalRewardAmount) private {

        IUniswapV2Router02(jfRouter).swapExactTokensForTokensSupportingFeeOnTransferTokens(
            totalRewardAmount,
            0,
            toJfPath(),
            address(this),
            block.timestamp
        );
    }

    // function safeApprove() public {
    //     //  Router swap 2 JF & addLiquidity
    //     IERC20(PAIR_TOKEN).safeApprove(jfRouter, ~uint256(0));
    //     //  Router addLiquidity
    //     IERC20(PAIR_TOKEN).safeApprove(jfRouter, 0);
    //     IERC20(PAIR_TOKEN).safeApprove(jfRouter, ~uint256(0));
    // }

    function min(uint256 a, uint256 b) private pure returns (uint256) {
        return a < b? a : b;
    }

}"
    },
    "/contracts/doge_factory/JFMemeCoin.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.6.12;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Address.sol";

import "./DividendTracker.sol";
import "./FeeProcessor.sol";

import "./interface/IUniswapV2Factory.sol";
import "./interface/IUniswapV2Router02.sol";
interface IFeeProcessor {
    function feeProcess() external;
}

contract  JFMemeCoin is ERC20 {
    using SafeMath for uint256;

    address public feeProcessor;
    address public recycleAddr;

    IUniswapV2Router02 public uniswapV2Router;
    address public  uniswapV2Pair;
    address public  tokenFactory;
    address public  ceoAddress;

    bool private swapping;

    DividendTracker public dividendTracker;

    address public deadWallet = 0x000000000000000000000000000000000000dEaD;

    address public JF = 0x5fAc926Bf1e638944BB16fb5B787B5bA4BC85b0A;
    // address public JF = 0x9A16b4f59Dc4072EA477ddd6bc7E45686d2a9804;

    uint256 public swapTokensAtAmount;

    uint256 public destroyFee;
    uint256 public dividendsFee;
    uint256 public liquidityFee;
    uint256 public marketFee;
    uint256 public jfContribute; //0.5%
    // based 10000
    uint256 public totalFees;

    // uint256 private minHoldAmount = 0;
    // use by default 300,000 gas to process auto-claiming dividends
    uint256 public gasForProcessing = 300000;
     // exclude from fees and max transaction amount
    mapping (address => bool) private _isExcludedFromFees;
    mapping (address => address) public userReferral;

    // store addresses that a automatic market maker pairs. Any transfer *to* these addresses
    // could be subject to a maximum transfer amount
    mapping (address => bool) public automatedMarketMakerPairs;

    event UpdateDividendTracker(address indexed newAddress, address indexed oldAddress);

    event UpdateUniswapV2Router(address indexed newAddress, address indexed oldAddress);

    event ExcludeFromFees(address indexed account, bool isExcluded);
    event ExcludeMultipleAccountsFromFees(address[] accounts, bool isExcluded);

    event SetAutomatedMarketMakerPair(address indexed pair, bool indexed value);

    event LiquidityWalletUpdated(address indexed newLiquidityWallet, address indexed oldLiquidityWallet);

    event GasForProcessingUpdated(uint256 indexed newValue, uint256 indexed oldValue);

    event SwapAndLiquify(
        uint256 tokensSwapped,
        uint256 ethReceived,
        uint256 tokensIntoLiquidity
    );

    event SendDividends(
    	uint256 amount
    );

    event ProcessedDividendTracker(
    	uint256 iterations,
    	uint256 claims,
        uint256 lastProcessedIndex,
    	bool indexed automatic,
    	uint256 gas,
    	address indexed processor
    );

    constructor(
        string memory name,
        string memory symbol,
        address _recipient,
        uint256 _totalSupply
    )   
        ERC20(name, symbol)
        public 
    {
        _mint(_recipient, _totalSupply);
        ceoAddress = _recipient;

        swapTokensAtAmount = _totalSupply / 10000;
    }

    function initialize(
        address _pairedToken,
        address _router,
        uint256 _destroyFee,
        uint256 _dividendsFee,
        uint256 _marketFee,
        uint256 _liquidityFee,
        uint256 _jfContributeFee,

        address _excludeAccount,
        address _dividendTracker,
        address _feeProcessor
    )
        public 
    {

        tokenFactory = msg.sender;


        destroyFee = _destroyFee;
        dividendsFee = _dividendsFee;
        marketFee =  _marketFee;
        liquidityFee = _liquidityFee;
        jfContribute = _jfContributeFee;

        totalFees = destroyFee.add(dividendsFee).add(marketFee).add(liquidityFee).add(jfContribute);
        require(totalFees <= 255, "Total fee no more than 25%");

        dividendTracker = DividendTracker(_dividendTracker); // new DividendTracker(address(this), totalSupply());
        DividendTracker(dividendTracker).initialize(JF, totalSupply());

        feeProcessor = _feeProcessor;// address( new FeeProcessor(_router, address(this), _pairedToken, address(dividendTracker), ceoAddress, marketFee, liquidityFee, dividendsFee, liquidityFee));

        IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(_router); // spooky router
         // Create a uniswap pair for this new token
        address _uniswapV2Pair = IUniswapV2Factory(_uniswapV2Router.factory())
            .pairFor(address(this), _pairedToken);  

        uniswapV2Router = _uniswapV2Router;
        uniswapV2Pair = _uniswapV2Pair;

    if( _excludeAccount!=address(0) ) {
        excludeFromFees(_excludeAccount, true);
        }
    // }

    // function init() private {
        _setAutomatedMarketMakerPair(uniswapV2Pair, true);
        // exclude from receiving dividends
        dividendTracker.excludeFromDividends(address(dividendTracker));
        dividendTracker.excludeFromDividends(address(this));
        dividendTracker.excludeFromDividends(deadWallet);
        //dividendTracker.excludeFromDividends(address(uniswapV2Pair));

        dividendTracker.excludeFromDividends(feeProcessor);

        // exclude from paying fees or having max transaction amount
        excludeFromFees(feeProcessor, true);
        excludeFromFees(_owner(), true);
        excludeFromFees(address(this), true);
    }

    function _owner() private view returns (address) {
        return ceoAddress;
    }

    // receive() external payable {
  	// }

    function excludeFromFees(address account, bool excluded) private {
        require(_isExcludedFromFees[account] != excluded, "Account is already the value of 'excluded'");
        _isExcludedFromFees[account] = excluded;
        emit ExcludeFromFees(account, excluded);
    }


    function _setAutomatedMarketMakerPair(address pair, bool value) private {
        require(automatedMarketMakerPairs[pair] != value, "Automated market maker pair is already set to that value");
        automatedMarketMakerPairs[pair] = value;

        if(value) {
            dividendTracker.excludeFromDividends(pair);
        }

        emit SetAutomatedMarketMakerPair(pair, value);
    }


    function getClaimWait() external view returns(uint256) {
        return dividendTracker.claimWait();
    }

    function getTotalDividendsDistributed() external view returns (uint256) {
        return dividendTracker.totalDividendsDistributed();
    }

    function isExcludedFromFees(address account) public view returns(bool) {
        return _isExcludedFromFees[account];
    }

    function withdrawableDividendOf(address account) public view returns(uint256) {
    	return dividendTracker.withdrawableDividendOf(account);
  	}

	function dividendTokenBalanceOf(address account) public view returns (uint256) {
		return dividendTracker.balanceOf(account);
	}

    function getAccountDividendsInfo(address account)
        external view returns (
            address,
            int256,
            int256,
            uint256,
            uint256,
            uint256,
            uint256,
            uint256) {
        return dividendTracker.getAccount(account);
    }

	function getAccountDividendsInfoAtIndex(uint256 index)
        external view returns (
            address,
            int256,
            int256,
            uint256,
            uint256,
            uint256,
            uint256,
            uint256) {
    	return dividendTracker.getAccountAtIndex(index);
    }

	function processDividendTracker(uint256 gas) external {
		(uint256 iterations, uint256 claims, uint256 lastProcessedIndex) = dividendTracker.process(gas);
		emit ProcessedDividendTracker(iterations, claims, lastProcessedIndex, false, gas, tx.origin);
    }

    function claim() external {
		dividendTracker.processAccount(msg.sender, false);
    }

    function getLastProcessedIndex() external view returns(uint256) {
    	return dividendTracker.getLastProcessedIndex();
    }

    function getNumberOfDividendTokenHolders() external view returns(uint256) {
        return dividendTracker.getNumberOfTokenHolders();
    }

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal override {

        if(from == address(dividendTracker)){
            super._transfer(from, to, amount);
            return;
        }

        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");

        // if(amount.add(minHoldAmount) > balanceOf(from) ) {
        //     if(balanceOf(from) > minHoldAmount) {
        //         amount = balanceOf(from).sub(minHoldAmount);
        //     }else {
        //         amount = 0;
        //     }
        // }

        if(amount == 0) {
            super._transfer(from, to, 0);
            return;
        }

		uint256 contractTokenBalance = balanceOf(address(this));

        bool canSwap = contractTokenBalance >= swapTokensAtAmount;

        if( canSwap &&
            !swapping &&
            !automatedMarketMakerPairs[from] &&
            from != _owner() &&
            to != _owner()
        ) {
            swapping = true;
            processFee();                           
            swapping = false;
        }

        bool takeFee = !swapping;
        // if any account belongs to _isExcludedFromFee account then remove the fee
        if(_isExcludedFromFees[from] || _isExcludedFromFees[to]) {
            takeFee = false;
        }

        if(takeFee) {
        	uint256 fees = amount.mul(totalFees).div(1000);
        	amount = amount.sub(fees);
            super._transfer(from, address(this), fees);
        }
        super._transfer(from, to, amount);

        try dividendTracker.setBalance(payable(from), balanceOf(from)) {} catch {
            require(false,"from not success");
        }
        try dividendTracker.setBalance(payable(to), balanceOf(to)) {} catch {
            require(false,"to not success");
        }
        if(!(from==_owner()||to==_owner())&&to == uniswapV2Pair ){
            if(!swapping) {
	    	    uint256 gas = gasForProcessing;

                try dividendTracker.process(gas) returns (uint256 iterations, uint256 claims, uint256 lastProcessedIndex) {
                    emit ProcessedDividendTracker(iterations, claims, lastProcessedIndex, true, gas, tx.origin);
                }
                catch {

                }
            }
        }
    }

    function processFee() private {
        uint256 bal = swapTokensAtAmount;
        // Destroy
        uint256 destroyAmount = bal.mul(destroyFee).div(totalFees);
        super._transfer(address(this), deadWallet, destroyAmount);
        // convert to Liquidity
        uint256 convertAmount = bal.sub(destroyAmount);
        // send fee to Processor
        super._transfer(address(this), feeProcessor, convertAmount);

        uint256 beforeConvertDividendReward = IERC20(dividendTracker.REWARD()).balanceOf(address(dividendTracker));
        // convert
        IFeeProcessor(feeProcessor).feeProcess();
        uint256 dividendAddition = IERC20(dividendTracker.REWARD()).balanceOf(address(dividendTracker)).sub(beforeConvertDividendReward);
        // dividends
        swapAndSendDividends(dividendAddition);
    }

    function swapAndSendDividends(uint256 dividends) private{
        // reward addition in FeeProcessor.
        dividendTracker.distributeUSDCDividends(dividends);
        emit SendDividends(dividends);
    }

    function refundMisToken(address token, address to) external {
        require(token != address(this), "Token not refund able");
        require(msg.sender == tokenFactory, "Token not refund able");

        if(token == address(0) ) {
            uint256 bal = address(this).balance;
            (bool success,) = to.call{value:bal}(new bytes(0));
            require(success, 'ETH_TRANSFER_FAILED');
        } else {
            uint256 bal = IERC20(token).balanceOf(address(this));
            safeTransfer(token, to, bal);
        }

    }

    function safeTransfer(address token, address to, uint value) internal {
        // bytes4(keccak256(bytes('transfer(address,uint256)')));
        (bool success, bytes memory data) = token.call(abi.encodeWithSelector(0xa9059cbb, to, value));
        require(success && (data.length == 0 || abi.decode(data, (bool))), 'TRANSFER_FAILED');
    }

    function owner() public view virtual returns (address) {
        return deadWallet;
    }
}"
    },
    "/contracts/doge_factory/interface/DividendPayingTokenInterface.sol": {
      "content": "pragma solidity ^0.6.12;


/// @title Dividend-Paying Token Interface
/// @author Roger Wu (https://github.com/roger-wu)
/// @dev An interface for a dividend-paying token contract.
interface DividendPayingTokenInterface {
  /// @notice View the amount of dividend in wei that an address can withdraw.
  /// @param _owner The address of a token holder.
  /// @return The amount of dividend in wei that `_owner` can withdraw.
  function dividendOf(address _owner) external view returns(uint256);

  /// @notice Distributes ether to token holders as dividends.
  /// @dev SHOULD distribute the paid ether to token holders as dividends.
  ///  SHOULD NOT directly transfer ether to token holders in this function.
  ///  MUST emit a `DividendsDistributed` event when the amount of distributed ether is greater than 0.
  //todo
  // function distributeDividends() external payable;

  /// @notice Withdraws the ether distributed to the sender.
  /// @dev SHOULD transfer `dividendOf(msg.sender)` wei to `msg.sender`, and `dividendOf(msg.sender)` SHOULD be 0 after the transfer.
  ///  MUST emit a `DividendWithdrawn` event if the amount of ether transferred is greater than 0.
  function withdrawDividend() external;

  /// @dev This event MUST emit when ether is distributed to token holders.
  /// @param from The address which sends ether to this contract.
  /// @param weiAmount The amount of distributed ether in wei.
  event DividendsDistributed(
    address indexed from,
    uint256 weiAmount
  );

  /// @dev This event MUST emit when an address withdraws their dividend.
  /// @param to The address which withdraws ether from this contract.
  /// @param weiAmount The amount of withdrawn ether in wei.
  event DividendWithdrawn(
    address indexed to,
    uint256 weiAmount
  );
}"
    },
    "/contracts/doge_factory/interface/DividendPayingTokenOptionalInterface.sol": {
      "content": "pragma solidity ^0.6.12;


/// @title Dividend-Paying Token Optional Interface
/// @author Roger Wu (https://github.com/roger-wu)
/// @dev OPTIONAL functions for a dividend-paying token contract.
interface DividendPayingTokenOptionalInterface {
  /// @notice View the amount of dividend in wei that an address can withdraw.
  /// @param _owner The address of a token holder.
  /// @return The amount of dividend in wei that `_owner` can withdraw.
  function withdrawableDividendOf(address _owner) external view returns(uint256);

  /// @notice View the amount of dividend in wei that an address has withdrawn.
  /// @param _owner The address of a token holder.
  /// @return The amount of dividend in wei that `_owner` has withdrawn.
  function withdrawnDividendOf(address _owner) external view returns(uint256);

  /// @notice View the amount of dividend in wei that an address has earned in total.
  /// @dev accumulativeDividendOf(_owner) = withdrawableDividendOf(_owner) + withdrawnDividendOf(_owner)
  /// @param _owner The address of a token holder.
  /// @return The amount of dividend in wei that `_owner` has earned in total.
  function accumulativeDividendOf(address _owner) external view returns(uint256);
}"
