interface IERC20Metadata is IERC20 {
    function name() external view returns (string memory);

    function symbol() external view returns (string memory);

    function decimals() external view returns (uint8);
}

contract ERC20 is Context, IERC20, IERC20Metadata {
    using SafeMath for uint256;

    mapping(address => uint256) private _balances;

    mapping(address => mapping(address => uint256)) private _allowances;

    uint256 internal _totalSupply;

    string private _name;
    string private _symbol;

    constructor(string memory name_, string memory symbol_) {
        _name = name_;
        _symbol = symbol_;
    }

    function name() public view virtual override returns (string memory) {
        return _name;
    }

    function symbol() public view virtual override returns (string memory) {
        return _symbol;
    }

    function decimals() public view virtual override returns (uint8) {
        return 18;
    }

    function totalSupply() public view virtual override returns (uint256) {
        return _totalSupply;
    }

    function balanceOf(
        address account
    ) public view virtual override returns (uint256) {
        return _balances[account];
    }

    function transfer(
        address recipient,
        uint256 amount
    ) public virtual override returns (bool) {
        _transfer(_msgSender(), recipient, amount);
        return true;
    }

    function allowance(
        address owner,
        address spender
    ) public view virtual override returns (uint256) {
        return _allowances[owner][spender];
    }

    function approve(
        address spender,
        uint256 amount
    ) public virtual override returns (bool) {
        _approve(_msgSender(), spender, amount);
        return true;
    }

    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) public virtual override returns (bool) {
        _transfer(sender, recipient, amount);
        _approve(
            sender,
            _msgSender(),
            _allowances[sender][_msgSender()].sub(
                amount,
                "ERC20: transfer amount exceeds allowance"
            )
        );
        return true;
    }

    function increaseAllowance(
        address spender,
        uint256 addedValue
    ) public virtual returns (bool) {
        _approve(
            _msgSender(),
            spender,
            _allowances[_msgSender()][spender].add(addedValue)
        );
        return true;
    }

    function decreaseAllowance(
        address spender,
        uint256 subtractedValue
    ) public virtual returns (bool) {
        _approve(
            _msgSender(),
            spender,
            _allowances[_msgSender()][spender].sub(
                subtractedValue,
                "ERC20: decreased allowance below zero"
            )
        );
        return true;
    }

    function _transfer(
        address sender,
        address recipient,
        uint256 amount
    ) internal virtual {
        require(sender != address(0), "ERC20: transfer from the zero address");
        require(recipient != address(0), "ERC20: transfer to the zero address");

        _beforeTokenTransfer(sender, recipient, amount);

        _balances[sender] = _balances[sender].sub(
            amount,
            "ERC20: transfer amount exceeds balance"
        );
        _balances[recipient] = _balances[recipient].add(amount);
        emit Transfer(sender, recipient, amount);
    }

    function _mint(address account, uint256 amount) internal virtual {
        require(account != address(0), "ERC20: mint to the zero address");

        _beforeTokenTransfer(address(0), account, amount);

        _totalSupply = _totalSupply.add(amount);
        _balances[account] = _balances[account].add(amount);
        emit Transfer(address(0), account, amount);
    }

    function _burn(address account, uint256 amount) internal virtual {
        require(account != address(0), "ERC20: burn from the zero address");

        _beforeTokenTransfer(account, address(0), amount);

        _balances[account] = _balances[account].sub(
            amount,
            "ERC20: burn amount exceeds balance"
        );
        _totalSupply = _totalSupply.sub(amount);
        emit Transfer(account, address(0), amount);
    }

    function _approve(
        address owner,
        address spender,
        uint256 amount
    ) internal virtual {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual {}
}

library SafeMath {
    function add(uint256 a, uint256 b) internal pure returns (uint256) {
        uint256 c = a + b;
        require(c >= a, "SafeMath: addition overflow");

        return c;
    }

    function sub(uint256 a, uint256 b) internal pure returns (uint256) {
        return sub(a, b, "SafeMath: subtraction overflow");
    }

    function sub(
        uint256 a,
        uint256 b,
        string memory errorMessage
    ) internal pure returns (uint256) {
        require(b <= a, errorMessage);
        uint256 c = a - b;

        return c;
    }

    function mul(uint256 a, uint256 b) internal pure returns (uint256) {
        if (a == 0) {
            return 0;
        }

        uint256 c = a * b;
        require(c / a == b, "SafeMath: multiplication overflow");

        return c;
    }

    function div(uint256 a, uint256 b) internal pure returns (uint256) {
        return div(a, b, "SafeMath: division by zero");
    }

    function div(
        uint256 a,
        uint256 b,
        string memory errorMessage
    ) internal pure returns (uint256) {
        require(b > 0, errorMessage);
        uint256 c = a / b;
        // assert(a == b * c + a % b); // There is no case in which this doesn't hold

        return c;
    }

    function mod(uint256 a, uint256 b) internal pure returns (uint256) {
        return mod(a, b, "SafeMath: modulo by zero");
    }

    function mod(
        uint256 a,
        uint256 b,
        string memory errorMessage
    ) internal pure returns (uint256) {
        require(b != 0, errorMessage);
        return a % b;
    }
}

contract CashemonPayment is Ownable {
    address public deadwallet = 0x000000000000000000000000000000000000dEaD;
    address public stakingc = 0xb6FABB3e20947620649e21Cea00f04Faa9724D67;
    address public lotteryw = 0xa8d94EBd2E4d4606D34128a2Ede72c8795F3608c;
    address public teamw = 0x0CDb98d3F8dE9AE54C7f1437F2DdBFCDCd8931D9;

    uint8 public stakingOutgoing = 40; // 40% will be sent to the stakingcontract
    uint8 public lotteryOutgoing = 30; // 30% will be sent to the Lottery
    uint8 public teamOutgoing = 20; // 20% goes to Team
    uint8 public burnPercent = 10; // 10% of tokens will go to the dead address.

    IERC20 public paytoken = IERC20(0x7B86f5ca09Dc00502E342b0FeF5117E1c32222Ce);

    function changeRewardPercent(
        uint8 _stakingOutgoing,
        uint8 _lotteryOutgoing,
        uint8 _teamOutgoing,
        uint8 _burnPercent
    ) public onlyOwner {
        require(
            _stakingOutgoing +
                _lotteryOutgoing +
                _teamOutgoing +
                _burnPercent ==
                uint8(100),
            "Sum should be 100 of total reward percent"
        );
        stakingOutgoing = _stakingOutgoing;
        lotteryOutgoing = _lotteryOutgoing;
        burnPercent = _burnPercent;
        teamOutgoing = _teamOutgoing;
    }

    function updateTeamWallet(address account) public onlyOwner {
        teamw = account;
    }

    function updatePayToken(address account) public onlyOwner {
        paytoken = IERC20(account);
    }

    function updateLotteryWallet(address account) public onlyOwner {
        lotteryw = account;
    }

    /* Write functions */
    function orderCashemon(uint256 amount) external {
        require(
            paytoken.transferFrom(
                msg.sender,
                address(this),
                amount
            ) == true,
            "Could not transfer tokens from your address to this contract"
        );

        uint256 collected = paytoken.balanceOf(address(this));

        uint256 staking = (collected * stakingOutgoing) / 100;
        uint256 burning = (collected * burnPercent) / 100;
        uint256 team = (collected * teamOutgoing) / 100;
        uint256 lottery = (collected * lotteryOutgoing) / 100;

        paytoken.transfer(teamw, team);
        paytoken.transfer(deadwallet, burning);
        paytoken.transfer(stakingc, staking);
        paytoken.transfer(lotteryw, lottery);
    }

    function clearStuckBalance(uint256 amountPercentage) external onlyOwner {
        uint256 amountBNB = address(this).balance;
        payable(msg.sender).transfer((amountBNB * amountPercentage) / 100);
    }

    function withdrawPayTokens(uint256 amountPercentage) external onlyOwner {
        uint256 amount = paytoken.balanceOf(address(this));
        paytoken.approve(address(this), amount);
        uint256 endamount = (amount * amountPercentage) / 100;
        paytoken.transfer(address(teamw), endamount);
    }
}
