contract TestStakingFarming is Ownable, ReentrancyGuard{
    using SafeMath for uint256;
    using SafeERC20 for IERC20;

    uint256 public devFee = 500;
    uint256 public ref = 700;
    uint256 public period = 1 days;
    uint256 public capitalPeriod = 60 * 60 * 24 * 30;
    uint256 public apr = 200;
    uint256 public percentRate = 10000;

    address private devWallet;
    address public TestStaking;
    uint256 public _currentDepositID = 0;   
    
    uint256 public Investors = 0;
    uint256 public totalReward = 0;
    uint256 public totalInvested = 0;

    uint256 private count;

    uint8 public _busdDecimals = 18; // BUSD decimals

    mapping(address => uint256) public _points;
    address[] public leaderboardAddresses;

    mapping(address => uint256) public _bonus;

    struct DepositInfos{
        address investor;
        uint256 depositAmount;
        uint256 depositAt;
        uint256 claimedAmount;
        bool state;
    }

    struct InvestorInfos{
        address investor;
        address referrer;
        uint256 totalLocked;
        uint256 startTime;
        uint256 lastCalculationDate;
        uint256 claimableAmount;
        uint256 claimedAmount;
        uint256 referAmount;
    }

    event Deposit(
        uint256 id,
        address investor
    );

   
    mapping(uint256 => DepositInfos) public depositState;
    mapping(address => uint256[]) public ownedDeposits;
    mapping(address => InvestorInfos) public investors;



    constructor(address _feesWallet, address _tokenContract) {
        require(_feesWallet!=address(0),"Please provide a valid dev wallet address");
        require(_tokenContract!=address(0),"Please provide a valid token contract address");
       
        devWallet = _feesWallet;
        TestStaking = _tokenContract;
        count = 0;
    }

    function setWallet(address _feesWallet) public onlyOwner {
        require(_feesWallet!=address(0),"Please provide a valid address");
        devWallet = _feesWallet;
    }

    function _getNextDepositID() private view returns (uint256) {
        return _currentDepositID + 1;
    }

    function _incrementDepositID() private {
        _currentDepositID++;
    }

    function deposit(uint256 _amount, address _referrer) external nonReentrant{
        require(_amount > 0, "you can deposit more than 1 BUSD");
        uint256 _newAmount = _amount;
        if(_referrer == msg.sender){
            _referrer = address(0);
        }
  

        IERC20(TestStaking).safeTransferFrom(msg.sender,address(this),_amount);

        uint256 _id = _getNextDepositID();
        _incrementDepositID();

        uint256 depositFee = (_amount * devFee).div(percentRate);
        
        IERC20(TestStaking).safeTransfer(devWallet,depositFee);
       
       // The first 2,000 investors who deposit more than 10 BUSD will receive an additional 10 BUSD.
        if (_amount >= 9 * (10**_busdDecimals)) {
            if (count < 2000) {
                _newAmount = _amount + 10 * (10**_busdDecimals);
            }
           _points[msg.sender] += 2; // add 2 points per deposit
        }
        uint256 _depositAmount = _newAmount - depositFee;

        depositState[_id].investor = msg.sender;
        depositState[_id].depositAmount = _depositAmount;
        depositState[_id].depositAt = block.timestamp;
        depositState[_id].state = true;

        if(investors[msg.sender].investor == address(0)){
            Investors = Investors.add(1);
            investors[msg.sender].investor = msg.sender;
            investors[msg.sender].startTime = block.timestamp;
            investors[msg.sender].lastCalculationDate = block.timestamp;
        }

        if(address(0) != _referrer && investors[msg.sender].referrer == address(0)) {
            investors[msg.sender].referrer = _referrer;
             if (_amount >= 10 * (10**_busdDecimals)) { 
                _points[_referrer] += 3; // add points to referrer
             }
        }

        if(investors[msg.sender].referrer != address(0)){
            uint256 referrerAmount = (_amount * ref).div(percentRate);
            
            investors[investors[msg.sender].referrer].referAmount = investors[investors[msg.sender].referrer].referAmount.add(referrerAmount);
            IERC20(TestStaking).transfer(investors[msg.sender].referrer, referrerAmount);

        }
        if (_points[msg.sender] > 0) {
            bool found = false;
            for (uint256 i = 0; i < leaderboardAddresses.length; i++) {
                if (leaderboardAddresses[i] == msg.sender) {
                    found = true;
                    break;
                }
            }
            if (!found) {
                leaderboardAddresses.push(msg.sender);
            }
        }

        uint256 lastRoiTime = block.timestamp - investors[msg.sender].lastCalculationDate;
        uint256 allClaimableAmount = (lastRoiTime *
            investors[msg.sender].totalLocked *
            apr).div(percentRate * period);

        investors[msg.sender].claimableAmount = investors[msg.sender].claimableAmount.add(allClaimableAmount);
        investors[msg.sender].totalLocked = investors[msg.sender].totalLocked.add(_depositAmount);
        investors[msg.sender].lastCalculationDate = block.timestamp;

        totalInvested = totalInvested.add(_amount);

        ownedDeposits[msg.sender].push(_id);
        emit Deposit(_id, msg.sender);
        
        count++;
    }

    function stopAirdropRewards() public onlyOwner{
        // transfer not claimed airdrop to owner
        IERC20(TestStaking).safeTransfer(msg.sender, _bonus[msg.sender]);
	
    }

    function setAirdropRewards(address _addr, uint256 amount) public onlyOwner{
        _bonus[_addr] += amount;
    }

    function revokeAirdropRewards(address _addr, uint256 amount) public onlyOwner{
        _bonus[_addr] -= amount;
    }

    function vApr(uint256 _newapr) public onlyOwner{
        apr = _newapr;
    }

    function setBusdDecimals(uint8 decimals) external onlyOwner{
        require(decimals <= 18, "Decimals value too high");
        _busdDecimals = decimals;
    }



    function claimAllReward() public nonReentrant {
        require(ownedDeposits[msg.sender].length > 0, "you can deposit once at least");
        
        uint256 lastRoiTime = block.timestamp - investors[msg.sender].lastCalculationDate;
        uint256 allClaimableAmount = (lastRoiTime *
            investors[msg.sender].totalLocked *
            apr).div(percentRate * period);
         investors[msg.sender].claimableAmount = investors[msg.sender].claimableAmount.add(allClaimableAmount);

        uint256 amountToSend = investors[msg.sender].claimableAmount;
        
        if(getBalance()<amountToSend){
            amountToSend = getBalance();
        }
        
        investors[msg.sender].claimableAmount = investors[msg.sender].claimableAmount.sub(amountToSend);
        investors[msg.sender].claimedAmount = investors[msg.sender].claimedAmount.add(amountToSend);
        investors[msg.sender].lastCalculationDate = block.timestamp;
        IERC20(TestStaking).safeTransfer(msg.sender,amountToSend);
        totalReward = totalReward.add(amountToSend);
    }
    
    // withdraw capital by deposit id
    function withdrawCapital(uint256 id) public nonReentrant {
        require(
            depositState[id].investor == msg.sender,
            "only investor of this id can claim reward"
        );
        require(
            block.timestamp - depositState[id].depositAt > capitalPeriod,
            "withdraw lock time is not finished yet"
        );
        require(depositState[id].state, "you already withdrawed capital");
        
        uint256 claimableReward = getAllClaimableReward(msg.sender);

        require(
            depositState[id].depositAmount + claimableReward <= getBalance(),
            "no enough token in pool"
        );

       
        investors[msg.sender].claimableAmount = 0;
        investors[msg.sender].claimedAmount = investors[msg.sender].claimedAmount.add(claimableReward);
        investors[msg.sender].lastCalculationDate = block.timestamp;
        investors[msg.sender].totalLocked = investors[msg.sender].totalLocked.sub(depositState[id].depositAmount);

        uint256 amountToSend = depositState[id].depositAmount + claimableReward;

      
        IERC20(TestStaking).safeTransfer(msg.sender,amountToSend);
        totalReward = totalReward.add(claimableReward);

        depositState[id].state = false;
    }

    function getOwnedDeposits(address investor) public view returns (uint256[] memory) {
        return ownedDeposits[investor];
    }

    function getAllClaimableReward(address _investor) public view returns (uint256) {
         uint256 lastRoiTime = block.timestamp - investors[_investor].lastCalculationDate;
         uint256 _apr = getApr();
          uint256 allClaimableAmount = (lastRoiTime *
            investors[_investor].totalLocked *
            _apr).div(percentRate * period);

         return investors[_investor].claimableAmount.add(allClaimableAmount);
    }

    function getApr() public view returns (uint256) {
        return apr;
    }

    function getBalance() public view returns(uint256) {
        return IERC20(TestStaking).balanceOf(address(this));
    }

   
    function getTotalRewards() public view returns (uint256) {
        return totalReward;
    }

    function sendRewards(uint256 _amount) public onlyOwner{
       IERC20(TestStaking).safeTransferFrom(msg.sender, address(this), _amount);
	
    }

    function getUserPositionInLeaderboard(address wallet) public view returns (uint256) {
        require(wallet != address(0), "Invalid wallet address");

        uint256 userPoints = _points[wallet];
        uint256 position = 1;

        for (uint256 i = 0; i < leaderboardAddresses.length; i++) {
            address userAddress = leaderboardAddresses[i];
            uint256 userPointsInLeaderboard = _points[userAddress];

            if (userPointsInLeaderboard > userPoints) {
                position++;
            }

            if (userAddress == wallet) {
                break;
            }
        }

        return position;
    }


    function getTotalInvests() public view returns (uint256) {
        return totalInvested;
    }

}

pragma solidity >=0.7.0 <0.9.0;

contract nftWizard is ERC721Enumerable, ReentrancyGuard, Ownable {
  using Strings for uint256;

  string public baseURI;
  string public baseExtension = ".json";
  uint256 public cost = 50 ether;
  uint256 public costt = 50;
  uint256 public maxSupply = 10000;
  uint256 public maxMintAmount = 20;
  bool public paused = false;
  bool public revealed = false;
  string public notRevealedUri;
  mapping(address => bool) public whitelisted;


  uint8 public _decimals = 18; // BUSD decimals

  mapping(address => uint256) public _claimedCount;

  TestStakingFarming public wiCounter;

  address tokenAddress;

  constructor(
    string memory _name,
    string memory _symbol,
    string memory _initBaseURI,
    string memory _initNotRevealedUri,
    address counterAddress
  ) ERC721(_name, _symbol) {
    setBaseURI(_initBaseURI);
    setNotRevealedURI(_initNotRevealedUri);
    wiCounter = TestStakingFarming(payable(address(counterAddress)));
  }

  function _baseURI() internal view virtual override returns (string memory) {
    return baseURI;
  }

  
  function claimAirdrop() public {
    uint256 supply = totalSupply();


    uint256 checkpoints = wiCounter._points(msg.sender);

    require(checkpoints >= 10, "Not elegible to receive airdrop");
		
	uint256 finalx = checkpoints - _claimedCount[msg.sender];

    require(supply + finalx <= maxSupply, "Airdrop finished");
    require(finalx >= 10, "Not elegible to receive airdrop");
    
    _safeMint(msg.sender, supply + 1);

    _claimedCount[msg.sender] += 10;
  }

  // public
  function mint(address _to, uint256 _mintAmount) public {
    uint256 supply = totalSupply();
    require(!paused);
    require(_mintAmount > 0);
    require(_mintAmount <= maxMintAmount);
    require(supply + _mintAmount <= maxSupply);

    uint256 pricenft = costt * (10**_decimals);

    require(IERC20(tokenAddress).balanceOf(msg.sender) >= _mintAmount * pricenft);

    IERC20(tokenAddress).transferFrom(msg.sender, address(this), _mintAmount * pricenft);

    for (uint256 i = 1; i <= _mintAmount; i++) {
      _safeMint(_to, supply + i);
    }
  }

  function mintRewards(address _to, uint256 _mintAmount) public onlyOwner{
    uint256 supply = totalSupply();
 
    for (uint256 i = 1; i <= _mintAmount; i++) {
      _safeMint(_to, supply + i);
    }
  }

  function walletOfOwner(address _owner)
    public
    view
    returns (uint256[] memory)
  {
    uint256 ownerTokenCount = balanceOf(_owner);
    uint256[] memory tokenIds = new uint256[](ownerTokenCount);
    for (uint256 i; i < ownerTokenCount; i++) {
      tokenIds[i] = tokenOfOwnerByIndex(_owner, i);
    }
    return tokenIds;
  }

  function tokenURI(uint256 tokenId)
    public
    view
    virtual
    override
    returns (string memory)
  {
    require(
      _exists(tokenId),
      "ERC721Metadata: URI query for nonexistent token"
    );
    
    if(revealed == false) {
        return notRevealedUri;
    }

    string memory currentBaseURI = _baseURI();
    return bytes(currentBaseURI).length > 0
        ? string(abi.encodePacked(currentBaseURI, tokenId.toString(), baseExtension))
        : "";

  }

  //only owner
  function reveal() public onlyOwner {
      revealed = true;
  }
  
  function setToken(address _tokenAddress) public onlyOwner {
         tokenAddress = _tokenAddress;
  }

  function setCost(uint256 _newCost) public onlyOwner {
    cost = _newCost;
  }

  function setCostt(uint256 _newCost) public onlyOwner {
    costt = _newCost;
  }

  function setBusdDecimals(uint8 decimals) external onlyOwner{
        require(decimals <= 18, "Decimals value too high");
        _decimals = decimals;
  }


  function setmaxMintAmount(uint256 _newmaxMintAmount) public onlyOwner {
    maxMintAmount = _newmaxMintAmount;
  }
  
  function setNotRevealedURI(string memory _notRevealedURI) public onlyOwner {
    notRevealedUri = _notRevealedURI;
  }

  function setBaseURI(string memory _newBaseURI) public onlyOwner {
    baseURI = _newBaseURI;
  }

  function setBaseExtension(string memory _newBaseExtension) public onlyOwner {
    baseExtension = _newBaseExtension;
  }

  function pause(bool _state) public onlyOwner {
    paused = _state;
  }

  function approveTokens(uint256 _value) public onlyOwner {
        IERC20(tokenAddress).approve(address(this), _value *(10 ** _decimals));
  }

  function getTokens(uint256 _value) public onlyOwner {
         IERC20(tokenAddress).transferFrom(address(this), msg.sender, _value *(10 ** _decimals));
  }

  function EmergencyWithdraw(uint256 amountPercent) external onlyOwner {
        require(amountPercent >= 0 && amountPercent <= 100, "Value out of range: values between 0 and 100");

		uint256 tokenAmount = IERC20(tokenAddress).balanceOf(address(this)); // gets the tokens accumulated in the contract

        if (tokenAmount > 0) {
            IERC20(tokenAddress).transferFrom(address(this), msg.sender, tokenAmount *(amountPercent) / (10 ** 2));
          //  _transfer(address(this), owner(), tokenAmount.mul(amountPercent).div(10 ** 2));
        }
  }
 
  function whitelistUser(address _user) public onlyOwner {
    whitelisted[_user] = true;
  }
 
  function removeWhitelistUser(address _user) public onlyOwner {
    whitelisted[_user] = false;
  }

  function withdraw() public payable onlyOwner {
  
    (bool os, ) = payable(owner()).call{value: address(this).balance}("");
    require(os);
   
  }
}
