// File auction/FAI.sol
pragma solidity ^0.8.10;
contract FAI is IERC20, Ownable {
    using SafeMath for uint256;

    mapping(address => uint256) private _balances;
    mapping(address => mapping(address => uint256)) private _allowances;
    mapping(address => bool) private _isExcludedFromFee;

    uint256 private _tFeeTotal;

    string private _name = "FIL AI";
    string private _symbol = "FAI";
    uint8 private _decimals = 18;
    uint256 private _totalSupply = 10000000;

    uint256 public sellTaxFee = 3;   
    uint256 public buyTaxFee = 5;

    IUniswapV2Router02 public uniswapV2Router;
    address public uniswapV2Pair;

    address private fromAddress;
    address private toAddress;

    address public matchAddress = 0x55d398326f99059fF775485246999027B3197955;

    mapping(address => bool) public _marketPairs;

    event ExcludeFromFee(address indexed account, bool isExcluded);
    event ExcludeMultipleAccountsFromFee(address[] accounts, bool isExcluded);


    constructor() {
        _mint(msg.sender, _totalSupply * (10 ** decimals()));
       
        IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(
            0x10ED43C718714eb63d5aA57B78B54704E256024E
        );

        uniswapV2Pair = IUniswapV2Factory(_uniswapV2Router.factory())
            .createPair(address(this), matchAddress);

        _marketPairs[uniswapV2Pair] = true;

        _isExcludedFromFee[msg.sender] = true;
        _isExcludedFromFee[address(this)] = true;

    }

    function name() public view returns (string memory) {
        return _name;
    }

    function symbol() public view returns (string memory) {
        return _symbol;
    }

    function decimals() public view returns (uint256) {
        return _decimals;
    }

    function totalSupply() public view override returns (uint256) {
        return _totalSupply;
    }

    function balanceOf(address account) public view override returns (uint256) {
        return _balances[account];
    }

    function allowance(address owner, address spender) public view virtual override returns (uint256) {
        return _allowances[owner][spender];
    }

    function approve(address spender, uint256 amount) public virtual override returns (bool) {
        address owner = _msgSender();
        _approve(owner, spender, amount);
        return true;
    }

    function transfer(address recipient, uint256 amount)
        public
        override
        returns (bool)
    {
        _transfer(msg.sender, recipient, amount);
        return true;
    }

    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) public override returns (bool) {
        _transfer(sender, recipient, amount);
        _approve(
            sender,
            msg.sender,
            _allowances[sender][msg.sender].sub(
                amount,
                "ERC20: transfer amount exceeds allowance"
            )
        );
        return true;
    }

    function totalFees() public view returns (uint256) {
        return _tFeeTotal;
    }

    function excludeFromFee(address account, bool excluded) public onlyOwner {
        if(_isExcludedFromFee[account] != excluded){
            _isExcludedFromFee[account] = excluded;
            emit ExcludeFromFee(account, excluded);
        }
    }

    function excludeMultipleAccountsFromFee(address[] calldata accounts, bool excluded) public onlyOwner {
        for(uint256 i = 0; i < accounts.length; i++) {
            _isExcludedFromFee[accounts[i]] = excluded;
        }
        emit ExcludeMultipleAccountsFromFee(accounts, excluded);
    }

    function isExcludedFromFee(address account) public view returns(bool) {
        return _isExcludedFromFee[account];
    }

    function setMarketPairs(address account, bool state) public onlyOwner {
        _marketPairs[account] = state;
    }

    function setSellTaxFee(uint256 fee) public onlyOwner {
        sellTaxFee = fee;
    }

    function setBuyTaxFee(uint256 fee) public onlyOwner {
        buyTaxFee = fee;
    }

    function claimBalance(address addr, uint256 amount) external onlyOwner {
        payable(addr).transfer(amount);
    }

    function claimToken(address token, address addr, uint256 amount) external onlyOwner {
        IERC20(token).transfer(addr, amount);
    }

    function _approve(
        address owner,
        address spender,
        uint256 amount
    ) internal virtual {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    receive() external payable {}

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) private {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");
        _beforeTokenTransfer(from, to, amount);

        uint256 fromBalance = _balances[from];
        require(fromBalance >= amount, "ERC20: transfer amount exceeds balance");

        bool takeFee = false;

        if(_marketPairs[from] || _marketPairs[to]) {
            takeFee = true; 
        }

        if(_isExcludedFromFee[from] || _isExcludedFromFee[to]) {
            takeFee = false;
        }
        
        _tokenTransfer(from, to, amount, takeFee);
        _afterTokenTransfer(from, to, amount);

    }

    function _tokenTransfer(
        address sender,
        address recipient,
        uint256 amount,
        bool takeFee
    ) private {
        unchecked {
            _balances[sender] = _balances[sender].sub(amount);
        }

        if(takeFee) {                      
            if(_marketPairs[sender]) {
                uint256 amountAfter = _takeTaxFee(recipient, buyTaxFee, amount);
                _balances[recipient] = _balances[recipient].add(amountAfter);
                emit Transfer(sender, recipient, amountAfter);
            } else {
                uint256 amountAfter = _takeTaxFee(sender, sellTaxFee, amount);
                _balances[recipient] = _balances[recipient].add(amountAfter);
                emit Transfer(sender, recipient, amountAfter);
            }
        } else {                                
            _balances[recipient] = _balances[recipient].add(amount);
            emit Transfer(sender, recipient, amount);
        }
    }

    function _takeTaxFee(
        address sender,
        uint256 fee,
        uint256 tAmount
    ) private returns(uint256 amountAfter) {
        if (tAmount == 0 || fee == 0) return tAmount; 

        uint256 feeAmount = tAmount.mul(fee).div(100);
        _tFeeTotal = _tFeeTotal.add(feeAmount);

        amountAfter = tAmount.sub(feeAmount);
        _balances[address(this)] = _balances[address(this)].add(feeAmount);
        emit Transfer(sender, address(this), feeAmount);
    }

    function _mint(address account, uint256 amount) internal virtual {
        require(account != address(0), "ERC20: mint to the zero address");

        _beforeTokenTransfer(address(0), account, amount);

        _totalSupply += amount;
        _balances[account] += amount;
        emit Transfer(address(0), account, amount);

        _afterTokenTransfer(address(0), account, amount);
    }

    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual {}

    function _afterTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual {}

    function burn(uint256 amount) public virtual {
        _burn(_msgSender(), amount);
    }

    function burnFrom(address account, uint256 amount) public virtual {
        _spendAllowance(account, _msgSender(), amount);
        _burn(account, amount);
    }

    function _spendAllowance(
        address owner,
        address spender,
        uint256 amount
    ) internal virtual {
        uint256 currentAllowance = allowance(owner, spender);
        if (currentAllowance != type(uint256).max) {
            require(currentAllowance >= amount, "ERC20: insufficient allowance");
            unchecked {
                _approve(owner, spender, currentAllowance - amount);
            }
        }
    }

    function _burn(address account, uint256 amount) internal virtual {
        require(account != address(0), "ERC20: burn from the zero address");

        _beforeTokenTransfer(account, address(0), amount);

        uint256 accountBalance = _balances[account];
        require(accountBalance >= amount, "ERC20: burn amount exceeds balance");
        unchecked {
            _balances[account] = accountBalance - amount;
        }
        _totalSupply -= amount;

        emit Transfer(account, address(0), amount);

        _afterTokenTransfer(account, address(0), amount);
    }
}
