    "contracts/SmartPEPE.sol": {
      "content": "/*────────────────────────────────────┐
│ - Smart Buybacks:                   │
│  * Start monitoring if buyback pool │
│    is 5+% of the liquidity pool.    │
│  * Start buybacks if price +/- 25%  │
│-------------------------------------│
│ - Initial Tax Rates:                │
│  * Buy        : 3%                  │
│  * Sell       : 3%                  │
│  * Transfer   : 0%                  │
│-------------------------------------│
│ - Tax Distribution:                 │
│  * Marketing  : 2/3                 │
│  * Buybacks   : 1/3                 │
│-------------------------------------│
│ - Max Tax Limits:                   │
│  * Buy+Sell   : 10%                 │
│  * Transfer   : 6%                  │
└─────────────────────────────────────┘
SPDX-License-Identifier: MIT */

pragma solidity 0.8.19;

import "@uniswap/v2-core/contracts/interfaces/IUniswapV2Factory.sol";
import "@uniswap/v2-core/contracts/interfaces/IUniswapV2Pair.sol";
import "@uniswap/v2-periphery/contracts/interfaces/IUniswapV2Router02.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract SmartPEPE is ERC20, Ownable {
    /* Structs */
    struct Tax {
        uint256 buy;
        uint256 sell;
        uint256 transfer;
    }

    /* Mappings */
    mapping(address => bool) private _isExcludedFromFees;

    /* Public Variables */
    IUniswapV2Router02 public uniswapV2Router;
    IUniswapV2Pair public uniswapV2Pair;
    Tax public tax;
    bool public tradingEnabled;
    address public marketingWallet;
    uint256 public launchTime;
    uint256 public lastBuybackTime;
    uint256 public buybackAmount;
    uint256 public buybackIterator;
    uint256 public monitoringPrice;
    bool public monitoringStarted;
    bool public buybacksStarted;

    /* Private Variables */
    bool private _inSwap;

    /* Events */
    event ExcludeFromFees(address account, bool excluded);
    event UpdateTax(Tax tax);
    event SetPair(address account, bool value);
    event SwapFeesAndDistribute(
        uint256 tokensSwapped,
        uint256 ethForBuybacks,
        uint256 ethForMarketing
    );
    event TradingEnabled();
    event UpdateMarketingWallet(address marketingWallet);
    event MonitoringStarted(uint256 ethPool, uint256 monitoringPrice);
    event PushBuybacksStarted();
    event ReversalBuybacksStarted();
    event Buyback(uint256 amount);

    /* Modifiers */
    modifier lockSwap() {
        _inSwap = true;
        _;
        _inSwap = false;
    }

    /* Constructor */
    constructor() ERC20("SmartPEPE", "SmartPEPE") Ownable(msg.sender) {
        marketingWallet = 0x0a81B9a978dDB300FEd18F1e1fDaaC3E3a5BfCcF;
        address routerAddress = 0x10ED43C718714eb63d5aA57B78B54704E256024E; // BSC Pancake Mainnet Router

        uniswapV2Router = IUniswapV2Router02(routerAddress);
        uniswapV2Pair = IUniswapV2Pair(
            IUniswapV2Factory(uniswapV2Router.factory()).createPair(
                address(this),
                uniswapV2Router.WETH()
            )
        );

        _isExcludedFromFees[owner()] = true;
        _isExcludedFromFees[marketingWallet] = true;
        _isExcludedFromFees[address(0xdead)] = true;
        _isExcludedFromFees[address(this)] = true;

        tax.buy = 3;
        tax.sell = 3;
        tax.transfer = 0;

        _mint(owner(), 100_000_000 * (10 ** decimals()));
    }

    /* Callbacks */
    receive() external payable {}

    /* Public Functions */
    function circulationSupply() public view virtual returns (uint256) {
        return totalSupply() - balanceOf(address(0xdead));
    }

    function isExcludedFromFees(address account) public view returns (bool) {
        return _isExcludedFromFees[account];
    }

    function requiredPoolForBuybacks() public view returns (uint256) {
        (uint256 Res0, uint256 Res1, ) = uniswapV2Pair.getReserves();
        return
            ((uniswapV2Pair.token1() == address(this) ? Res0 : Res1) * 5) / 100;
    }

    function currentPrice() public view returns (uint256) {
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = uniswapV2Router.WETH();

        return uniswapV2Router.getAmountsOut(10 ** decimals(), path)[1];
    }

    /* Owner Functions */
    function excludeFromFees(address account, bool excluded) public onlyOwner {
        require(
            isExcludedFromFees(account) != excluded,
            "Account is already the value of 'excluded'"
        );

        _isExcludedFromFees[account] = excluded;

        emit ExcludeFromFees(account, excluded);
    }

    function excludeFromFees(address[] memory accounts, bool excluded) public onlyOwner {
        for (uint i = 0; i < accounts.length; i++) {
            address account = accounts[i];
            _isExcludedFromFees[account] = excluded;
        }
    }

    function updateTax(
        uint256 buy,
        uint256 sell,
        uint256 transfer
    ) public onlyOwner {
        require(
            buy + sell <= 10,
            "Total buy/sell tax should be lower than 10%"
        );
        require(transfer <= 6, "Transfer tax should be lower than 6%");

        tax.buy = buy;
        tax.sell = sell;
        tax.transfer = transfer;

        emit UpdateTax(tax);
    }

    function enableTrading() public onlyOwner {
        require(!tradingEnabled, "Trading already enabled");

        tradingEnabled = true;
        launchTime = block.timestamp;

        emit TradingEnabled();
    }

    function updateMarketingWallet(
        address _marketingWallet
    ) external onlyOwner {
        require(
            _marketingWallet != marketingWallet,
            "Marketing wallet is already that address"
        );
        require(
            _marketingWallet != address(0),
            "Marketing wallet cannot be the zero address"
        );

        marketingWallet = _marketingWallet;

        emit UpdateMarketingWallet(marketingWallet);
    }

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal override {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");
        require(
            tradingEnabled ||
                isExcludedFromFees(from) ||
                isExcludedFromFees(to),
            "Trading not yet enabled"
        );

        if (amount == 0) {
            super._transfer(from, to, amount);
            return;
        }

        if (tradingEnabled) {
            uint256 buybackPool = requiredPoolForBuybacks();
            if (!monitoringStarted && address(this).balance >= buybackPool) {
                monitoringPrice = currentPrice();
                buybackAmount = buybackPool / 6;
                monitoringStarted = true;
                emit MonitoringStarted(buybackPool, monitoringPrice);
            }

            if (monitoringStarted) {
                if (!buybacksStarted) {
                    if (currentPrice() <= (monitoringPrice * 75) / 100) {
                        buybacksStarted = true;
                        emit ReversalBuybacksStarted();
                    } else if (
                        currentPrice() >= (monitoringPrice * 125) / 100
                    ) {
                        buybacksStarted = true;
                        emit PushBuybacksStarted();
                    }
                }
            }
        }

        if (!_inSwap && address(uniswapV2Pair) == to && tradingEnabled) {
            if (balanceOf(address(this)) >= circulationSupply() / 5_000) {
                _swapFees();
            }

            if (
                buybacksStarted &&
                block.timestamp - 10 minutes > lastBuybackTime &&
                address(this).balance >= buybackAmount
            ) {
                _buyback();
            }
        }

        if (!(isExcludedFromFees(from) || isExcludedFromFees(to) || _inSwap)) {
            uint256 fee = 0;

            if (address(uniswapV2Pair) == from) {
                fee = (amount * tax.buy) / 100;
            } else if (address(uniswapV2Pair) == to) {
                fee = (amount * tax.sell) / 100;
            } else {
                fee = (amount * tax.transfer) / 100;
            }

            if (fee > 0) {
                amount -= fee;
                super._transfer(from, address(this), fee);
            }
        }

        super._transfer(from, to, amount);
    }

    /* Private Functions */
    function _swapFees() private lockSwap {
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = uniswapV2Router.WETH();

        uint256 initialBalance = address(this).balance;
        uint256 tokensToSwap = balanceOf(address(this));

        _approve(address(this), address(uniswapV2Router), tokensToSwap);

        uniswapV2Router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            tokensToSwap,
            0,
            path,
            address(this),
            block.timestamp
        );

        uint256 ethRecieved = address(this).balance - initialBalance;
        uint256 ethForMarketing = ethRecieved / 3 * 2;
        uint256 ethForBuybacks = ethRecieved / 3;

        payable(marketingWallet).call{value: ethForMarketing}("");

        emit SwapFeesAndDistribute(
            tokensToSwap,
            ethForBuybacks,
            ethForMarketing
        );
    }

    function _buyback() private lockSwap {
        address[] memory path = new address[](2);
        path[0] = uniswapV2Router.WETH();
        path[1] = address(this);

        _approve(address(this), address(uniswapV2Router), buybackAmount);

        uniswapV2Router.swapExactETHForTokensSupportingFeeOnTransferTokens{
            value: buybackAmount
        }(0, path, address(0xdead), block.timestamp);

        lastBuybackTime = block.timestamp;
        buybackIterator++;

        if (buybackIterator == 6) {
            monitoringPrice = 0;
            buybackIterator = 0;
            buybacksStarted = false;
            monitoringStarted = false;
        }

        emit Buyback(buybackAmount);
    }
}
