    if (sender == to) { revert SameRecipient(); }
  }
  function _transferTo(address currency, address sender, address to, uint256 amount) internal {
    _checkSenderTo(sender, to);
    if (currency == NATIVE_ADDRESS) {
      _sendValue(payable(to), amount);
    } else {
      IERC20(currency).transfer(to, amount);
    }
  }
  function _transferFrom(address currency, address sender, address to, uint256 amount) internal {
    _checkSenderTo(sender, to);
    if (currency == NATIVE_ADDRESS) {
