    } else {
      IERC20(currency).transferFrom(sender, to, amount);
    }
  }
  function _sendValue(address payable recipient, uint256 amount) private {
    if (address(this).balance < amount) { revert InsufficientBalance(); }
    (bool success, ) = recipient.call{ value: amount }("");
    if (!success) { revert SendValueFailed(); }
  }
  function _getBaseAmount(address token, uint8 deductDecimals) internal view returns(uint256) {
    return 10 ** (IERC20Metadata(token).decimals() - deductDecimals);
  }
