contract PrepaidSubscription is AddressGuard, Guard, TokenHelper, ERC1155URIStorage, ERC1155Holder, Multicall {
  /**
    @param duration     Duration of the plan
    @param price        Price of the plan
    @param currency     Payment token to accept
    @param trust        Trust address as payment receipient
    @param totalSubs    Total subscribers
    @param createdAt    Timestamp when plan was created
    @param category     Category of the plan
    @param uri          Storage uri of the plan
    @param finderPerc   Finder percentage by 100_00 bps
   */
  struct Plan {
    bool free;
    uint256 duration;
    uint256 price;
    address currency;
    address trust;
    uint256 totalSubs;
    uint256 createdAt;
    string category;
    string uri;
    uint256 finderPerc;
  }
  /**
    @param quantity   Amount to mint
    @param duration   Duration of the plan
    @param price      Price of the plan
    @param currency   Payment token to accept
    @param trust      Trust address as payment receipient
    @param category   Category of the plan
    @param uri        Storage uri of the plan
    @param finderPerc   Finder percentage by 100_00 bps    
   */
  struct PlanParams {
    uint256 quantity;
    uint256 duration;
    uint256 price;
    uint256 finderPerc;
    address currency;
    address trust;
    string category;
    string uri;
  }
  uint256 public totalSupply;
  string public name;
  string public symbol;
  address public owner;
  mapping(string => bool) public forUris;
  mapping(string => bool) public forPlans;
  mapping(uint256 => Plan) private plans;
  mapping(string => address) private uris;
  mapping(address => mapping(string => uint256)) internal uriExpirations;
  mapping(address => mapping(string => uint256)) internal expirations;
  event PlanUpdated(uint256 indexed tokenId);
  event Subscribed(uint256 indexed tokenId, address indexed subscriber, uint256 indexed expiration);
  event SubscribedUri(address indexed subscriber, string indexed uri, uint256 indexed expiration);
  constructor(string memory name_, string memory symbol_, string memory uri_) ERC1155(uri_) {
    name = name_;
    symbol = symbol_;
    totalSupply = 0;
    owner = _msgSender();
    _grantRole(DEFAULT_ADMIN_ROLE, owner);
    _grantRole(OPERATOR_ROLE, owner);
    _grantRole(UPGRADER_ROLE, owner);
  }
  /// @notice Returns the current timestamp
  function currentTimestamp() external view returns(uint256) {
    return block.timestamp;
  }
  /// @notice Returns the plan by token id  
  function getPlan(uint256 tokenId) external view returns(Plan memory) {
    return plans[tokenId];
  }
  /// @notice Override the plan details by token id
  function setPlan(uint256 tokenId, PlanParams memory params) external {
    _onlyOperator();
    plans[tokenId] = Plan({
      free: false,
      duration: params.duration,
      price: params.price,
      currency: params.currency,
      totalSubs: plans[tokenId].totalSubs,
      createdAt: plans[tokenId].createdAt,
      trust: plans[tokenId].trust,
      category: params.category,
      uri: params.uri,
      finderPerc: params.finderPerc
    });
    emit PlanUpdated(tokenId);
  }

  function setFreePlan(uint256 tokenId, uint256 duration) external {
    _onlyOperator();
    if (!plans[tokenId].free) {
      revert NotFreePlan();
    }
    plans[tokenId].duration = duration;
    emit PlanUpdated(tokenId);
  }
  /// @notice Updates the trust recipieint
  /// @param tokenId Token id of the plan
  /// @param trust Address of the payment recipient
  function setTrust(uint256 tokenId, address trust) external {
    _onlyAdmin();
    _requireAddress(trust);
    plans[tokenId].trust = trust;
  }
  /// @notice Updates the uri of the plan
  /// @param tokenId Token id of the plan
  /// @param uri IPFS/Arweave format uri
  function setPlanUri(uint256 tokenId, string memory uri) external {
    _onlyOperator();
    plans[tokenId].uri = uri;
    _setURI(tokenId, uri);
  }
  /// @notice Sets category status for non-uri subscription types  
  function setForPlans(string memory category, bool status) external {
    _onlyOperator();
    _setForPlans(category, status);
  }
  /// @notice Sets category status for uri subscription types
  function setForUris(string memory category, bool status) external {
    _onlyOperator();
    _setForUris(category, status);
  }
  /// @notice Mints limited edition subscription plan
  /// @param params Subscription plan details
  function mint(PlanParams memory params) external nonReentrant returns(uint256) {
    _onlyOperator();
    uint256 tokenId = _getNextId();
    _requireAddress(params.currency); 
    _requireAddress(params.trust);
    if (!(forUris[params.category] || forPlans[params.category])) {
      revert CategoryNotAllowed();
    }
    _mint(address(this), tokenId, params.quantity, "");
    _setURI(tokenId, params.uri);
    plans[tokenId] = Plan({
      free: false,
      duration: params.duration,
      price: params.price,
      currency: params.currency,
      trust: params.trust,
      totalSubs: 0,
      createdAt: block.timestamp,
      category: params.category,
      uri: params.uri,
      finderPerc: params.finderPerc
    });
    
    emit PlanUpdated(tokenId);
    return tokenId;
  }
  function mintFree(string memory category, string memory uri, uint256 quantity, uint256 duration) external nonReentrant returns(uint256) {
    _onlyOperator();
    uint256 tokenId = _getNextId();
    _mint(address(this), tokenId, quantity, "");
    _setURI(tokenId, uri);       
    plans[tokenId].free = true;
    plans[tokenId].category = category;
    plans[tokenId].createdAt = block.timestamp;
    plans[tokenId].uri = uri;
    plans[tokenId].duration = duration;
    return tokenId;
  }
  /// Returns the timestamp of total days, 1825 days == 365 days * 5
  function calcDuration(uint256 totalDays) external pure returns(uint256) {
    return totalDays * 1 days;
  }
  /// @notice Returns the total amount of token to pay
  function quote(uint256 tokenId, uint256 quantity) external view returns(uint256) {
    return plans[tokenId].price * quantity;
  }
  /// @notice Returns the expiration timestamp
  function expiresAtFor(address subscriber, string memory category) external view returns(uint256) {
    return expirations[subscriber][category];
  }
  /// @notice Returns the expiration timestamp
  function expiresAtForUri(address subscriber, string memory uri) external view returns(uint256) {
    return uriExpirations[subscriber][uri];
  }
  function quoteSubscription(address subscriber, uint256 tokenId, uint256 quantity) external view returns(uint256) {
    return _computeExpiration(subscriber, tokenId, quantity);    
  }
  function quoteSubscriptionUri(address subscriber, uint256 tokenId, string memory uri, uint256 quantity) external view returns(uint256) {
    return _computeUriExpiration(subscriber, tokenId, uri, quantity);
  }
  /// @notice Returns who owns the uri, when it expires, and remaining
  function uriFor(string memory uri) external view returns(address, uint256, uint256, uint256) {
    address subscriber = uris[uri];
    uint256 expiresIn = _expiresInUri(subscriber, uri);
    return (subscriber, uriExpirations[subscriber][uri], expiresIn, expiresIn / 1 days);
  }
  /// @notice Returns the remaining time for the subscription of the uri
  function expiresInUri(address subscriber, string memory uri) external view returns(uint256) {
    return _expiresInUri(subscriber, uri);
  }
  /// @notice Buy the subscription prior to usage
  /// @dev Requires payment token approval and contract balance
  /// @param tokenId The nft token id to purchase
  /// @param quantity How many subscription plan to purchase
  function buy(uint256 tokenId, uint256 quantity, address finder) external nonReentrant {
    address subscriber = _msgSender();
    address self = address(this);
    Plan memory _plan = plans[tokenId];
    uint256 totalPrice = _plan.price * quantity;
    if (!_verifyAllowance(subscriber, self, _plan.currency, totalPrice)) {
      revert InsufficientAllowance();
    }
    if (balanceOf(self, tokenId) < quantity) {
      revert InsufficientNFTBalance();
    }
    if (_plan.finderPerc > 0 && finder != address(0)) {
      uint256 finderFee = (totalPrice * _plan.finderPerc) / 100_00;
      _transferFrom(_plan.currency, subscriber, finder, finderFee);
      _transferFrom(_plan.currency, subscriber, _plan.trust, totalPrice - finderFee);
    } else {
      _transferFrom(_plan.currency, subscriber, _plan.trust, totalPrice);
    }
    _safeTransferFrom(self, subscriber, tokenId, quantity, "");
  }

  function claim(uint256 tokenId, address subscriber) external nonReentrant {
    Plan memory _plan = plans[tokenId];
    if (!_plan.free) {
      revert NotFreePlan();
    }
    if (balanceOf(subscriber, tokenId) > 0) {
      revert MaxClaimableExceeded();
    }
    _safeTransferFrom(address(this), subscriber, tokenId, 1, "");
  }
  /// @notice Subscribe to subscription plans, burn to subscribe
  /// @dev Requires non-uri subscription type
  /// @param tokenId Token id of the plan
  /// @param quantity Quantity to burn
  function subscribe(uint256 tokenId, uint256 quantity) external nonReentrant {
    address subscriber = _msgSender();
    _verifyQuantity(quantity);
    _onlyForPlans(tokenId);
    _subscribe(subscriber, tokenId, quantity);
    _burn(subscriber, tokenId, quantity);
  }
  function _onlyUriOwner(string memory uri, address subscriber) internal view {
    address uriOwner = uris[uri];
    if (!(uriOwner == address(0) || uriOwner == subscriber)) {
      revert NotUriOwner();
    }
  }
  /// @notice Subscribe to uri type subscription plans, burn to subscribe, default to 1
  /// @dev Requires uri subscription type
  /// @param tokenId Token id of the plan
  /// @param uri Uri handle to subscribe for
  /// @param subscribeFor If zero, sets sender as subscriber
  function subscribeUri(uint256 tokenId, string memory uri, uint256 quantity, address subscribeFor) external nonReentrant {
    address subscriber = _msgSender();
    subscribeFor = subscribeFor == address(0) ? subscriber : subscribeFor;
    _verifyQuantity(quantity);
    _onlyForUri(tokenId);
    _onlyUriOwner(uri, subscribeFor);
    _subscribeUri(subscribeFor, tokenId, uri, quantity);
    _burn(subscriber, tokenId, quantity);
  }
  /// @notice Clear the uri used by the subscriber if expired
  /// @dev Requires an upgrader contract
  /// @param subscriber Address of subscriber
  /// @param uri uri handle used
  function clearUri(address subscriber, string memory uri) external {
    _onlyUpgrader();
    if (_expiresInUri(subscriber, uri) == 0 && uris[uri] == subscriber) {
      delete uriExpirations[subscriber][uri];
      delete uris[uri];
    }
  }
  /*** INTERNAL SUBSCRIBE METHODS */
  function _setForPlans(string memory category, bool status) internal {
    forPlans[category] = status;
  }  
  function _setForUris(string memory category, bool status) internal {
    forUris[category] = status;
  }  
  function _getNextId() internal returns(uint256 nextId) {
    nextId = totalSupply + 1;
    totalSupply++;
  }
  function _verifyQuantity(uint256 quantity) internal pure {
    if (quantity == 0) {
      revert InvalidQty();
    }
  }
  function _onlyAvailableUri(string memory uri) internal view {
    if (uris[uri] != address(0)) {
      revert UriAlreadyTaken();
    }
  }
  function _onlyForPlans(uint256 tokenId) internal view {
    if (!forPlans[plans[tokenId].category]) {
      revert SubscriptionForNonUriOnly();
    }
  }
  function _onlyForUri(uint256 tokenId) internal view {
    if (!forUris[plans[tokenId].category]) {
      revert SubscriptionForUriOnly();
    }
  }
  function _subscribe(address subscriber, uint256 tokenId, uint256 quantity) internal {
    uint256 expiration = _computeExpiration(subscriber, tokenId, quantity);
    expirations[subscriber][plans[tokenId].category] = expiration;
    plans[tokenId].totalSubs += quantity;
    emit Subscribed(tokenId, subscriber, expiration);
  }
  function _subscribeUri(address subscriber, uint256 tokenId, string memory uri, uint256 quantity) internal {
    uint256 expiration = _computeUriExpiration(subscriber, tokenId, uri, quantity);
    uriExpirations[subscriber][uri] = expiration;
    uris[uri] = subscriber;
    plans[tokenId].totalSubs += quantity;
    emit SubscribedUri(subscriber, uri, expiration);
  }
  function _computeExpiration(address subscriber, uint256 tokenId, uint256 quantity) internal view returns(uint256) {
    Plan memory _plan = plans[tokenId];
    uint256 totalDuration =_plan.duration * quantity;
    return expirations[subscriber][_plan.category] == 0
      ? block.timestamp + totalDuration
      : expirations[subscriber][_plan.category] + totalDuration;
  }
  function _computeUriExpiration(address subscriber, uint256 tokenId, string memory uri, uint256 quantity) internal view returns(uint256) {
    uint256 duration =plans[tokenId].duration * quantity;
    uint256 expiration = uriExpirations[subscriber][uri];
    return expiration == 0
      ? block.timestamp + duration
      : expiration + duration;
  }
  function _expiresInUri(address subscriber, string memory uri) internal view returns(uint256) {
    uint256 expires = uriExpirations[subscriber][uri];
    if (expires > 0 && expires >= block.timestamp) {
      return expires - block.timestamp;
    }
    return 0;
  }
  function supportsInterface(bytes4 interfaceId) public view override(ERC1155, ERC1155Receiver, AccessControlEnumerable) returns(bool) {
    return super.supportsInterface(interfaceId);
  }
}

