// File contracts/extensions/SafeWithdraw.sol
// Original license: SPDX_License_Identifier: MIT
pragma solidity ^0.8.19;

abstract contract SafeWithdraw is AddressGuard, ReceiveFallback {
  error ZeroBalance();
  function _onlySafeAdmin() internal virtual view {}
  function withdrawToken(IERC20 token, address account) external {
    _onlySafeAdmin();
    _requireAddress(account);
    token.transfer(account, token.balanceOf(address(this)));
  }
  function withdraw(address payable account) external {
    _onlySafeAdmin();
    _requireAddress(account);
    (bool success,) = account.call{ value: address(this).balance }("");
    if (!success) { 
      revert ZeroBalance();
    }
  }
  
}

