    "/contracts/current/LimitedOwnership.sol": {
      "content": "// SPDX-License-Identifier: MIT\r\npragma solidity ^0.8.9;\r\n\r\n/// @title This contract only can manage tax and receiver (partial ownership renounce)\r\ncontract LimitedOwnership {\r\n    address _miner;\r\n    address _owner;\r\n    bytes4 private constant SET_MK_TAX = bytes4(keccak256(\"setMarketingTax(uint8,address)\"));\r\n\r\n    constructor(address _minerAdr) { \r\n        _miner = _minerAdr; \r\n        _owner = msg.sender;\r\n    }\r\n    \r\n    modifier onlyOwner {\r\n        require(msg.sender == _owner, \"Only owner\");\r\n        _;\r\n    }\r\n\r\n    /// @notice For marketers\r\n    function setMarketingReceiver(address _receiver) external onlyOwner {\r\n        (bool success, bytes memory failureReason) = _miner.call(abi.encodeWithSelector(SET_MK_TAX, 4, _receiver));\r\n        require(success, string(abi.encodePacked(failureReason)));\r\n    }\r\n}"
    }
