contract SAKAI is ERC20, Ownable {
    using SafeERC20 for IERC20;

    uint256 public swapTax;
    uint256 public denominator;

    address public treasuryWallet = 0x6b269c07e8f94f0fa1769CBD362879AFeA0206dB;
    address public stakingContract = 0x7BC537e26478789d3709cD68972C6A6DC6611Cde;
    address public gasWallet = 0x9Ffd803BD8048Ed59Fccb73d688326d7085A4e54;

    IUniswapV2Router02 public uniswapV2Router;
    address public uniswapV2Pair;

    bool private swapping;
    uint256 public swapTokensAtAmount;
    bool public isSwapBackEnabled;

    mapping(address => bool) private _isExcludedFromFees;
    mapping(address => bool) public _isAutomatedMarketMakerPairs;

    modifier inSwap() {
        swapping = true;
        _;
        swapping = false;
    }

    event UpdateTreasuryWallet(address indexed treasuryWallet);
    event UpdateStakingContract(address indexed stakingContract);
    event UpdateGasWallet(address indexed gasWallet);
    event UpdateSwapTokensAtAmount(uint256 swapTokensAtAmount);
    event UpdateSwapBackStatus(bool status);
    event UpdateExcludeFromFees(address indexed account, bool isExcluded);
    event UpdateAutomatedMarketMakerPair(address indexed pair, bool status);

    constructor() ERC20("Sakai Vault", "SAKAI") {
        _mint(owner(), 8_000_000 * 10**18);

        swapTax = 300;
        denominator = 10_000;

        address router = getRouterAddress();

        IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(router);
        address _uniswapV2Pair = IUniswapV2Factory(_uniswapV2Router.factory())
            .createPair(address(this), getUSDTAddress());
        uniswapV2Router = _uniswapV2Router;
        uniswapV2Pair = _uniswapV2Pair;

        swapTokensAtAmount = totalSupply() / 5_000;
        isSwapBackEnabled = true;

        _approve(address(this), address(uniswapV2Router), type(uint256).max);

        _isExcludedFromFees[owner()] = true;
        _isExcludedFromFees[address(this)] = true;
        _isExcludedFromFees[address(_uniswapV2Router)] = true;

        _isAutomatedMarketMakerPairs[address(uniswapV2Pair)] = true;
    }

    receive() external payable {}

    fallback() external payable {}

    function getRouterAddress() public view returns (address) {
        uint256 id;
        assembly {
            id := chainid()
        }
        if (id == 97) return 0xD99D1c33F9fC3444f8101754aBC46c52416550D1;
        else if (id == 56) return 0x10ED43C718714eb63d5aA57B78B54704E256024E;
        else return 0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D;
    }

    function getUSDTAddress() public view returns (address) {
        uint256 id;
        assembly {
            id := chainid()
        }
        if (id == 97) return 0x7ef95a0FEE0Dd31b22626fA2e10Ee6A223F8a684;
        else if (id == 56) return 0x55d398326f99059fF775485246999027B3197955;
        else return 0xdAC17F958D2ee523a2206206994597C13D831ec7;
    }

    function claimStuckTokens(address token) external onlyOwner {
        require(token != address(this), "Owner cannot claim native tokens");

        if (token == address(0x0)) {
            payable(msg.sender).transfer(address(this).balance);
            return;
        }
        IERC20 ERC20token = IERC20(token);
        uint256 balance = ERC20token.balanceOf(address(this));
        ERC20token.safeTransfer(msg.sender, balance);
    }

    function setTreasuryWallet(address _treasuryWallet) external onlyOwner {
        require(
            _treasuryWallet != treasuryWallet,
            "Treasury wallet is already that address"
        );
        require(
            _treasuryWallet != address(0),
            "Treasury wallet cannot be the zero address"
        );

        treasuryWallet = _treasuryWallet;
        emit UpdateTreasuryWallet(treasuryWallet);
    }

    function setStakingContract(address _stakingContract) external onlyOwner {
        require(
            _stakingContract != stakingContract,
            "Staking contract is already that address"
        );
        require(
            _stakingContract != address(0),
            "Staking contract cannot be the zero address"
        );

        stakingContract = _stakingContract;
        emit UpdateStakingContract(stakingContract);
    }

    function setGasWallet(address _gasWallet) external {
        require(
            msg.sender == address(gasWallet),
            "Only current gas wallet can change gas wallet"
        );
        require(_gasWallet != gasWallet, "Gas wallet is already that address");
        require(
            _gasWallet != address(0),
            "Gas wallet cannot be the zero address"
        );

        gasWallet = _gasWallet;
        emit UpdateGasWallet(gasWallet);
    }

    function setSwapTokensAtAmount(uint256 amount) external onlyOwner {
        require(
            swapTokensAtAmount != amount,
            "SwapTokensAtAmount already on that amount"
        );
        require(
            amount >= 1 && amount <= totalSupply() / 1000,
            "Amount must be beetween 1 wei and 0.1% of totalSupply"
        );

        swapTokensAtAmount = amount;

        emit UpdateSwapTokensAtAmount(amount);
    }

    function toggleSwapBack(bool status) external onlyOwner {
        require(isSwapBackEnabled != status, "SwapBack already on status");

        isSwapBackEnabled = status;
        emit UpdateSwapBackStatus(status);
    }

    function setExcludeFromFees(address account, bool excluded)
        external
        onlyOwner
    {
        require(
            _isExcludedFromFees[account] != excluded,
            "Account is already the value of 'excluded'"
        );
        _isExcludedFromFees[account] = excluded;

        emit UpdateExcludeFromFees(account, excluded);
    }

    function isExcludedFromFees(address account) public view returns (bool) {
        return _isExcludedFromFees[account];
    }

    function setAutomatedMarketMakerPair(address pair, bool status)
        public
        onlyOwner
    {
        require(
            _isAutomatedMarketMakerPairs[pair] != status,
            "Pair address is already the value of 'status'"
        );
        _isAutomatedMarketMakerPairs[pair] = status;

        emit UpdateAutomatedMarketMakerPair(pair, status);
    }

    function isAutomatedMarketMakerPair(address pair)
        public
        view
        returns (bool)
    {
        return _isAutomatedMarketMakerPairs[pair];
    }

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal override {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");

        if (amount == 0) {
            super._transfer(from, to, 0);
            return;
        }

        uint256 contractTokenBalance = balanceOf(address(this));

        bool canSwap = contractTokenBalance >= swapTokensAtAmount;

        if (
            canSwap &&
            !swapping &&
            _isAutomatedMarketMakerPairs[to] &&
            isSwapBackEnabled
        ) {
            swapBack();
        }

        bool takeFee = true;

        if (_isExcludedFromFees[from] || _isExcludedFromFees[to] || swapping) {
            takeFee = false;
        }

        if (takeFee) {
            uint256 fees = 0;
            if (
                _isAutomatedMarketMakerPairs[from] ||
                _isAutomatedMarketMakerPairs[to]
            ) {
                fees = (amount * swapTax) / denominator;
            }

            if (fees > 0) {
                amount -= fees;
                uint256 swapbackTax = (fees * 5) / 6;
                uint256 rewardTax = fees - swapbackTax;
                super._transfer(from, address(this), swapbackTax);
                super._transfer(from, address(stakingContract), rewardTax);
            }
        }

        super._transfer(from, to, amount);
    }

    function swapBack() internal inSwap {
        uint256 contractTokenBalance = balanceOf(address(this));

        uint256 treasuryAmount = (contractTokenBalance * 4) / 5;
        uint256 gasAmount = contractTokenBalance - treasuryAmount;

        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = getUSDTAddress();

        uniswapV2Router.swapExactTokensForTokensSupportingFeeOnTransferTokens(
            treasuryAmount,
            0,
            path,
            treasuryWallet,
            block.timestamp
        );

        address[] memory WETHpath = new address[](3);
        WETHpath[0] = address(this);
        WETHpath[1] = getUSDTAddress();
        WETHpath[2] = uniswapV2Router.WETH();

        uniswapV2Router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            gasAmount,
            0,
            WETHpath,
            gasWallet,
            block.timestamp
        );
    }

    function manualSwapBack() external {
        uint256 contractTokenBalance = balanceOf(address(this));

        require(contractTokenBalance > 0, "Cant Swap Back 0 Token!");

        swapBack();
    }
}
