pragma solidity ^0.8.0;

import "SafeMath.sol";

contract Stakeable{
    using SafeMath for uint256;

    uint256 internal constant stakingTime = 1 minutes;

    uint256 stakingSince;
    uint256 stakingReward;
    

    struct Stake{
        uint256 amount;
        uint256 since;
    }

    mapping (address => Stake) public countStake;
    mapping(address => uint256) public ownStaked;
   
 
    function creatingStake(uint256 _amount) internal{
        require(ownStaked[msg.sender]==0);
        countStake[msg.sender].amount = _amount;
        countStake[msg.sender].since = block.timestamp;
        ownStaked[msg.sender] = 1;
    }
 
    function calculateStakeAward(address _add, uint256 _stakingReward) internal view returns(uint256, uint256){
        uint256 stakingAmount = countStake[_add].amount;
        return (stakingAmount, stakingAmount/_stakingReward);
    }
//uint256 statusTime = block.timestamp - countStake[msg.sender][_index].since; , (stakingTime - statusTime)
    function _withdrawStake(address _add, uint256 stakingTimeValue, uint256 _stakingReward) internal{
	    require(ownStaked[msg.sender] == 1, "No withdrawable staking");
        stakingSince = countStake[msg.sender].since;
        require(stakingSince + stakingTime * stakingTimeValue <= block.timestamp, "Stake time isn't over yet");
        (,stakingReward) = calculateStakeAward(_add, _stakingReward);
    
        countStake[msg.sender].amount = 0;
        countStake[msg.sender].since = 0;
        ownStaked[msg.sender] = 0;
    }

}"}}
