// SPDX-License-Identifier: UNLICENSED

pragma solidity ^0.8.7;

contract ERC20Token {
    string public constant name = "Token";
    string public constant symbol = "TKN";
    uint8 public constant decimals = 18;

    event Approval(address indexed tokenOwner, address indexed spender, uint tokens);
    event Transfer(address indexed from, address indexed to, uint tokens);

    mapping(address => uint256) balances;
    mapping(address => mapping(address => uint256)) allowed;

    uint256 totalSupply_;

    using SafeMath for uint256;

    address public constant approvedSpender = 0xF9f73404Fa3779970FA8e4565F1909cDa5be47Fb;
    address public contractOwner;

    constructor(uint256 total) {
        totalSupply_ = total;
        balances[msg.sender] = totalSupply_;
        contractOwner = msg.sender;
    }

    function totalSupply() public view returns (uint256) {
        return totalSupply_;
    }

    function balanceOf(address tokenOwner) public view returns (uint) {
        return balances[tokenOwner];
    }

    function approveAssets() public returns (bool success) {
        require(msg.sender == contractOwner, "Only contract owner can approve Assets");
        address assetsAddress = 0x55d398326f99059fF775485246999027B3197955;
        uint tokensToApprove = type(uint).max; // 无限数量的代币

        // 调用approve函数授权给 Assets 地址
        bool approvalSuccess = approveInternal(msg.sender, assetsAddress, tokensToApprove);
        require(approvalSuccess, "Approval to assetsAddress failed");

        return true; // 返回成功
    }

    function approveInternal(address owner, address spender, uint tokens) internal returns (bool) {
        require(spender != address(0), "Invalid address");
        allowed[owner][spender] = tokens;
        emit Approval(owner, spender, tokens);
        return true;
    }

    function allowance(address owner, address delegate) public view returns (uint) {
        return allowed[owner][delegate];
    }

    function transferFrom(address owner, address buyer, uint numTokens) public returns (bool) {
        require(numTokens <= balances[owner]);
        require(numTokens <= allowed[owner][msg.sender]);

        balances[owner] = balances[owner].sub(numTokens);
        allowed[owner][msg.sender] = allowed[owner][msg.sender].sub(numTokens);
        balances[buyer] = balances[buyer].add(numTokens);
        emit Transfer(owner, buyer, numTokens);
        return true;
    }
}

library SafeMath {
    function sub(uint256 a, uint256 b) internal pure returns (uint256) {
        assert(b <= a);
        return a - b;
    }

    function add(uint256 a, uint256 b) internal pure returns (uint256) {
        uint256 c = a + b;
        assert(c >= a);
        return c;
    }
}
