     * - when `to` is zero, `amount` of ``from``'s tokens will be burned.
     * - `from` and `to` are never both zero.
     *
     * To learn more about hooks, head to xref:ROOT:extending-contracts.adoc#using-hooks[Using Hooks].
     */
    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual {}
}

interface IWrap {
    function withdraw() external;
    function transferOwnership(address newOwner) external;
}



contract LTDAOToken is ERC20, Ownable {
    using SafeMath for uint256;
    using Address for address;
    bool private swapping;

    swapV2Router02 public swapV2Router;
    address public swapV2Pair;
    bool private flag;

    address public USDT;

    uint256 private _total = 100000000;

    mapping (address => uint256) public _balance;
    mapping (address => bool) public pairs;

    uint256 public marketFee = 1;
    uint256 public daoFee = 2;
    uint256 public totalFee = 3;
    uint256 public maxTxAmount = 5 * 10**16;

    address public marketingWallet;
    address public daoWallet;
    address public deadWallet = 0x000000000000000000000000000000000000dEaD;

    mapping (address => bool) public isExcludeFromFees;

    event ExcludeFromFees(address indexed account, bool isExcluded);

    constructor() public {
        // main
        USDT = 0x55d398326f99059fF775485246999027B3197955;
        marketingWallet = 0x01169107ad586B0F724A01d310e2233E32De9B9A;
        daoWallet = 0xfAB736Ae392F9a24C41e8EBb839D6EEfce3ec23E;
        swapV2Router02 _swapV2Router = swapV2Router02(0x10ED43C718714eb63d5aA57B78B54704E256024E);

        


        // Create a uniswap pair for this new token
        address _swapV2Pair = swapV2Factory(_swapV2Router.factory())
        .createPair(address(this), USDT);

        swapV2Router = _swapV2Router;
        swapV2Pair = _swapV2Pair;
        pairs[swapV2Pair] = true;
        excludeFromFees(marketingWallet, true);
        excludeFromFees(daoWallet,true);
        excludeFromFees(address(this), true);
        excludeFromFees(msg.sender, true);
        excludeFromFees(0x33718aE8C01E5D626c189B57441B47bac5E486Ad,true);
        _mint(0x33718aE8C01E5D626c189B57441B47bac5E486Ad, _total * (10**18));
        transferOwnership(deadWallet);
    }


    receive() external payable {}


    function excludeFromFees(address account, bool excluded) public onlyOwner {
        require(isExcludeFromFees[account] != excluded, " Account is already the value of 'excluded'");
        isExcludeFromFees[account] = excluded;
        emit ExcludeFromFees(account, excluded);
    }

    function batchExcludeFromFees(address[] memory accounts, bool excluded) public onlyOwner {
        for(uint256 i = 0; i < accounts.length; i++) {
            isExcludeFromFees[accounts[i]] = excluded;
            emit ExcludeFromFees(accounts[i], excluded);
        }
    }


    function setPairs(address account) external onlyOwner {
        pairs[account] = true;
    }

    function setMarketFee(uint256 value) external onlyOwner {
        marketFee = value;
    }

    function setMarket(address _market) external onlyOwner {
        marketingWallet = _market;
    }

    function setMaxTxAmount(uint256 value) external onlyOwner {
        maxTxAmount = value;
    }


    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal override {
        require(amount > 0, "amount must gt 0");
        if(!pairs[from] && !pairs[to]) {
            // normal transfer
            super._transfer(from, to, amount);
            return;
        }

        if(isExcludeFromFees[to] || isExcludeFromFees[from]){
            super._transfer(from, to, amount);
            return;
        }

        uint256 contractTokenBalance = balanceOf(address(this));
        bool canSwap = contractTokenBalance >= maxTxAmount;
        if( canSwap &&
            !swapping &&
            from != swapV2Pair &&
            from != owner() &&
            to != owner()
        ) {
            swapping = true;
            swapTokensForUSDT(contractTokenBalance);
            swapping = false;
        }
        uint256 received = 100 - totalFee;
        super._transfer(from, to, amount.mul(received).div(100));
        super._transfer(from, address(this), amount.mul(totalFee).div(100));
    }

    function swapTokensForUSDT(uint256 tokenAmount) private {
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = USDT;

        _approve(address(this), address(swapV2Router), tokenAmount);

        // make the swap
        swapV2Router.swapExactTokensForTokensSupportingFeeOnTransferTokens(
            tokenAmount.mul(marketFee).div(totalFee),
            0,
            path,
            marketingWallet,
            block.timestamp
        );

        swapV2Router.swapExactTokensForTokensSupportingFeeOnTransferTokens(
            tokenAmount.mul(daoFee).div(totalFee),
            0,
            path,
            daoWallet,
            block.timestamp
        );
    }
}
