      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.18;

import { ReentrancyGuard } from "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import { Address } from "@openzeppelin/contracts/utils/Address.sol";
import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import { Ownable } from "@openzeppelin/contracts/access/Ownable.sol";
import { Pausable } from "@openzeppelin/contracts/security/Pausable.sol";
import { IStaking } from "./interfaces/IStaking.sol";

contract Staking is Ownable, Pausable, ReentrancyGuard {
    using SafeERC20 for IERC20;
    using Address for address;

    uint256 private constant RECOVERY_DELAY = 14 days; // Example of a one-week delay

    struct Stakeholder {
        uint256 staked; // amount of staked tokens
        uint256 timestamp;
        uint256 locktime;
        uint256 earnedRewards;
        uint256 rewardDebt;
        uint256 pendingRewards;
        uint256 totalRewardsClaimed;
    }

    struct Pool {
        address stakingToken;
        address rewardToken;
        uint256 rewardAmount;
        uint256 startTime;
        uint256 stopTime;
        uint256 duration;
        uint256 lockTime;
        uint256 totalStaked;
        uint256 rewardPerSecond;
        uint256 accRewardPerShare;
        uint256 lastRewardTimestamp;
        uint256 totalRewardsDisbursed;
        bool recovered;
        mapping(address => Stakeholder) stakeholders;
    }

    Pool[] Pools;

    uint256 private constant REWARDS_PRECISION = 1e12;

    event Staked(address indexed staker, uint256 amount, uint256 poolId);
    event Unstaked(address indexed staker, uint256 amount, uint256 poolId);
    event Withdraw(address indexed staker, uint256 rewardAmount, uint256 poolId);
    event Recover(address indexed token, uint256 amount);

    constructor(
    ) {
    }

    function loadReward(uint256 _poolId) external onlyOwner {
        Pool storage pool = Pools[_poolId];
        IERC20(pool.rewardToken).safeTransferFrom(msg.sender, address(this), pool.rewardAmount);

    }

    function recoverTokens(uint256 _poolId) external onlyOwner {
        require(_poolId < Pools.length, "Pool ID out of range");

        Pool storage pool = Pools[_poolId];
        uint256 recoveryTime = pool.stopTime + RECOVERY_DELAY;
        require(block.timestamp > recoveryTime, "Cannot recover tokens before recovery time");

        IERC20 rewardToken = IERC20(pool.rewardToken);
        uint256 balance = pool.rewardAmount - pool.totalRewardsDisbursed; // Compute balance for this specific pool

        require(balance > 0, "No rewards left to recover"); // Ensure there is something to recover

        rewardToken.safeTransfer(owner(), balance); // Transfer the remaining rewards
        pool.rewardAmount = pool.rewardAmount - balance; // Update the reward amount accordingly
        pool.recovered = true; // Mark the tokens as recovered

        emit Recover(address(rewardToken), balance);
    }

    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }

    function addPool(address _stakingToken,
        address _rewardToken,
        uint256 _rewardAmount,
        uint256 _startTime,
        uint256 _stopTime,
        uint256 _lockTime) external onlyOwner
    {
        require(_stakingToken.isContract(), "Staking: stakingToken not a contract address");
        require(_rewardToken.isContract(), "Staking: rewardToken not a contract address");
        require(_rewardAmount > 0, "Staking: rewardAmount must be greater than zero");
        require(_startTime > block.timestamp && _startTime < _stopTime, "Staking: incorrect timestamps");
        require(_lockTime > 0, "Staking: lockTime must be greater than zero");

        Pool storage newPool = Pools.push();

        newPool.stakingToken = _stakingToken;
        newPool.rewardToken = _rewardToken;
        newPool.rewardAmount = _rewardAmount;
        newPool.startTime = _startTime;
        newPool.stopTime = _stopTime;
        newPool.duration = _stopTime - _startTime;
        newPool.lockTime = _lockTime;
        newPool.rewardPerSecond = _rewardAmount / (_stopTime - _startTime);
        newPool.lastRewardTimestamp = _startTime;
        newPool.accRewardPerShare = 0;
    }

    function stake(uint256 _amount, uint256 _poolId) external whenNotPaused {
        updatePoolRewards(_poolId);
        Pool storage pool = Pools[_poolId];

        require(block.timestamp >= pool.startTime, "Staking: staking not started");
        require(block.timestamp <= pool.stopTime, "Staking: staking period over");
        require(_amount > 0, "Staking: amount can't be 0");

        Stakeholder storage stakeholder = pool.stakeholders[msg.sender];
        if(stakeholder.staked > 0){
            uint256 accumulatedReward = stakeholder.staked * pool.accRewardPerShare / REWARDS_PRECISION;
            uint256 pendingReward = accumulatedReward - stakeholder.rewardDebt;
            stakeholder.pendingRewards = stakeholder.pendingRewards + pendingReward;
        }

        stakeholder.staked += _amount;
        stakeholder.rewardDebt = stakeholder.staked * pool.accRewardPerShare / REWARDS_PRECISION;

        stakeholder.timestamp = block.timestamp;
        stakeholder.locktime = block.timestamp + pool.lockTime;


        pool.totalStaked += _amount;

        IERC20(pool.stakingToken).safeTransferFrom(msg.sender, address(this), _amount);

        emit Staked(msg.sender, _amount, _poolId);
    }

    function unstake(uint256 _amount, uint256 _poolId) external whenNotPaused nonReentrant{
        updatePoolRewards(_poolId);
        Pool storage pool = Pools[_poolId];

        require(_amount > 0, "_amount have to be bigger than 0");
        Stakeholder storage stakeholder = pool.stakeholders[msg.sender];
        // New condition to bypass locktime if pool has ended
        require(stakeholder.locktime <= block.timestamp || block.timestamp >= pool.stopTime, "Staking: tokens are locked");

        require(stakeholder.staked > 0, "Staking: you have not participated in staking");
        require(stakeholder.staked >= _amount, "Staking: cannot unstake more than your balance");
        uint256 accumulatedReward = stakeholder.staked * pool.accRewardPerShare / REWARDS_PRECISION;
        uint256 pendingReward = accumulatedReward + stakeholder.pendingRewards - stakeholder.rewardDebt;

        stakeholder.rewardDebt = accumulatedReward - (_amount * pool.accRewardPerShare / REWARDS_PRECISION);
        stakeholder.totalRewardsClaimed += pendingReward;
        stakeholder.pendingRewards = 0;
        stakeholder.staked -= _amount;

        pool.totalRewardsDisbursed += pendingReward;
        pool.totalStaked -= _amount;
        _withdrawReward(msg.sender, _poolId, pendingReward);
        _withdrawStaked(msg.sender, _amount, pool.stakingToken);

        emit Unstaked(msg.sender, _amount, _poolId);
    }

    function claimRewards(uint256 _poolId) external whenNotPaused nonReentrant {
        updatePoolRewards(_poolId);
        Pool storage pool = Pools[_poolId];


        Stakeholder storage stakeholder = pool.stakeholders[msg.sender];
        // New condition to bypass locktime if pool has ended
        require(stakeholder.locktime <= block.timestamp || block.timestamp >= pool.stopTime, "Staking: tokens are locked");
        uint256 rewardsToHarvest = (stakeholder.staked * pool.accRewardPerShare / REWARDS_PRECISION) + stakeholder.pendingRewards - stakeholder.rewardDebt;
        if (rewardsToHarvest == 0) {
            stakeholder.rewardDebt = stakeholder.staked * pool.accRewardPerShare / REWARDS_PRECISION;
            return;
        }
        stakeholder.rewardDebt = stakeholder.staked * pool.accRewardPerShare / REWARDS_PRECISION;
        stakeholder.totalRewardsClaimed += rewardsToHarvest;
        stakeholder.pendingRewards = 0;
        pool.totalRewardsDisbursed += rewardsToHarvest;
        _withdrawReward(msg.sender, _poolId, rewardsToHarvest);
    }


    function updatePoolRewards(uint256 pid) public {
        Pool storage pool = Pools[pid];
        uint256 currentTimestamp = block.timestamp > pool.stopTime ? pool.stopTime : block.timestamp;
        if (currentTimestamp > pool.lastRewardTimestamp) {
            uint256 lpSupply = pool.totalStaked;
            if (lpSupply > 0) {
                uint256 timestamps = currentTimestamp - pool.lastRewardTimestamp;
                uint256 rewards = timestamps * pool.rewardPerSecond;
                pool.accRewardPerShare = pool.accRewardPerShare + (rewards * REWARDS_PRECISION / pool.totalStaked);
            }
            pool.lastRewardTimestamp = currentTimestamp;
        }
    }


    function earned(address _stakeholder, uint256 _poolId) public view returns (uint256) {
        Pool storage pool = Pools[_poolId];
        Stakeholder memory stakeholder = pool.stakeholders[_stakeholder];
        if (stakeholder.staked == 0) return 0;

        uint256 accRewardPerShare = pool.accRewardPerShare;
        uint256 lpSupply = pool.totalStaked;
        if (block.timestamp > pool.lastRewardTimestamp && lpSupply != 0) {
            uint256 timestampSinceLastReward = (block.timestamp < pool.stopTime ? block.timestamp : pool.stopTime) - pool.lastRewardTimestamp;
            uint256 rewards = timestampSinceLastReward * pool.rewardPerSecond;
            accRewardPerShare = accRewardPerShare + ((rewards * REWARDS_PRECISION) / lpSupply);
        }
        uint256 pending = (stakeholder.staked * accRewardPerShare) / REWARDS_PRECISION + stakeholder.pendingRewards - stakeholder.rewardDebt;

        return pending;
    }


    function getStartTime(uint256 _poolId) external view returns (uint256) {
        Pool storage pool = Pools[_poolId];
        return pool.startTime;
    }

    function getStopTime(uint256 _poolId) external view returns (uint256) {
        Pool storage pool = Pools[_poolId];
        return pool.stopTime;
    }

    function getLockTime(uint256 _poolId) external view returns (uint256) {
        Pool storage pool = Pools[_poolId];
        return pool.lockTime;
    }

    function getDuration(uint256 _poolId) external view returns (uint256) {
        Pool storage pool = Pools[_poolId];
        return pool.duration;
    }

    function getTotalStaked(uint256 _poolId) external view returns (uint256) {
        Pool storage pool = Pools[_poolId];
        return pool.totalStaked;
    }

    function getRewardTokenBalance(uint256 _poolId) external view returns (uint256) {
        Pool storage pool = Pools[_poolId];
        return IERC20(pool.rewardToken).balanceOf(address(this));
    }

    function getStakingTokenBalance(uint256 _poolId) external view returns (uint256) {
        Pool storage pool = Pools[_poolId];
        return IERC20(pool.stakingToken).balanceOf(address(this));
    }

    function getStakingTokenBalancebyAddress(uint256 _poolId, address _userAddress) external view returns (uint256) {
        Pool storage pool = Pools[_poolId];
        Stakeholder memory stakeholder = pool.stakeholders[_userAddress];
        return stakeholder.staked;
    }

    function getTimeRemaining(uint256 _poolId) public view returns (uint256) {
        Pool storage pool = Pools[_poolId];
        uint256 timeRemaining = block.timestamp <= pool.stopTime
            ? block.timestamp >= pool.startTime ? pool.stopTime - block.timestamp : pool.duration
            : 0;
        return timeRemaining;
    }

    function getTimeElapsed(uint256 _poolId) external view returns (uint256) {
        Pool storage pool = Pools[_poolId];
        uint256 timeElapsed = block.timestamp >= pool.startTime
            ? block.timestamp <= pool.stopTime ? block.timestamp - pool.startTime : pool.duration
            : 0;
        return timeElapsed;
    }

    function getStaked(address _stakeholder, uint256 _poolId) external view returns (uint256) {
        Pool storage pool = Pools[_poolId];
        return pool.stakeholders[_stakeholder].staked;
    }

    function getStakedLock(address _stakeholder, uint256 _poolId) external view returns (uint256) {
        Pool storage pool = Pools[_poolId];
        return pool.stakeholders[_stakeholder].timestamp;
    }

    function getRewardShare(uint256 _poolId) external view returns (uint256) {
        Pool storage pool = Pools[_poolId];
        return pool.accRewardPerShare;
    }

    function getRewardperSecond(uint256 _poolId) external view returns (uint256) {
        Pool storage pool = Pools[_poolId];
        return pool.rewardPerSecond;
    }

    function getLastReward(uint256 _poolId) external view returns (uint256) {
        Pool storage pool = Pools[_poolId];
        return pool.lastRewardTimestamp;
    }

    function getTotalRewardsClaimed(address _stakeholder, uint256 _poolId) external view returns (uint256) {
        Pool storage pool = Pools[_poolId];
        return pool.stakeholders[_stakeholder].totalRewardsClaimed;
    }

    function _withdrawStaked(address _to, uint256 _amount, address _stakingToken) internal {
        IERC20(_stakingToken).safeTransfer(_to, _amount);
    }

    function _withdrawReward(address _to, uint256 _poolId, uint256 _reward) internal {
        Pool storage pool = Pools[_poolId];
        IERC20(pool.rewardToken).safeTransfer(_to, _reward);
        pool.stakeholders[_to].totalRewardsClaimed += _reward;

        emit Withdraw(msg.sender, _reward, _poolId);
    }

    function getStakeholderInfo(address _staker, uint256 _poolId) external view returns (
        uint256 staked,
        uint256 timestamp,
        uint256 earnedRewards,
        uint256 rewardDebt,
        uint256 pendingRewards,
        uint256 totalRewardsClaimed,
        uint256 locktime
    ) {
        Stakeholder storage stakeholder = Pools[_poolId].stakeholders[_staker];
        staked = stakeholder.staked;
        timestamp = stakeholder.timestamp;
        earnedRewards = stakeholder.earnedRewards;
        rewardDebt = stakeholder.rewardDebt;

        Pool storage pool = Pools[_poolId];
        uint256 accumulatedReward = (staked * pool.accRewardPerShare) / REWARDS_PRECISION;
        pendingRewards = accumulatedReward - rewardDebt;
        totalRewardsClaimed = stakeholder.totalRewardsClaimed;
        locktime = stakeholder.locktime;
    }

    function poolLength() external view returns (uint256) {
        return Pools.length;
    }

    function getPoolInfo(uint256 _poolId) external view returns (
        address stakingToken,
        address rewardToken,
        uint256 rewardAmount,
        uint256 startTime,
        uint256 stopTime,
        uint256 duration,
        uint256 lockTime,
        uint256 totalStaked,
        uint256 rewardPerSecond,
        uint256 totalRewardsDisbursed
    ) {
        Pool storage pool = Pools[_poolId];
        stakingToken = pool.stakingToken;
        rewardToken = pool.rewardToken;
        rewardAmount = pool.rewardAmount;
        startTime = pool.startTime;
        stopTime = pool.stopTime;
        duration = pool.duration;
        lockTime = pool.lockTime;
        totalStaked = pool.totalStaked;
        rewardPerSecond = pool.rewardPerSecond;
        totalRewardsDisbursed = pool.totalRewardsDisbursed;
    }
}
