// Created by https://whiskey.tools/
// Contract Address for WSKY (Staking Token): 0x05f735dbF6cA67207BE286f2ae8Dc5d844DaC1Be

pragma solidity ^0.8.0;





contract WhiskeyStaking is ERC20Burnable, Ownable, ReentrancyGuard {
    IERC20 public wskyToken;

    struct StakeInfo {
        uint256 amount;
        uint256 lastRewardClaimTime;
        uint256 lockTime;
        uint256 multiplier;
    }

    mapping(address => StakeInfo) public stakes;
    mapping(address => bool) private _inTransaction;

    uint256 public totalStaked;
    uint256 public totalRewardsDistributed;

    uint256 public rewardRate = 400000; // WSKY needed to stake to get 1 SHOTS per second reward
    uint256 public lastRewardUpdateTime = block.timestamp;
    uint256 public constant INCREASE_PERIOD = 30 days; // Reward rate increases every month
    uint256 public constant INCREASE_RATE = 10; // Reward Rate increases by 10% every month for 12 months

    uint256 public constant MINIMUM_LOCK_TIME = 6 seconds;

    bool public stakingEnabled = true; // Always true

    event Stake(address indexed user, uint256 amount);
    event Unstake(address indexed user, uint256 amount);
    event RewardClaimed(address indexed user, uint256 amount);
    event StakingStatusChanged(bool newStatus);
    event RewardRateChanged(uint256 newRate);

    modifier notInTransaction() {
        require(!_inTransaction[msg.sender], "Cannot perform multiple operations in one transaction");
        _inTransaction[msg.sender] = true;
        _;
        _inTransaction[msg.sender] = false;
    }

    constructor(address _wskyToken) ERC20("WSKY Credits", "SHOTS") {
        _mint(msg.sender, 250000 * 10 ** decimals());
        wskyToken = IERC20(_wskyToken); // Token used for staking
    }

    /**
     * @dev Updates the reward rate based on time passed.
     */
    function updateRewardRate() internal {
        uint256 elapsedTime = block.timestamp - lastRewardUpdateTime;
        if (elapsedTime >= INCREASE_PERIOD) {
            uint256 periodsPassed = elapsedTime / INCREASE_PERIOD;
            for (uint256 i = 0; i < periodsPassed; i++) {
                if (i >= 12) {
                    break;
                }
                rewardRate = (rewardRate * (100 + INCREASE_RATE)) / 100;
            }
            lastRewardUpdateTime = block.timestamp;
            emit RewardRateChanged(rewardRate);
        }
    }

    /**
     * @dev Stake a specific amount for a given lock time.
     * @param amount The amount of tokens to stake.
     * @param newLockTime The lock time for the staked tokens.
     */
    function stake(uint256 amount, uint256 newLockTime) external nonReentrant notInTransaction {
        updateRewardRate();

        require(stakingEnabled, "Staking is disabled");
        require(amount > 0, "Amount must be greater than 0");
        require(newLockTime >= MINIMUM_LOCK_TIME, "Lock time must be at least 6 hours");

        StakeInfo storage user = stakes[msg.sender];

        // Check if the user has already staked tokens
        if (user.amount > 0) {
            uint256 remainingLockTime = (block.timestamp >= user.lockTime) ? 0 : user.lockTime - block.timestamp;
            require(newLockTime >= remainingLockTime, "New lock time cannot be less than remaining lock time");
        }
        user.lastRewardClaimTime = block.timestamp;

        // Update stake amount
        user.amount += amount;

        // Update lock time
        user.lockTime = block.timestamp + newLockTime;

        // Update multiplier based on new lock time
        user.multiplier = getMultiplier(newLockTime);

        // Transfer staked tokens into contract
        wskyToken.transferFrom(msg.sender, address(this), amount);
        totalStaked += amount; // Update current total staked amount in contract

        emit Stake(msg.sender, amount);
    }

   
    
    // @dev Unstake all tokens 
    
    function unstake() external nonReentrant {
        StakeInfo storage user = stakes[msg.sender];
        require(user.amount > 0, "You don't have any staked tokens");
        require(block.timestamp >= user.lockTime, "Lock time has not expired yet");

        // Unstake all tokens and transfer to the user
        uint256 unstakeAmount = user.amount;

        // Update contract state
        totalStaked -= user.amount;

        // Reset the user's staking information
        user.amount = 0;
        user.lockTime = 0;
        user.multiplier = 0;

        // Update last reward claim time
        user.lastRewardClaimTime = block.timestamp;

        // Transfer staked tokens back to the user
        wskyToken.transfer(msg.sender, unstakeAmount);

        emit Unstake(msg.sender, unstakeAmount);
    }

    /**
     * @dev Get the claimable reward balance for a user.
     * @param userAddress The address of the user.
     * @return The claimable reward balance.
     */
    function claimableBalance(address userAddress) external view returns (uint256) {
        StakeInfo storage user = stakes[userAddress];
        uint256 reward = calculateReward(user);
        return reward;
    }

    /**
     * @dev Claim SHOTS rewards for the calling user.
     */
    function claimShots() external nonReentrant notInTransaction {
        StakeInfo storage user = stakes[msg.sender];
        require(user.amount > 0, "No staked amount to claim");

        updateRewardRate();

        uint256 reward = calculateReward(user);
        _mint(msg.sender, reward); // Mint SHOTS as rewards

        user.lastRewardClaimTime = block.timestamp;
        totalRewardsDistributed += reward;

        emit RewardClaimed(msg.sender, reward);
    }

    /**
    * @dev Get the remaining lock time for a user.
    * @param userAddress The address of the user.
    * @return The remaining lock time in seconds.
    */
    function getRemainingLockTime(address userAddress) public view returns (uint256) {
        StakeInfo storage user = stakes[userAddress];
        if (block.timestamp >= user.lockTime) {
            return 0;
        }
        return user.lockTime - block.timestamp;
    }

    /**
     * @dev Calculate the reward for a user.
     * @param user The user's staking information.
     * @return The calculated reward amount.
     */
    function calculateReward(StakeInfo storage user) internal view returns (uint256) {
        uint256 timeSinceLastClaim = block.timestamp - user.lastRewardClaimTime;
        uint256 reward = (user.amount * timeSinceLastClaim * 10 ** decimals()) / (rewardRate * 10 ** decimals()); // Multiply by 1e18 to account for token decimals
        return ((reward * user.multiplier) / 100);
    }

    /**
     * @dev Get the reward multiplier based on lock time.
     * @param lockTime The lock time in seconds.
     * @return The reward multiplier.
     */
    function getMultiplier(uint256 lockTime) public pure returns (uint256) {
        if (lockTime >= 365 days) {
            return 300;
        } else if (lockTime >= 180 days) {
            return 200;
        } else if (lockTime >= 30 days) {
            return 150;
        } else if (lockTime >= 14 days) {
            return 120;
        } else if (lockTime >= 7 days) {
            return 110;
        } else {
            return 100;
        }
    }
}
