// File: contracts/ArkerMarket.sol


pragma solidity 0.8.7;







struct Sale {
    uint256 tokenId;
    IERC721 tokenAddress;
    address owner;
    uint256 price;
    bool buyed;
    uint256 endDate;
}

contract ArkerMarket is Pausable, Ownable {

    using SafeMath for uint256;
    using Counters for Counters.Counter;

    // ERC721 => Enabled
    mapping(address => bool) public _collections;

    // ERC721 => Token ID => Sale ID
    mapping (address => mapping (uint256 => uint256)) public _lastSaleByToken;

    Counters.Counter private _saleIds;
    mapping (uint256 => Sale) public sales;

    IERC20 public _currency;
    uint256 public _fee = 500; // 5%
    address public _feeReceiver;
    uint256 public _maxExpireDate = 15778800; // 6 months

    event SaleCreated(uint256 saleId, address indexed wallet, uint256 tokenId, address tokenAddress, uint256 amount, uint256 created, uint256 expire);
    event SaleCanceled(uint256 indexed saleId, uint256 created);
    event SaleBought(uint256 indexed saleId, address indexed wallet, uint256 amount, uint256 tokenId, address tokenAddress, uint256 created);


    constructor(IERC20 currency) {
        _currency = currency;
        _feeReceiver = msg.sender;
    }

    function sell(IERC721 _address, uint256 tokenId, uint256 amount, uint256 expire) external whenNotPaused {
        require(_collections[address(_address)], "Collection not whitelisted");
        require(_address.ownerOf(tokenId) == msg.sender, "User is not the owner");
        require(_address.isApprovedForAll(msg.sender, address(this)), "Contract address must be approved for transfer");
        require(expire <= _maxExpireDate, "You can't create a sale that long");

        uint256 oldSaleId = _lastSaleByToken[address(_address)][tokenId];
        if (isSaleAvailable(oldSaleId)) {
            cancel(oldSaleId);
        }

        _saleIds.increment();
        uint256 saleId = _saleIds.current();

        Sale memory sale;
        sale.tokenId = tokenId;
        sale.tokenAddress = _address;
        sale.owner = msg.sender;
        sale.price = amount;
        sale.buyed = false;
        sale.endDate = block.timestamp + expire;
        sales[saleId] = sale;
        _lastSaleByToken[address(_address)][tokenId] = saleId;
        emit SaleCreated(saleId, msg.sender, tokenId, address(_address), amount, block.timestamp, block.timestamp + expire);
    }

    function cancel(uint256 _saleId) public whenNotPaused isSaleActive(_saleId) {
        require(sales[_saleId].tokenAddress.ownerOf(sales[_saleId].tokenId) == msg.sender, "User is not the owner");

        sales[_saleId].endDate = block.timestamp;

        emit SaleCanceled(_saleId, block.timestamp);
    }

    function buy(uint256 _saleId, uint256 expire) external whenNotPaused isSaleActive(_saleId) {
        require(block.timestamp < expire, "Buy order expired");
        require(sales[_saleId].tokenAddress.ownerOf(sales[_saleId].tokenId) == sales[_saleId].owner, "Sale creator is not longer NFT owner");
        require(_currency.balanceOf(msg.sender) >= sales[_saleId].price, "User does not have enough balance");
        
        uint amount = sales[_saleId].price;
        uint256 feeAmount = amount.mul(_fee).div(10000);
        uint256 amountAfterFee = amount.sub(feeAmount);

        require(_currency.transferFrom(msg.sender, _feeReceiver, feeAmount), "Transfer from failed");
        require(_currency.transferFrom(msg.sender, sales[_saleId].owner, amountAfterFee), "Transfer from failed");
    
        sales[_saleId].tokenAddress.transferFrom(sales[_saleId].owner, msg.sender, sales[_saleId].tokenId);
        
        sales[_saleId].buyed = true;

        emit SaleBought(_saleId, msg.sender, sales[_saleId].price, sales[_saleId].tokenId, address(sales[_saleId].tokenAddress), block.timestamp);
    }

    function isSaleAvailable(uint256 _saleId) public view returns (bool) {
        return (sales[_saleId].endDate > block.timestamp) 
        && sales[_saleId].buyed == false 
        && sales[_saleId].tokenAddress.ownerOf(sales[_saleId].tokenId) == sales[_saleId].owner
        && sales[_saleId].tokenAddress.isApprovedForAll(sales[_saleId].owner, address(this));
    }

    modifier isSaleActive(uint256 _saleId) {
        require(
            (sales[_saleId].endDate > block.timestamp) && sales[_saleId].buyed == false,
            'Sale ended'
        );
        _;
    }

    function totalSales() external view returns (uint256) {
        return _saleIds.current();
    }

    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }

    function setFeeReceiver(address feeReceiver) external onlyOwner {
        _feeReceiver = feeReceiver;
    }

    function setFee(uint256 fee) external onlyOwner {
        require(fee < 3000, "Fee can't be higher than 30%");
        _fee = fee;
    }

    function setMaxExpireDate(uint256 maxExpireDate) external onlyOwner {
        _maxExpireDate = maxExpireDate;
    }

    function setCollection(IERC721 _address, bool enabled) external onlyOwner {
        _collections[address(_address)] = enabled;
    }

    function setCurrency(IERC20 currency) external onlyOwner {
        _currency = currency;
    }
}
