      "content": "// SPDX-License-Identifier: MIT

pragma solidity >= 0.7.0;
pragma experimental ABIEncoderV2;

import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "./Constructors.sol";
import "../roles/Manageable.sol";

contract RestrictedTransactionsHelper is Ownable, Manageable {
    using SafeMath for uint256;

    IERC20 _token;
    Constructors.AntiWhaleParams _params;

    mapping(address => bool) public _vipAddresses;
    mapping(address => bool) _marketMakerPairAddresses;

    bool _isTradingEnabled = false;

    constructor(
        address tokenAddress,
        uint256 decimals,
        address tokenOwnerAddress,
        address pancakePairAddress,
        Constructors.AntiWhaleHRParams memory params
    ){
        _token = IERC20(tokenAddress);
        _vipAddresses[address(_token)] = true;
        _vipAddresses[tokenOwnerAddress] = true;
        _marketMakerPairAddresses[pancakePairAddress] = true;

        _params.antiWhaleEndDate = params.antiWhaleEndDate;
        _params.whalePercentageThreshold = params.whalePercentageThreshold;
        _params.isMaxSellTransactionAmountOptionEnabled = params.isMaxSellTransactionAmountOptionEnabled;
        _params.maxSellTransactionAmount = params.humanReadableMaxSellTransactionAmount.mul((10 ** decimals));
        _params.isMaxWalletSizeOptionEnabled = params.isMaxWalletSizeOptionEnabled;
        _params.maxWalletSize = params.humanReadableMaxWalletSize.mul((10 ** decimals));
        _params.isWhaleMaxSellTransactionAmountOptionEnabled = params.isWhaleMaxSellTransactionAmountOptionEnabled;
        _params.whaleMaxSellTransactionAmount = params.humanReadableWhaleMaxSellTransactionAmount.mul((10 ** decimals));
    }

    function excludeFromVipAddresses(address vipAddress) external onlyAdmin {
        require(_vipAddresses[vipAddress] == true, "address is not a vip");
        _vipAddresses[vipAddress] = false;
    }

    function addToVipAddresses(address vipAddress) external onlyAdmin {
        require(_vipAddresses[vipAddress] == false, "address is already a vip");
        _vipAddresses[vipAddress] = true;
    }

    function enableTrading() external onlyAdmin {
        _isTradingEnabled = true;
    }

    function removeLimitations() external onlyAdmin {
        _params.isMaxSellTransactionAmountOptionEnabled = false;
        _params.isMaxWalletSizeOptionEnabled = false;
        _params.isWhaleMaxSellTransactionAmountOptionEnabled = false;
    }

    function setMaxWalletSize(uint256 maxWalletSize) external onlyAdmin {
        require(maxWalletSize >= _params.maxWalletSize, 'Cannot decrease max wallet size');
        _params.maxWalletSize = maxWalletSize;
    }

    function setAntiWhaleEndDate(uint256 endDate) external onlyAdmin {
        require(endDate < 10000000000, 'Unix timestamp must be in seconds, not milliseconds');
        _params.antiWhaleEndDate = endDate;
    }

    function getAntiWhaleEndDate() external view onlyAdmin returns (uint256) {
        return _params.antiWhaleEndDate;
    }

    function isLpAddress(address requestAddress) public view returns (bool) {
        return _marketMakerPairAddresses[requestAddress];
    }

    function addMarketMakerPair(address lpAddress) external onlyAdmin {
        require(!_marketMakerPairAddresses[lpAddress], "LP address already exists");
        _marketMakerPairAddresses[lpAddress] = true;
    }

    function isWhale(address wallet) public view returns (bool){
        uint256 computedWhaleBalance = _token.totalSupply() * _params.whalePercentageThreshold / 100;
        if (_token.balanceOf(wallet) > computedWhaleBalance) {
            return true;
        }
        return false;
    }

    function isTransactionAllowed(address from, address to, uint256 amount) external view returns (bool){
        if (_vipAddresses[from] || _vipAddresses[to]) {
            return true;
        }
        require(_isTradingEnabled, "Trading is not enabled");
        //buy
        if (_marketMakerPairAddresses[from]) {
            return isBuyTransactionAllowed(to, amount);
        }
        //sell
        if (_marketMakerPairAddresses[to]) {
            return isSellTransactionAllowed(from, amount);
        }
        return true;
    }

    function isBuyTransactionAllowed(address buyerAddress, uint256 amount) public view returns (bool){
        if (!_params.isMaxWalletSizeOptionEnabled) {
            return true;
        }

        uint256 computedNewBalance = _token.balanceOf(buyerAddress) + amount;

        if (computedNewBalance <= _params.maxWalletSize) {
            return true;
        }

        return false;
    }

    function isSellTransactionAllowed(address sellerAddress, uint256 amount) public view returns (bool){
        if (_params.antiWhaleEndDate > block.timestamp &&
        _params.isWhaleMaxSellTransactionAmountOptionEnabled &&
        isWhale(sellerAddress) &&
            amount > _params.whaleMaxSellTransactionAmount
        ) {
            return false;
        }

        if (_params.isMaxSellTransactionAmountOptionEnabled && amount > _params.maxSellTransactionAmount) {
            return false;
        }

        return true;
    }

    /*
        additional whaleFees regarding whale size
        from 2 to 3% -> + 0.1%
        from 3 to 4% -> + 0.2%
        from 4 to 5% -> + 0.3%
        from 5 to 6% -> + 0.5%
        from 6 to 7% -> + 0.8%
        from 7 to 8% -> + 1.3%
        from 8 to 9% -> + 2.1%
        > 9%         -> + 3.4%
    */
    function computeAdditionalWhaleFeePerThousand(address wallet) external view returns (uint256){
        if(!isWhale(wallet)){
            return 0;
        }

        uint256 whaleSize = _token.balanceOf(wallet).mul(100).div(_token.totalSupply());
        if(whaleSize <= 3){
            return 1;
        }
        if(whaleSize <= 4){
            return 2;
        }
        if(whaleSize <= 5){
            return 3;
        }
        if(whaleSize <= 6){
            return 5;
        }
        if(whaleSize <= 7){
            return 8;
        }
        if(whaleSize <= 8){
            return 13;
        }
        if(whaleSize <= 9){
            return 21;
        }

        return 34;
    }
}"
