    "contracts/Collateral/Collateral.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import './interfaces/ICollateral.sol';
import './Compound.sol';
import '@openzeppelin/contracts/access/AccessControl.sol';
import '@openzeppelin/contracts/token/ERC20/IERC20.sol';
import '@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol';

/*
 * Implementation of ICollateral
 */
contract Collateral is ICollateral, AccessControl, Compound {
    using SafeERC20 for IERC20;

    IERC20 private immutable aliceToken;

    mapping(address => UserStake) public stakes;
    // After stop, we count till that time, isntead of till time of withdrawal. So
    // when we import (addPlayerStake) again, we reset this time to 0 again.
    uint64 public contractStoppedAt = 0;
    // On each Season or reset, we'll change the version
    uint16 public currentVersion;
    uint256 public lastImportTime;
    uint256 public APY;
    uint256 public immutable yearlyApyInRay;

    constructor(address _admin, address _aliceToken, uint16 _currentVersion, uint256 _yearlyApy) {
        _setRoleAdmin(ADMIN, DEFAULT_ADMIN_ROLE);
        _grantRole(ADMIN, _admin);
        aliceToken = IERC20(_aliceToken);
        currentVersion = _currentVersion;
        APY = _yearlyApy;
        yearlyApyInRay = yearlyRateToRay(_yearlyApy * 10000);
    }

    bytes32 public constant ADMIN = keccak256('ADMIN');
    modifier onlyAdmin() {
        require(hasRole(ADMIN, msg.sender), 'Admin only operation');
        _;
    }

    function _calculateRewardForUserStake(
        uint256 _startTime,
        uint256 _givenEndTime,
        uint256 _stakedAmount
    ) internal view returns (uint256) {
        if (0 == _stakedAmount) {
            return 0;
        }

        // In case of client choosing future end time (only to query, not to withdraw), that one prevails
        uint256 endTime = _givenEndTime > 0 ? _givenEndTime : block.timestamp;

        // But if the contract is stopped, then that overwrites end time, also for future based queries
        if (contractStoppedAt > 0) {
            endTime = contractStoppedAt;
        }

        if (endTime < _startTime) {
            return 0;
        }

        uint256 duration = endTime - _startTime;

        uint256 newPrincipal = accrueInterest(_stakedAmount, yearlyApyInRay, duration);
        return newPrincipal - _stakedAmount;
    }

    function _isCurrentVersion(uint16 _version) internal view returns (bool) {
        return (_version == currentVersion);
    }

    function _withdrawPlayerCollateral(address _player) internal {
        UserStake storage userStake = stakes[_player];
        require(
            userStake.stakedAmount > 0 &&
            // Required that the imported amount was on this version: we
            // don't withdraw if last stake is from previous version
            _isCurrentVersion(userStake.version),
            'No stake for this user and version'
        );

        // We force 0 endTime, meaning it's FORBIDDEN to choose future time as end of period for withdrawal
        uint256 reward = _calculateRewardForUserStake(
            userStake.startTime,
            0,
            userStake.stakedAmount
        );

        // If reward is cero this shouldn't be invoked, but better avoid failing and breaking a batch operation
        if (reward <= 0) {
            return;
        }

        userStake.totalRewardedAcrossVersions += reward;
        userStake.totalRewardedSinceImport += reward;
        userStake.lastRewardedAmount = reward;
        userStake.startTime = block.timestamp;

        emit RewardRetrievedEvent(_player, reward);
        aliceToken.safeTransfer(_player, reward);
    }

    function _addPlayerStake(address _player, uint256 _amount) internal {
        if (0 >= _amount) {
            return;
        }

        // You can't import again for the same player UNLESS it's next version (season)
        UserStake memory userStake = stakes[_player];
        if (0 < userStake.stakedAmount && _isCurrentVersion(userStake.version)) {
            return;
        }

        userStake = UserStake(
            // This accumulates across versions/seasons
            userStake.totalRewardedAcrossVersions,
            // Reset total per version and last reward on this version
            0,
            0,
            _amount,
            block.timestamp,
            currentVersion
        );
        stakes[_player] = userStake;
        emit StakeAddedEvent(_player, _amount);
    }

    /*************************************/
    /* External player operations
    /*************************************/
    function seeMyReward() external view returns (uint256) {
        if (! _isCurrentVersion(stakes[msg.sender].version)) {
            return 0;
        }

        return
            _calculateRewardForUserStake(
                stakes[msg.sender].startTime,
                0,
                stakes[msg.sender].stakedAmount
            );
    }

    function seeMyFutureReward(uint256 endTime) external view returns (uint256) {
        return
            _calculateRewardForUserStake(
                stakes[msg.sender].startTime,
                endTime,
                stakes[msg.sender].stakedAmount
            );
    }

    function withdrawMyReward() external {
        _withdrawPlayerCollateral(msg.sender);
    }

    function getMyHistory() external view returns (UserStake memory) {
        if (! _isCurrentVersion(stakes[msg.sender].version)) {
            return UserStake(
                stakes[msg.sender].totalRewardedAcrossVersions,
                // Reset total per version and last reward on this version
                0, // rewards since import: there was no import for this version
                0, // last reward: there was no last reward for this version
                0, // staked amount: nothing for this version
                0, // start time: none for this version
                stakes[msg.sender].version
            );
        } else {
            return stakes[msg.sender];
        }
    }

    /*************************************/
    /* External admin only operations
    /*************************************/
    function addPlayerStakes(
        address[] calldata _players,
        uint256[] calldata _amounts,
        uint16 _currentVersion
    ) external override onlyAdmin {
        assert(_players.length == _amounts.length);
        require(_currentVersion >= currentVersion, 'Version can only change to a higher value');

        contractStoppedAt = 0;
        currentVersion = _currentVersion;
        for (uint i; i < _players.length; i++) {
            _addPlayerStake(_players[i], _amounts[i]);
        }

        lastImportTime = block.timestamp;
    }

    function withdrawRewardAsAdmin(address[] calldata _players) external override onlyAdmin {
        for (uint i; i < _players.length; i++) {
            _withdrawPlayerCollateral(_players[i]);
        }
    }

    function stopContract() external override onlyAdmin {
        contractStoppedAt = uint64(block.timestamp);
        emit StopEvent(currentVersion);
    }

    function addFundsToContract(uint256 _amount) external override onlyAdmin {
        aliceToken.safeTransferFrom(msg.sender, address(this), _amount);
    }

    function withdrawFunds() external override onlyAdmin {
        uint256 amount = aliceToken.balanceOf(address(this));
        aliceToken.safeTransfer(msg.sender, amount);
    }

    function updateApy(uint256 _newApy) external override onlyAdmin {
        APY = _newApy;
    }
}
"
    },
    "contracts/Collateral/Compound.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import './math.sol';

/*
 * Implementation of ICollateral
 */
contract Compound is DSMath {
    // Based on https://github.com/wolflo/solidity-interest-helper
    //// Fixed point scale factors
    // mikroAlice → smallest unit of Alice, the base unit (wei)
    // wad → 1 Alice → mikroAlice * 10 ** 6 of mikroAlice
    // ray → wad * 10 ** 9 → mikroAlice * 10 ** 15
    uint256 public constant alice = 10 ** 6;

    // Go from wad (10**6) to ray (10**15)
    function wadToRay(uint _wad) internal pure returns (uint) {
        return mul(_wad, 10 ** 9);
    }

    // Go from wei to ray (10**15)
    function weiToRay(uint _wei) internal pure returns (uint) {
        return mul(_wei, 10 ** 15);
    }

    /**
     * @dev Uses an approximation of continuously compounded interest
     * (discretely compounded every second)
     * @param _principal The principal to calculate the interest on.
     *   Accepted in wei.
     * @param _rate The interest rate. Accepted as a ray representing
     *   1 + the effective interest rate per second, compounded every
     *   second. As an example:
     *   I want to accrue interest at a nominal rate (i) of 5.0% per year
     *   compounded continuously. (Effective Annual Rate of 5.127%).
     *   This is approximately equal to 5.0% per year compounded every
     *   second (to 8 decimal places, if max precision is essential,
     *   calculate nominal interest per year compounded every second from
     *   your desired effective annual rate). Effective Rate Per Second =
     *   Nominal Rate Per Second compounded every second = Nominal Rate
     *   Per Year compounded every second * conversion factor from years
     *   to seconds
     *   Effective Rate Per Second = 0.05 / (365 days/yr * 86400 sec/day) = 1.5854895991882 * 10 ** -9
     *   The value we want to send this function is
     *   1 * 10 ** 15 + Effective Rate Per Second * 10 ** 15
     *   = 1000000001585489599188229325
     *   This will return 5.1271096334354555 Dai on a 100 Dai principal
     *   over the course of one year (31536000 seconds)
     * @param _age The time period over which to accrue interest. Accepted
     *   in seconds.
     * @return The new principal as a wad. Equal to original principal +
     *   interest accrued
     */
    function accrueInterest(uint _principal, uint _rate, uint _age) public pure returns (uint) {
        return rmul(_principal, rpow(_rate, _age));
    }

    /**
     * @dev Takes in the desired nominal interest rate per year, compounded
     *   every second (this is approximately equal to nominal interest rate
     *   per year compounded continuously). Returns the ray value expected
     *   by the accrueInterest function
     * @param _rateWad A wad of the desired nominal interest rate per year,
     *   compounded continuously. Converting from Alice to wei will effectively
     *   convert from a decimal value to a wad. So 5% rate = 0.05
     *   should be input as yearlyRateToRay( 0.05 Alice )
     * @return 1 * 10 ** 15 + Effective Interest Rate Per Second * 10 ** 15
     */
    function yearlyRateToRay(uint _rateWad) public pure returns (uint) {
        // 365 days in a year and 86400 seconds in a day
        return add(wadToRay(1 * alice), rdiv(wadToRay(_rateWad), weiToRay(365 * 86400)));
    }
}
"
    },
    "contracts/Collateral/interfaces/ICollateral.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

/*
* https://myneighboralice.atlassian.net/wiki/spaces/TECH/pages/262406145/Staking+Collateralized+ALICE
*
* Contract interface to store all the Alice staked by all the players. The operation for adding
* is admin only and batchable. There's a time per player of beginning of staking. And player
* entry (amount, start time...) is updated only when you reimport for a new version.
* Afterwards the reward can be retrieved by each player or by the admin on behalf of a
* batch of players.
* The amount for period (1 year) is given by APY, so if you staked 100 and APY is 20, your
* reward per year is 20, but in order to get this amount you should wait 1 year. Otherwise
* you get a proportional part. The admin can also decide to close this contract, after which
* the reward amount is calculated from the import till the stop (instead of till current time).
* NOTE: This contract affects only rewards. In said example, the player would still have
* her 100 Alice in game staked wich she can get back by selling her collateralized to Ivan.
*/
interface ICollateral {
    struct UserStake {
        uint256 totalRewardedAcrossVersions;
        uint256 totalRewardedSinceImport;
        uint256 lastRewardedAmount;
        uint256 stakedAmount;
        uint256 startTime;
        // Player stake is for current version. If it's not current one, it means
        // the player didn't get stake imported last time. Meaning user is reset.
        // The reason for this approach is mainly to avoid having to reset the
        // whole mapping before each import.
        uint16 version;
    }

    event StakeAddedEvent(address _player, uint256 _amount);
    event RewardRetrievedEvent(address _player, uint256 _amount);
    event StopEvent(uint16 _version);

    /*************************************/
    /* External player operations
    /*************************************/

    function seeMyReward() external view returns (uint256);

    function seeMyFutureReward(uint256 startTime) external view returns (uint256);

    function withdrawMyReward() external;

    function getMyHistory() external view returns (UserStake memory);

    /*************************************/
    /* External admin only operations
    /*************************************/

    function addPlayerStakes(
        address[] calldata _players,
        uint256[] calldata _amounts,
        uint16 _currentVersion
    ) external;

    function withdrawRewardAsAdmin(address[] calldata _players) external;

    function stopContract() external;

    function addFundsToContract(uint256 _amount) external;

    function withdrawFunds() external;

    function updateApy(uint256 _newApy) external;
}
"
    },
    "contracts/Collateral/math.sol": {
      "content": "// SPDX-License-Identifier: MIT
/// math.sol -- mixin for inline numerical wizardry

// This program is free software: you can redistribute it and/or modify
// it under the terms of the GNU General Public License as published by
// the Free Software Foundation, either version 3 of the License, or
// (at your option) any later version.

// This program is distributed in the hope that it will be useful,
// but WITHOUT ANY WARRANTY; without even the implied warranty of
// MERCHANTABILITY or FITNESS FOR A PARTICULAR PURPOSE.  See the
// GNU General Public License for more details.

// You should have received a copy of the GNU General Public License
// along with this program.  If not, see <http://www.gnu.org/licenses/>.

pragma solidity >0.4.13;

contract DSMath {
    function add(uint x, uint y) internal pure returns (uint z) {
        require((z = x + y) >= x, 'ds-math-add-overflow');
    }

    function sub(uint x, uint y) internal pure returns (uint z) {
        require((z = x - y) <= x, 'ds-math-sub-underflow');
    }

    function mul(uint x, uint y) internal pure returns (uint z) {
        require(y == 0 || (z = x * y) / y == x, 'ds-math-mul-overflow');
    }

    function min(uint x, uint y) internal pure returns (uint z) {
        return x <= y ? x : y;
    }

    function max(uint x, uint y) internal pure returns (uint z) {
        return x >= y ? x : y;
    }

    function imin(int x, int y) internal pure returns (int z) {
        return x <= y ? x : y;
    }

    function imax(int x, int y) internal pure returns (int z) {
        return x >= y ? x : y;
    }

    // Values modified from the original lib, due to the number of supported Alice decimals, which is 6
    uint constant WAD = 10 ** 6;
    uint constant RAY = 10 ** 15;

    //rounds to zero if x*y < WAD / 2
    function wmul(uint x, uint y) internal pure returns (uint z) {
        z = add(mul(x, y), WAD / 2) / WAD;
    }

    //rounds to zero if x*y < WAD / 2
    function rmul(uint x, uint y) internal pure returns (uint z) {
        z = add(mul(x, y), RAY / 2) / RAY;
    }

    //rounds to zero if x*y < WAD / 2
    function wdiv(uint x, uint y) internal pure returns (uint z) {
        z = add(mul(x, WAD), y / 2) / y;
    }

    //rounds to zero if x*y < RAY / 2
    function rdiv(uint x, uint y) internal pure returns (uint z) {
        z = add(mul(x, RAY), y / 2) / y;
    }

    // This famous algorithm is called "exponentiation by squaring"
    // and calculates x^n with x as fixed-point and n as regular unsigned.
    //
    // It's O(log n), instead of O(n) for naive repeated multiplication.
    //
    // These facts are why it works:
    //
    //  If n is even, then x^n = (x^2)^(n/2).
    //  If n is odd,  then x^n = x * x^(n-1),
    //   and applying the equation for even x gives
    //    x^n = x * (x^2)^((n-1) / 2).
    //
    //  Also, EVM division is flooring and
    //    floor[(n-1) / 2] = floor[n / 2].
    //
    function rpow(uint x, uint n) internal pure returns (uint z) {
        z = n % 2 != 0 ? x : RAY;

        for (n /= 2; n != 0; n /= 2) {
            x = rmul(x, x);

            if (n % 2 != 0) {
                z = rmul(z, x);
            }
        }
    }
