interface IPoint {
    event Mint(address indexed to, uint256 value);

    event Consume(address indexed consumer, address indexed from, uint256 value);

    event Approval(address indexed owner, address indexed spender, uint256 value);

    function gainOf(address account) external view returns (uint256);

    function balanceOf(address account) external view returns (uint256);

    function allowance(address owner, address spender) external view returns (uint256);

    function approve(address spender, uint256 amount) external returns (bool);

    function consume(address spender, uint256 amount) external;

    function mint(address to, uint256 amount) external;

    function name() external view returns (string memory);

    function symbol() external view returns (string memory);

    function decimals() external view returns (uint8);
}

interface IPancakePair {
    event Approval(address indexed owner, address indexed spender, uint256 value);
    event Transfer(address indexed from, address indexed to, uint256 value);

    function name() external pure returns (string memory);

    function symbol() external pure returns (string memory);

    function decimals() external pure returns (uint8);

    function totalSupply() external view returns (uint256);

    function balanceOf(address owner) external view returns (uint256);

    function allowance(address owner, address spender) external view returns (uint256);

    function approve(address spender, uint256 value) external returns (bool);

    function transfer(address to, uint256 value) external returns (bool);

    function transferFrom(
        address from,
        address to,
        uint256 value
    ) external returns (bool);

    function DOMAIN_SEPARATOR() external view returns (bytes32);

    function PERMIT_TYPEHASH() external pure returns (bytes32);

    function nonces(address owner) external view returns (uint256);

    function permit(
        address owner,
        address spender,
        uint256 value,
        uint256 deadline,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) external;

    event Mint(address indexed sender, uint256 amount0, uint256 amount1);
    event Burn(address indexed sender, uint256 amount0, uint256 amount1, address indexed to);
    event Swap(address indexed sender, uint256 amount0In, uint256 amount1In, uint256 amount0Out, uint256 amount1Out, address indexed to);
    event Sync(uint112 reserve0, uint112 reserve1);

    function MINIMUM_LIQUIDITY() external pure returns (uint256);

    function factory() external view returns (address);

    function token0() external view returns (address);

    function token1() external view returns (address);

    function getReserves()
        external
        view
        returns (
            uint112 reserve0,
            uint112 reserve1,
            uint32 blockTimestampLast
        );

    function price0CumulativeLast() external view returns (uint256);

    function price1CumulativeLast() external view returns (uint256);

    function kLast() external view returns (uint256);

    function mint(address to) external returns (uint256 liquidity);

    function burn(address to) external returns (uint256 amount0, uint256 amount1);

    function swap(
        uint256 amount0Out,
        uint256 amount1Out,
        address to,
        bytes calldata data
    ) external;

    function skim(address to) external;

    function sync() external;

    function initialize(address, address) external;
}

interface IUniswapV2RouterPartial {
    function swapExactTokensForTokens(
        uint256 amountIn,
        uint256 amountOutMin,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external returns (uint256[] memory amounts);

    function swapTokensForExactTokens(
        uint256 amountOut,
        uint256 amountInMax,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external returns (uint256[] memory amounts);

    function swapExactETHForTokens(
        uint256 amountOutMin,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external payable returns (uint256[] memory amounts);

    function swapETHForExactTokens(
        uint256 amountOut,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external payable returns (uint256[] memory amounts);

    function getAmountOut(
        uint256 amountIn,
        uint256 reserveIn,
        uint256 reserveOut
    ) external view returns (uint256 amountOut);

    function getAmountsOut(uint256 amountIn, address[] memory path) external view returns (uint256[] memory amounts);
}

contract TTCSwapProxy is Ownable, Proxy {
    using Address for address;

    event PointMinted(address indexed to, uint256 ttcAmoutOut, uint256 USDEquals, uint256 pointOut);
    event PointContractChanged(address indexed operator, address ttPoint);
    event PointRateChanged(address indexed operator, uint256 pointsPerEther);

    address public immutable factory;
    address public immutable WETH;

    //address of the uniswap v2 router
    address public constant PANCAKE_V2_ROUTER = 0x10ED43C718714eb63d5aA57B78B54704E256024E;
    address public constant TTC = 0x6A684b3578f5B07c0Aa02fAFc33ED248AE0c2dB2;
    address public constant USDT = 0x55d398326f99059fF775485246999027B3197955;
    address private constant ZERO_ADDRESS = address(0x0);
    uint256 private constant ONE_ETHER = 1 ether;

    address public TTPoint;
    uint256 public pointsPerUSD = 1 ether;

    constructor(address factory_, address weth_) {
        factory = factory_;
        WETH = weth_;
    }

    function _implementation() internal view virtual override returns (address) {
        return PANCAKE_V2_ROUTER;
    }

    // set percentage of tx fee
    function setPointsPerUSD(uint256 pointsPerUSD_) external onlyOwner {
        pointsPerUSD = pointsPerUSD_;
        emit PointRateChanged(msg.sender, pointsPerUSD_);
    }

    function setPointContract(address ttPoint_) external onlyOwner {
        TTPoint = ttPoint_;
        emit PointContractChanged(msg.sender, ttPoint_);
    }

    function swapExactTokensForTokens(
        uint256 amountIn,
        uint256 amountOutMin,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external returns (uint256[] memory amounts) {
        bytes memory result = _implementation().functionDelegateCall(abi.encodeWithSelector(TTCSwapProxy.swapExactTokensForTokens.selector, amountIn, amountOutMin, path, to, deadline));
        amounts = abi.decode(result, (uint256[]));
        _checkAndMintPoints(path, amounts);
    }

    function swapTokensForExactTokens(
        uint256 amountOut,
        uint256 amountInMax,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external returns (uint256[] memory amounts) {
        bytes memory result = _implementation().functionDelegateCall(abi.encodeWithSelector(TTCSwapProxy.swapTokensForExactTokens.selector, amountOut, amountInMax, path, to, deadline));
        amounts = abi.decode(result, (uint256[]));
        _checkAndMintPoints(path, amounts);
    }

    function swapExactETHForTokens(
        uint256 amountOutMin,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external payable returns (uint256[] memory amounts) {
        bytes memory result = _implementation().functionDelegateCall(abi.encodeWithSelector(TTCSwapProxy.swapExactETHForTokens.selector, amountOutMin, path, to, deadline));
        amounts = abi.decode(result, (uint256[]));
        _checkAndMintPoints(path, amounts);
    }

    function swapETHForExactTokens(
        uint256 amountOut,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external payable returns (uint256[] memory amounts) {
        bytes memory result = _implementation().functionDelegateCall(abi.encodeWithSelector(TTCSwapProxy.swapETHForExactTokens.selector, amountOut, path, to, deadline));
        amounts = abi.decode(result, (uint256[]));
        _checkAndMintPoints(path, amounts);
    }

    function _checkAndMintPoints(address[] calldata path, uint256[] memory amounts) private {
        if (path[path.length - 1] == TTC) {
            uint256 ttcAmountOut = amounts[path.length - 1];
            address[] memory usdPath = new address[](3);
            usdPath[0] = TTC;
            usdPath[1] = WETH;
            usdPath[2] = USDT;
            uint256[] memory USDPathAmount = abi.decode(
                _implementation().functionStaticCall(abi.encodeWithSelector(IUniswapV2RouterPartial.getAmountsOut.selector, ttcAmountOut, usdPath)),
                (uint256[])
            );
            uint256 pointAmounts = (USDPathAmount[2] * pointsPerUSD) / ONE_ETHER;
            if (TTPoint != ZERO_ADDRESS) IPoint(TTPoint).mint(msg.sender, pointAmounts);
            emit PointMinted(msg.sender, ttcAmountOut, USDPathAmount[2], pointAmounts);
        }
    }

    function sortTokens(address tokenA, address tokenB) internal pure returns (address token0, address token1) {
        require(tokenA != tokenB, "PancakeLibrary: IDENTICAL_ADDRESSES");
        (token0, token1) = tokenA < tokenB ? (tokenA, tokenB) : (tokenB, tokenA);
        require(token0 != address(0), "PancakeLibrary: ZERO_ADDRESS");
    }

    function pairFor(address tokenA, address tokenB) public view returns (address pair) {
        (address token0, address token1) = sortTokens(tokenA, tokenB);
        bytes32 hashCode = keccak256(
            abi.encodePacked(
                hex"ff",
                factory,
                keccak256(abi.encodePacked(token0, token1)),
                hex"00fb7f630766e6a796048ea87d01acd3068e8ff67d078148a3fa3f4a84f69bd5" // init code hash
            )
        );
        pair = address(uint160(uint256(hashCode)));
    }

    function getReserves(address tokenA, address tokenB) public view returns (uint256 reserveA, uint256 reserveB) {
        (address token0, ) = sortTokens(tokenA, tokenB);
        pairFor(tokenA, tokenB);
        (uint256 reserve0, uint256 reserve1, ) = IPancakePair(pairFor(tokenA, tokenB)).getReserves();
        (reserveA, reserveB) = tokenA == token0 ? (reserve0, reserve1) : (reserve1, reserve0);
    }

    function impactOf(
        uint256 amountIn,
        address tokenA,
        address tokenB
    ) external view returns (uint256 impact) {
        (uint256 reserveA, uint256 reserveB) = getReserves(tokenA, tokenB);
        uint256 amountOut = abi.decode(_implementation().functionStaticCall(abi.encodeWithSelector(IUniswapV2RouterPartial.getAmountOut.selector, amountIn, reserveA, reserveB)), (uint256));
        impact = (amountIn * reserveB * 10000) / (amountOut * reserveA) - 10000;
    }
}
