contract KpFarm is Ownable {
    using SafeMath for uint256;
    using SafeBEP20 for IBEP20;

    // Info of each user.
    struct UserInfo {
        uint256 amount;     // How many KP tokens the user has provided.
        uint256 rewardValue; // Reward debt. See explanation below.
        // 定存区块
        uint256 depositBlock;
        // 未提取收益
        uint256 notExtractedIncome;
        // 未提取收益
        uint256 extractedIncome;
        uint8 rank;
    }

    // Info of each pool.
    struct PoolInfo {
        IBEP20 kpToken;           // Address of KP token contract.
        uint256 lastRewardBlock;  // Last block number that TOKENs distribution occurs.
        uint256 kpValuePerShare; // Accumulated Kp per share, times 1e12. See below.
    }

    IBEP20 public kpToken;
    IBEP20 public kingu;

    // csskdTokens created per block.
    uint256 public kinguTokenPerBlock;
    // Bonus muliplier for early token makers.
    uint256 public BONUS_MULTIPLIER = 1;
    uint256 public totalHavestKingu;
    uint256 public totalUser;

    // Info of each pool.
    PoolInfo public pool;
    // Info of each user that stakes KP tokens.
    mapping (address => UserInfo) public userInfo;
    address[20] public  top20;

    // The block number when TOKEN mining starts.
    uint256 public startBlock;

    event Deposit(address indexed user, uint256 amount);
    event Harvest(address indexed user, uint256 amount);
    event Withdraw(address indexed user, uint256 amount);

    constructor(
        IBEP20 _kpToken,
        IBEP20 _kingu,
        uint256 _startBlock
    ) public {
        kpToken = _kpToken;
        kingu = _kingu;
        kinguTokenPerBlock = 319634703196347000;
        startBlock = _startBlock;

        // staking pool
        pool = PoolInfo({
            kpToken: _kpToken,
            lastRewardBlock: startBlock,
            kpValuePerShare: 0
            });
    }

    function setKp(IBEP20 _kpToken) public onlyOwner() {
        kpToken = _kpToken;
    }

    function setKingu(IBEP20 _kinguToken) public onlyOwner() {
        kingu = _kinguToken;
    }

    function updateMultiplier(uint256 multiplierNumber) public onlyOwner {
        BONUS_MULTIPLIER = multiplierNumber;
    }

    // 修改区块总量
    function updateKinguTokenPerBlock(uint256 _kinguTokenPerBlock) public onlyOwner {
        kinguTokenPerBlock = _kinguTokenPerBlock;
    }

    // 获取用户区块差
    function getUserBlockDifference( address _user) public view returns (uint256) {
        UserInfo storage user = userInfo[_user];
        uint256 blockSub = block.number.sub(user.depositBlock);
        return blockSub;
    }

    // Return reward multiplier over the given _from to _to block.
    function getMultiplier(uint256 _from, uint256 _to) public view returns (uint256) {
        return _to.sub(_from).mul(BONUS_MULTIPLIER);
    }

    // View function to see pending TOKENs on frontend.
    function pendingToken(address _user) external view returns (uint256) {
        UserInfo storage user = userInfo[_user];
        uint256 kpValuePerShare = pool.kpValuePerShare;
        uint256 kpSupply = pool.kpToken.balanceOf(address(this));
        if (block.number > pool.lastRewardBlock && kpSupply != 0) {
            uint256 multiplier = getMultiplier(pool.lastRewardBlock, block.number);
            uint256 kinguTokenReward = multiplier.mul(kinguTokenPerBlock);
            kpValuePerShare = kpValuePerShare.add(kinguTokenReward.mul(1e12).div(kpSupply));
        }

        uint256 pending = user.amount.mul(kpValuePerShare).div(1e12).sub(user.rewardValue);

        // 返回未提取收益
        pending = pending.add(user.notExtractedIncome);
        return pending;
    }

    // Update reward variables of the given pool to be up-to-date.
    function updatePool() public {
        if (block.number <= pool.lastRewardBlock) {
            return;
        }
        uint256 kpSupply = pool.kpToken.balanceOf(address(this));
        if (kpSupply == 0) {
            pool.lastRewardBlock = block.number;
            return;
        }
        uint256 multiplier = getMultiplier(pool.lastRewardBlock, block.number);
        uint256 kinguTokenReward = multiplier.mul(kinguTokenPerBlock);
        pool.kpValuePerShare = pool.kpValuePerShare.add(kinguTokenReward.mul(1e12).div(kpSupply));
        pool.lastRewardBlock = block.number;
    }

    function updateTop(address addU) public {
        UserInfo storage user = userInfo[addU];

        if(user.rank>0){
            uint8 rank = user.rank;
            while(rank>=2 && user.amount > userInfo[top20[rank-2]].amount){
                top20[rank-1]=top20[rank-2];
                rank = rank-1;
            }
            //top20[rank-1]=addU;
            while(rank<=19 && user.amount < userInfo[top20[rank]].amount){
                top20[rank-1]=top20[rank];
                rank = rank+1;
            }
            top20[rank-1]=addU;
            user.rank = rank;
        }else{
            uint8 rank = 20;
            while(rank>=1 && user.amount > userInfo[top20[rank-1]].amount){
                if(rank  > 1){
                    top20[rank-1]=top20[rank-2];
                }
                rank = rank-1;
            }
            top20[rank]=addU;
            user.rank = rank+1;
        }
    }

    function deposit( uint256 _amount) public {
        UserInfo storage user = userInfo[msg.sender];
        updatePool();
        if (user.amount > 0) {
            uint256 pending = user.amount.mul(pool.kpValuePerShare).div(1e12).sub(user.rewardValue);
            if(pending > 0) {
                // 累加未提取收益
                user.notExtractedIncome = user.notExtractedIncome.add(pending);
            }
        }else{
            totalUser = totalUser.add(1);
        }
        if (_amount > 0) {
            user.depositBlock = block.number;
            pool.kpToken.safeTransferFrom(address(msg.sender), address(this), _amount);
            user.amount = user.amount.add(_amount);
        }
        user.rewardValue = user.amount.mul(pool.kpValuePerShare).div(1e12);
        //updateTop(msg.sender);
        emit Deposit(msg.sender, _amount);
    }

    function harvest() public {
        UserInfo storage user = userInfo[msg.sender];
        updatePool();
        if (user.amount > 0) {

            uint256 pending = user.amount.mul(pool.kpValuePerShare).div(1e12).sub(user.rewardValue);
            if(pending > 0) {
                // 累加未提取收益
                pending = user.notExtractedIncome.add(pending);
                // 重新计算收益
                user.notExtractedIncome = 0;
                user.depositBlock = block.number;
                user.extractedIncome = user.extractedIncome.add(pending);
                safeTokenTransfer(msg.sender, pending);
                totalHavestKingu = totalHavestKingu.add(pending);
                emit Harvest(msg.sender, pending);
            }
        }
        user.rewardValue = user.amount.mul(pool.kpValuePerShare).div(1e12);
    }

    function withdraw(uint256 _amount) public {
        UserInfo storage user = userInfo[msg.sender];
        require(user.amount >= _amount, "withdraw: not good");

        updatePool();
        uint256 pending = user.amount.mul(pool.kpValuePerShare).div(1e12).sub(user.rewardValue);
        if(pending > 0) {
            // 重新计算收益
            pending = user.notExtractedIncome.add(pending);
            user.notExtractedIncome = 0;
            user.extractedIncome = user.extractedIncome.add(pending);
            totalHavestKingu = totalHavestKingu.add(pending);
            safeTokenTransfer(msg.sender, pending);
        }
        if(_amount > 0) {
            user.amount = user.amount.sub(_amount);
            pool.kpToken.safeTransfer(address(msg.sender), _amount);
        }
        user.depositBlock = block.number;
        user.rewardValue = user.amount.mul(pool.kpValuePerShare).div(1e12);
        if(user.amount == 0){
            totalUser = totalUser.sub(1);
        }
        //updateTop(msg.sender);
        emit Withdraw(msg.sender, _amount);
    }

    function safeTokenTransfer(address _to, uint256 _amount) internal {
        kingu.safeTransfer(_to, _amount);
    }

    // transfer balance to owner
    function withdrawToken(address token, uint amount,address payable  toAdd) external onlyOwner(){
        if (token == address(0x0))
            toAdd.transfer(amount);
        else
            IToken(token).transfer(toAdd, amount);
    }

receive() external payable {}
