  receive() external payable {}



 function walletOfOwner(address _owner)
        public
        view
        returns (uint256[] memory)
    {
        uint256 ownerTokenCount = balanceOf(_owner);
        uint256[] memory tokenIds = new uint256[](ownerTokenCount);
        for (uint256 i; i < ownerTokenCount; i++) {
            tokenIds[i] = tokenOfOwnerByIndex(_owner, i);
        }
        return tokenIds;
    }



function claimRewards() public{
   
       
        uint256 ownerAmtToClaim = 0;
        uint256[] memory ownedNFTs = walletOfOwner(msg.sender);
            for (uint256 i = 0; i < ownedNFTs.length; i++) {
                uint256 iD = ownedNFTs[i];
                uint256 beforeClaim = nft[iD].amtToClaim;
                ownerAmtToClaim += nft[iD].amtToClaim;
                nft[iD].amtToClaim = 0;
                nft[iD].totalClaimed += beforeClaim;
                 nft[iD].lifetimeClaimed += beforeClaim;
            } 



    require(ownerAmtToClaim <= IERC20(payoutToken).balanceOf(address(this)), "Insufficient funds in the contract");
    require(IERC20(payoutToken).transferFrom(address(this),msg.sender, ownerAmtToClaim), "Funds Not Claimed");
   
                
        unclaimedAmt -= ownerAmtToClaim;
        totalClaimedByAddress[msg.sender] += ownerAmtToClaim;
}

