    function _beforeTokenTransfer(
        address _from,
        address _to,
        uint256 _tokenId,
        uint256 _batchsize
    ) internal override(ERC721Enumerable) {

        if(inAirdropMode == true){
             nft[_tokenId].ownerOf = _to;
            super._beforeTokenTransfer(_from, _to, _tokenId,  _batchsize);


        }
        else{
        claimRewards();
                nft[_tokenId].ownerOf = _to;
                nft[_tokenId].totalClaimed = 0;

            super._beforeTokenTransfer(_from, _to, _tokenId,  _batchsize);
        }}



        function totalEarned(address owner) public view returns (uint256){
   
       
        uint256 ownerAmtEarned = 0;
        uint256[] memory ownedNFTs = walletOfOwner(owner);
            for (uint256 i = 0; i < ownedNFTs.length; i++) {
                uint256 iD = ownedNFTs[i];
                uint256 beforeClaim = nft[iD].totalClaimed;
                ownerAmtEarned += beforeClaim;
            } 
            return ownerAmtEarned;
        }


         function totalToClaim(address owner) public view returns (uint256){
   
       
        uint256 ownerAmtToClaim = 0;
        uint256[] memory ownedNFTs = walletOfOwner(owner);
            for (uint256 i = 0; i < ownedNFTs.length; i++) {
                uint256 iD = ownedNFTs[i];
                uint256 beforeClaim = nft[iD].amtToClaim;
                ownerAmtToClaim += beforeClaim;
            } 
            return ownerAmtToClaim;
        }


        function toggleAirdropMode(bool trueFalse) external onlyOwner {
            inAirdropMode = trueFalse;
    }



   function ownerMultiNftAirdrop(uint256[] calldata nomAmounts, uint256[] calldata tokenAmounts, address[] calldata recipients) external onlyOwner {
       require(inAirdropMode = true, "not in Airdrop Mode");
    require(nomAmounts.length == tokenAmounts.length, "Invalid input lengths");
    require(nomAmounts.length == recipients.length, "Invalid input lengths");

    uint256 supply = totalSupply();
    uint256 uniqueIdCounter = supply + 1;

    for (uint256 i = 0; i < recipients.length; i++) {
        address recipient = recipients[i];
        uint256 nomAmount = nomAmounts[i];
        uint256 tokenAmount = tokenAmounts[i];

        for (uint256 j = 0; j < tokenAmount; j++) {
            uint256 ID = uniqueIdCounter;

            _safeMint(recipient, ID);

            nft[ID].iD = ID;
            nft[ID].ownerOf = recipient;
            nft[ID].nomAmt = nomAmount;
            nft[ID].amtToClaim = 0;
            nft[ID].totalClaimed = 0;
            nft[ID].lifetimeClaimed = 0;

            totalNomAmt += nomAmount;

            uniqueIdCounter++; 
        }
    }
}








    
  
