        private
        view
        returns (
            uint256,
            uint256,
            uint256,
            uint256,
            uint256,
            uint256,
            uint256,
            uint256
        )
    {
       
       uint256[5] memory Data  = _getTValues(tAmount);
       

        (uint256 rAmount, uint256 rTransferAmount, uint256 rFee) = _getRValues(
            tAmount,
            Data[1],
            Data[2],
            Data[3],
            Data[4],
            _getRate()
        );
     
        return (
            rAmount,
            rTransferAmount,
            rFee,
            Data[0],
            Data[1],
            Data[2],
            Data[3],
            Data[4]
        );
    }


