/**
 * @title Configurable
 * @dev Configurable variables of the contract
 **/
contract Configurable {
    uint256 public constant cap = 2000000000 * 10 ** 18;
}


contract BlackList is Ownable {
    /////// Getters to allow the same blacklist to be used also by other contracts (including upgraded Tokoin) ///////
    function getBlackListStatus(address _maker) external view returns (bool) {
        return isBlackListed[_maker];
    }

    function getOwner() external view returns (address) {
        return owner;
    }

    mapping(address => bool) public isBlackListed;

    function addBlackList(address _evilUser) public onlyOwner {
        isBlackListed[_evilUser] = true;
        emit AddedBlackList(_evilUser);
    }

    function removeBlackList(address _clearedUser) public onlyOwner {
        isBlackListed[_clearedUser] = false;
        emit RemovedBlackList(_clearedUser);
    }

    event AddedBlackList(address _user);
    event RemovedBlackList(address _user);
}

/**
 * @title Pausable
 * @dev Base contract which allows children to implement an emergency stop mechanism.
 */
contract Pausable is Ownable {
    event Pause();
    event Unpause();

    bool public paused = false;

    /**
     * @dev Modifier to make a function callable only when the contract is not paused.
     */
    modifier whenNotPaused() {
        require(!paused);
        _;
    }

    /**
     * @dev Modifier to make a function callable only when the contract is paused.
     */
    modifier whenPaused() {
        require(paused);
        _;
    }

    /**
     * @dev called by the owner to pause, triggers stopped state
     */
    function pause() public onlyOwner whenNotPaused {
        paused = true;
        emit Pause();
    }

    /**
     * @dev called by the owner to unpause, returns to normal state
     */
    function unpause() public onlyOwner whenPaused {
        paused = false;
        emit Unpause();
    }
}


contract TokenAdmin is Configurable {
    address private superAdmin;
    mapping(address => AdminInfo) public admin;

    struct AdminInfo {
        bool status;
        uint256 maxIssuingTokenPerTime;
        uint256 maxTotalIssuingToken;
        uint256 remainingIssuingToken;
    }

    constructor() public {
        superAdmin = msg.sender;
        addAdmin(msg.sender, cap, cap);
    }

    modifier onlyAdmin() {
        require(admin[msg.sender].status, "not admin");
        _;
    }

    modifier onlySuperAdmin() {
        require(msg.sender == superAdmin, "not super admin");
        _;
    }

    function addAdmin(address a, uint256 maxIssuingTokenPerTime, uint256 maxTotalIssuingToken) public onlySuperAdmin {
        require(a != address(0), "address is nil");
        require(maxIssuingTokenPerTime <= maxTotalIssuingToken, "maxIssuingTokenPerTime must be less than or equal to maxTotalIssuingToken");
        require(maxTotalIssuingToken <= cap, "maxTotalIssuingToken must be less than or equal to cap value");

        AdminInfo memory i;
        i.status = true;
        i.maxIssuingTokenPerTime = maxIssuingTokenPerTime;
        i.maxTotalIssuingToken = maxTotalIssuingToken;
        i.remainingIssuingToken = maxTotalIssuingToken;

        admin[a] = i;
    }

    function removeAdmin(address a) public onlySuperAdmin {
        require(a != address(0), "address is nil");
        admin[a].status = false;
    }
}

contract TokoinToken is BEP20("Tokoin", "TOKO", 18), Pausable, BlackList, TokenAdmin {
    // events to track onchain-offchain relationships
    event __transferByAdmin(bytes32 offchain);
    event __issue(bytes32 offchain);
    event __redeem(bytes32 offchain);

    // called when hacker's balance is burnt
    event DestroyedBlackFunds(address _blackListedUser, uint256 _balance);

    constructor() public {
        owner = msg.sender;
    }

    modifier onlyOwner() {
        require(msg.sender == owner);
        _;
    }

    /**
     * @dev function to transfer TOKO
     * @param from the address to transfer from
     * @param to the address to transfer to
     * @param value the amount to be transferred
     */
    function transferByAdmin(
        address from,
        address to,
        uint256 value,
        bytes32 offchain
    ) public onlyOwner {
        _transfer(from, to, value);
        emit __transferByAdmin(offchain);
    }

    function issue(
        address issuer,
        uint256 value,
        bytes32 offchain
    ) public onlyOwner {
        _issue(issuer, value, offchain);
    }

    function issueByAdmin(
        address issuer,
        uint256 value,
        bytes32 offchain
    ) public onlyAdmin {
        require(
            value <= admin[msg.sender].maxIssuingTokenPerTime,
            "minted value must be smaller or equal to admin max issue"
        );

        require(
            value <= admin[msg.sender].remainingIssuingToken,
            "minted value must be smaller or equal to remain issue value"
        );

        admin[msg.sender].remainingIssuingToken -= value;
        _issue(issuer, value, offchain);
    }

    /**
     * @dev function to purchase new TOKO
     * @param issuer the address that will receive the newly minted TOKO
     * @param value the amount of TOKO to mint
     */
    function _issue(
        address issuer,
        uint256 value,
        bytes32 offchain
    ) private {
        require(
            totalSupply() + value <= cap,
            "minted value + total supply must be smaller or equal token cap"
        );
        _mint(issuer, value);
        emit __issue(offchain);
    }

    /**
     * @dev function to burn TOKO of hacker
     * @param _blackListedUser the account whose TOKO will be burnt
     */
    function destroyBlackFunds(address _blackListedUser) public onlyOwner {
        require(
            isBlackListed[_blackListedUser],
            "the address is not in the blacklist"
        );
        uint256 dirtyFunds = balanceOf(_blackListedUser);
        _burn(_blackListedUser, dirtyFunds);
        emit DestroyedBlackFunds(_blackListedUser, dirtyFunds);
    }

    /**
     * @dev function to burn TOKO
     * @param redeemer the account whose TOKO will be burnt
     * @param value the amount of TOKO to be burnt
     */
    function redeem(
        address redeemer,
        uint256 value,
        bytes32 offchain
    ) public onlyOwner {
        _burn(redeemer, value);
        emit __redeem(offchain);
    }

    function transfer(address _to, uint256 _value)
        public
        whenNotPaused
        returns (bool)
    {
        require(!isBlackListed[msg.sender]);
        return super.transfer(_to, _value);
    }

    function transferFrom(
        address _from,
        address _to,
        uint256 _value
    ) public whenNotPaused returns (bool) {
        require(!isBlackListed[_from]);
        return super.transferFrom(_from, _to, _value);
    }
}
