pragma solidity =0.8.18;

import "./IStakingPlatform.sol";
import "./Ownable.sol";
import "./IERC20.sol";
import "./SafeERC20.sol";
import "./ReentrancyGuard.sol";

interface ILaunchpadWhitelist {

    function isWhitelisted(address account)
        external
        view
        returns (bool isWhitelisted_);

    function whitelistedAmountFor(address account)
        external
        view
        returns (uint256 whitelistedAmount);
}

contract FlightSilverNode is IStakingPlatform, Ownable, ReentrancyGuard {
    using SafeERC20 for IERC20;


    ILaunchpadWhitelist public LaunchpadWhitelist;

    IERC20 public immutable token;

    uint public immutable stakingDuration;
    uint public immutable stakingMax;

    uint public startPeriod;
    uint public lockupPeriod;
    uint public endStake;

    address private _stakeAdmin;

    uint private _totalStaked;
    uint256 public totalStakedUsers = 0;


    struct Deposits {
        uint8 poolId;
        uint256 amount;
        uint256 startStake;
        uint256 latestClaim;
        uint256 endStake;
        uint256 vestingDate;
        uint period;
    }

    struct Pool {
        uint8 year;
        uint8 apy;
        uint8 vestingPeriod;
        uint256 LockupDuration;
        uint256 UserStakingMax;
        uint256 totalDeposit;
    }

    mapping(uint8 => Pool) private _pools;
    mapping(address => Deposits) private _deposits;
    mapping(address => bool) public _hasStaked;
    mapping(address => uint) private _userStartTime;


    constructor(
        address _token,
        ILaunchpadWhitelist _whitelistAddress,
        uint _durationInDays,
        uint _maxAmountStaked
    ) {
        stakingDuration = _durationInDays * 1 days; //STAKE TIME
        token = IERC20(_token);
        _stakeAdmin = _msgSender();
        LaunchpadWhitelist = _whitelistAddress;
        stakingMax = _maxAmountStaked;
    }


    function startStaking() external override onlyOwner {
        require(startPeriod == 0, "Staking has already started");
        startPeriod = block.timestamp;
        endStake = block.timestamp + stakingDuration;
        emit StartStaking(startPeriod, endStake);
    }

    function setPool(uint8 poolId,uint8 poolYear,uint8 poolApy,
        uint8 poolVestingperiod,uint256 poolsLockupDuration,
            uint256 poolUserStakingMax)  external onlyOwner {

        require(startPeriod == 0, "Staking has started");
        require(poolId > 0,"PoolId not zero");
        require(poolYear > 0,"poolYear not zero");
        require(poolApy > 0,"poolApy not zero");
        require(poolVestingperiod > 0,"poolVestingperiod not zero");
        require(poolsLockupDuration > 0,"poolsLockupDuration not zero");
        require(poolUserStakingMax > 0,"userStakingMax not zero");

        _pools[poolId].year = poolYear;
        _pools[poolId].apy = poolApy;
        _pools[poolId].vestingPeriod = poolVestingperiod;
        _pools[poolId].LockupDuration = poolsLockupDuration * 1 days;
        _pools[poolId].UserStakingMax = poolUserStakingMax;

        emit SetPool(poolYear, poolApy, poolVestingperiod, (poolsLockupDuration * 1 days), poolUserStakingMax);

    }

    function addDeposit(address from,uint256 amount, uint32 userRemaningDays, uint8 poolId) external override {
        require(_stakeAdmin == _msgSender(), "Unauthorized action");

        require(
            (endStake == 0) || endStake > block.timestamp,
            "Staking period ended"
        );

        require(
            _totalStaked + amount <= stakingMax,
            "Amount staked exceeds MaxStake"
        );


        uint256 contract_balance = token.balanceOf(address(this));

        require((_totalStaked + amount) <= contract_balance, "Not enought staked amount");

        require(userRemaningDays > 0, "userStartTime must be greater than 0");

        require((userRemaningDays * 1 days) <= _pools[poolId].LockupDuration, "Remaining days are big");

        require(amount > 0, "Amount must be greater than 0");

        require(LaunchpadWhitelist.isWhitelisted(from), "User not whitelist");

        require(_deposits[from].amount + amount <= LaunchpadWhitelist.whitelistedAmountFor(from), "Amount staked exceeds Whitelist User");

        require(_deposits[from].amount + amount <= _pools[poolId].UserStakingMax, "Amount staked exceeds UserstakingMax");

        if (_hasStaked[from])
        {
            require(_deposits[from].poolId == poolId,"userPoolId is not valid");
            _deposits[from].amount = _deposits[from].amount + amount;

        }else{
            _deposits[from] = Deposits(
                poolId,
                amount,
                block.timestamp,
                block.timestamp,
                block.timestamp + (userRemaningDays * 1 days),
                block.timestamp + (userRemaningDays * 1 days),
                _pools[poolId].vestingPeriod
            );

            _hasStaked[from] = true;
            totalStakedUsers += 1;
        }

        _totalStaked = _totalStaked + amount;
        _pools[poolId].totalDeposit = _pools[poolId].totalDeposit + amount;
    }


    function withdraw() external nonReentrant override   {
        require(block.timestamp > 0, "Block time is zero");

        require(_hasStaked[_msgSender()],"User not staked");

        require(
            _deposits[_msgSender()].vestingDate <= block.timestamp,
            "No withdraw until lockup ends"
        );

        require(_deposits[_msgSender()].amount > 0, "Amount must be greater than 0");


        uint256 vestingAmount = _deposits[_msgSender()].amount /  _deposits[_msgSender()].period;

        _deposits[_msgSender()].vestingDate = block.timestamp + (30 * 1 days);
        _deposits[_msgSender()].latestClaim = _deposits[_msgSender()].endStake;
        _deposits[_msgSender()].period--;

        _totalStaked = _totalStaked - vestingAmount;
        _deposits[_msgSender()].amount = _deposits[_msgSender()].amount - vestingAmount;
        _pools[_deposits[_msgSender()].poolId].totalDeposit = _pools[_deposits[_msgSender()].poolId].totalDeposit - vestingAmount;

        if (_deposits[_msgSender()].period == 0)
        {
            _deposits[_msgSender()].startStake = 0;
            _deposits[_msgSender()].amount = 0;
            _hasStaked[_msgSender()] = false;
            totalStakedUsers = totalStakedUsers - 1;
        }


        token.safeTransfer(_msgSender(), vestingAmount);

        emit Withdraw(_msgSender(), vestingAmount);
    }

    function withdrawResidualBalance(address from) external nonReentrant onlyOwner {
        require(
            block.timestamp >= _deposits[from].vestingDate + (365 * 1 days),
            "Withdraw 1year after User endStake"
        );

        uint256 balance = _deposits[from].amount;
        require(balance > 0, "No residual Balance to withdraw");

        _deposits[from].startStake = 0;
        _deposits[from].amount = 0;
        _hasStaked[from] = false;
        totalStakedUsers -= 1;
        _totalStaked = _totalStaked - balance;
        _pools[_deposits[from].poolId].totalDeposit -= balance;

        token.safeTransfer(owner(), balance);
    }

    function userDeposits(address stakeHolder)
        external
        view
        returns (
            uint8,
            uint256,
            uint256,
            uint256,
            uint256,
            uint256,
            uint
        )
    {
        if (_hasStaked[stakeHolder]) {
            return (
                _deposits[stakeHolder].poolId,
                _deposits[stakeHolder].amount,
                _deposits[stakeHolder].startStake,
                _deposits[stakeHolder].latestClaim,
                _deposits[stakeHolder].endStake,
                _deposits[stakeHolder].vestingDate,
                _deposits[stakeHolder].period
            );
        } else {
            return (0,0, 0, 0, 0, 0,0);
        }
    }

    function poolInfo(uint8 poolId)
        external
        view
        returns (
            uint8,
            uint8,
            uint8,
            uint256,
            uint256,
            uint256
        )
    {
        if (_pools[poolId].year > 0) {
            return (
                _pools[poolId].year,
                _pools[poolId].apy,
                _pools[poolId].vestingPeriod,
                _pools[poolId].LockupDuration,
                _pools[poolId].UserStakingMax,
                _pools[poolId].totalDeposit
            );
        } else {
            return (0, 0, 0, 0, 0, 0);
        }
    }

    function amountStaked(address stakeHolder)
        external
        view
        override
        returns (uint)
    {
        return _deposits[stakeHolder].amount;
    }

    function totalDeposited() external view override returns (uint) {
        return _totalStaked;
    }


    function setStakeAdmin(address account)
        external
        onlyOwner
    {
        require(account != address(0), "Zero account");

        _stakeAdmin = account;
    }
}"}}
