      "content": "// SPDX-License-Identifier: SimPL-2.0
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "./IBitbull.sol";
import "../Staking/interface/IDechatStaking.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import "./interface/IDechatIDOClaim.sol";


contract DechatIDOClaim is Ownable, IDechatIDOClaim{ 
    using SafeERC20 for IERC20;
    using EnumerableSet for EnumerableSet.AddressSet;

    uint constant baseProportion = 1000000;
    uint constant gtAmount = 6000e18;

    address lastAddr;
    IERC20 public output;
    IDechatStaking public stakingAddr;
    mapping(address => uint) _payed;
    mapping(address => uint) _staked;
    mapping(address => uint) balance;
    mapping(address => uint) public ref;

    ReleaseInfo[] public releaseInfoList1;
    ReleaseInfo[] public releaseInfoList2;
    EnumerableSet.AddressSet accounts;

    struct ReleaseInfo {
        uint date;
        uint proportion;
    }

    constructor(IERC20 _output, IDechatStaking _stakingAddr, ReleaseInfo[] memory info1, ReleaseInfo[] memory info2) {
        output = _output;
        stakingAddr = _stakingAddr;
        for (uint i;i < info1.length; i++) {
            releaseInfoList1.push(info1[i]);
        }
        for (uint i;i < info2.length; i++) {
            releaseInfoList2.push(info2[i]);
        }
    }

    struct InputParam {
        address account;
        uint amount;
        uint ref;
    }

    function setStakingAddr(IDechatStaking stakingAddr_) external onlyOwner {
        stakingAddr = stakingAddr_;
    }

    function setLastAddr(address lastAddr_) external onlyOwner {
        lastAddr = lastAddr_;
    }

    function payed(address acount) public view override returns(uint){
        if (lastAddr != address(0)) {
            return IDechatIDOClaim(lastAddr).payed(acount) + _payed[acount];
        }
        return _payed[acount];
    }

    function staked(address acount) public view override returns(uint){
        if (lastAddr != address(0)) {
            return IDechatIDOClaim(lastAddr).staked(acount) + _staked[acount];
        }
        return _staked[acount];
    }



    function inputData(InputParam[] memory inputParam) external onlyOwner{
        for (uint i; i < inputParam.length; i++) {
            balance[inputParam[i].account] += inputParam[i].amount;
            ref[inputParam[i].account] += inputParam[i].ref;
            accounts.add(inputParam[i].account);
        }
    }

    function clean() external onlyOwner {
        uint length = accounts.length();
        for (uint i; i < length; i++) {
            delete balance[accounts.at(i)];
            delete ref[accounts.at(i)];
        }
    }

    function claim() external {
        uint releasedAmount = canClaim(msg.sender);
        require(releasedAmount > 0);
        _payed[msg.sender] += releasedAmount;
        output.safeTransfer(msg.sender, releasedAmount);
    }

    function released(address account) public view returns (uint) {
        uint proportion;
        for (uint i; i < releaseInfoList1.length; i++) {
            if (releaseInfoList1[i].date <= block.timestamp) {
                proportion += releaseInfoList1[i].proportion;
            }
        }
        if (balanceOf(account) >= gtAmount) {
            proportion = 0;
            for (uint i; i < releaseInfoList2.length; i++) {
                if (releaseInfoList2[i].date <= block.timestamp) {
                    proportion += releaseInfoList2[i].proportion;
                }
            }
        }
        return balanceOf(account) * proportion  / baseProportion;

    }

    function canClaim(address account) public view returns (uint) {
        uint amount = released(account);
        if (payed(account) + staked(account) >= amount) return 0;
        return amount - payed(account) - staked(account);
    }

     function canStake(address account) public view returns (uint) {
        uint proportion = releaseInfoList1[0].proportion;
        uint _balance = balanceOf(account);
        if (_balance > gtAmount) proportion = releaseInfoList2[0].proportion;
        _balance = _balance * proportion / baseProportion;
        if(payed(account) + staked(account) > _balance) return 0;
        return _balance - payed(account) - staked(account);
    }

    struct HomeView {
        uint released;
        uint balance;
        uint payed;
        uint staked;
        uint canClaim;
        uint canStake;
    }

    function homeView(address account) external view returns(HomeView memory) {
        return HomeView(released(account), balanceOf(account) , payed(account), staked(account), canClaim(account), canStake(account));
    }

    function withdraw(address addr) external onlyOwner(){
        SafeERC20.safeTransfer(IERC20(addr), msg.sender, IERC20(addr).balanceOf(address(this)));
    }

    function balanceOf(address account) public view returns(uint) {
        return balance[account] *  (baseProportion + ((ref[account] > 0) ? 30000 : 0)) / baseProportion;
    }

    function stakeRelease() external{
        uint _balance = canStake(msg.sender);
        require(_balance > 0, "Insufficient Balance");
        _staked[msg.sender] += _balance;
        require(stakingAddr.stakeRelease(msg.sender, _balance));
    }

}"
