contract SanSanHuLuo31 is Ownable {
    struct Member {
        address sponsor;
        address[] referrals;
        uint8 level;
    }
    address public liquidityAddress;

    IERC20 public usdt;
    IERC20 public nftt;
    IUniswapV2Router02 public uniswapV2Router;

    mapping(address => Member) public members;
    mapping(uint8 => uint256) public levelPrice;
    mapping(uint8 => uint256) public levelCommissionPercentage;

    // 用户的奖励结构
    struct RewardInfo {
        uint256 totalReward;      // 总奖励
        uint256 rewardClaimed;    // 已领取的奖励
    }

    // 用户的奖励映射
    mapping(address => mapping(uint8 => RewardInfo)) public userRewards;

    // 设置每个等级的奖励金额
    mapping(uint8 => uint256) public levelRewardAmount;


    address public USDT_ADDRESS;
    address public nftToken;

    constructor() {

        USDT_ADDRESS = address(0x55d398326f99059fF775485246999027B3197955);
        uniswapV2Router = IUniswapV2Router02(address(0x10ED43C718714eb63d5aA57B78B54704E256024E));

        // 设置等级价格
        levelPrice[1] = 100e18;
        levelPrice[2] = 200e18;
        levelPrice[3] = 300e18;
        levelPrice[4] = 600e18;
        levelPrice[5] = 3000e18;
        levelPrice[6] = 12000e18;
        levelPrice[7] = 28000e18;
        levelPrice[8] = 38000e18;
        // 设置分佣比例
        levelCommissionPercentage[1] = 50;
        levelCommissionPercentage[2] = 45;
        levelCommissionPercentage[3] = 40;
        levelCommissionPercentage[4] = 35;
        levelCommissionPercentage[5] = 30;
        levelCommissionPercentage[6] = 25;
        levelCommissionPercentage[7] = 20;
        levelCommissionPercentage[8] = 15;

        levelRewardAmount[1] = 150e18;
        levelRewardAmount[2] = 810e18;
        levelRewardAmount[3] = 3240e18;
        levelRewardAmount[4] = 17010e18;
        levelRewardAmount[5] = 218700e18;
        levelRewardAmount[6] = 2187000e18;

    }
    //绑定推荐关系
    function join(address sponsor) external {
        require(sponsor != msg.sender,"Cannot bind oneself");
        require(members[msg.sender].level == 0, "Already a member");

        if(members[sponsor].referrals.length < 3) {
            members[sponsor].referrals.push(msg.sender);
        } else {
            // 如果直接推荐人已经有3个下线，则寻找下一个可用的推荐人
            address newSponsor = findNextAvailableSponsor(sponsor);
            members[newSponsor].referrals.push(msg.sender);
            sponsor = newSponsor;
        }

        members[msg.sender].sponsor = sponsor;
    }

    function findNextAvailableSponsor(address sponsor) private view returns(address) {
        require(members[sponsor].referrals.length >= 3, "Sponsor has less than 3 referrals");

        for(uint i = 0; i < members[sponsor].referrals.length; i++) {
            address referral = members[sponsor].referrals[i];
            if(members[referral].referrals.length < 3) {
                return referral;
            }
        }

        // 递归查找下一个可用的推荐人
        for(uint i = 0; i < members[sponsor].referrals.length; i++) {
            address nextSponsor = findNextAvailableSponsor(members[sponsor].referrals[i]);
            if(nextSponsor != address(0)) {
                return nextSponsor;
            }
        }

        return address(0);
    }

    function buyLevel() external {
        members[msg.sender].level +=  1; // 必须按顺序购买等级
        uint8 _level = members[msg.sender].level;
        require(_level >= 1 && _level <= 8, "Invalid level");
        uint256 tokenAmount = usdt2Token(levelPrice[_level]);
        require(nftt.transferFrom(msg.sender, address(this), tokenAmount), "Transfer failed");
        // 执行分佣
        distributeCommission(msg.sender, _level);
        userRewards[msg.sender][_level].totalReward = levelRewardAmount[_level];
    }

    // 其他功能，如分佣、提现等
    function distributeCommission(address buyer, uint8 _level) private {
        address sponsor = members[buyer].sponsor;
        uint256 tokenAmount = usdt2Token(levelPrice[_level]);
        uint256 commissionAmount = (tokenAmount * levelCommissionPercentage[_level]) / 100;

        // 检查上级是否有资格接收佣金
        while(sponsor != address(0) && members[sponsor].level < _level) {
            sponsor = members[sponsor].sponsor;
        }

        if (sponsor != address(0)) {
            require(nftt.transfer(sponsor, commissionAmount), "Commission transfer failed");
            userRewards[sponsor][_level].rewardClaimed += commissionAmount;
        } else {
            // 如果没有合格的上级，则佣金留在合约内或转移到其他地址
            require(nftt.transfer(address(this), commissionAmount), "Commission transfer to owner failed");
        }
    }


    // 获取待领取的奖励
    function getPendingReward(address _user, uint8 _level) public view returns (uint256) {

        uint256 total = userRewards[_user][_level].totalReward;
        uint256 claimed = userRewards[_user][_level].rewardClaimed;

        return total - claimed;
    }

    function setNFTT(address _usdtAddress, address _nfttAddress,address _nftTokenAddress) public onlyOwner {
        usdt = IERC20(_usdtAddress);
        nftt = IERC20(_nfttAddress);
        nftToken = _nftTokenAddress;
    }

    function withdrawToken(address _tokenAddress,address _to,uint256 amount) public onlyOwner{
        IERC20(_tokenAddress).transfer(_to,amount);
    }

    function getReferrals(address _member) external view returns(address[] memory) {
        return members[_member].referrals;
    }

    function _getUsdt2Token(uint usdtAmount)internal view returns(uint){
        address[] memory path = new address[](2);
